INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (98,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,79,'D2GS_TRADE','(DWORD) Request ID\r\n(WORD)  Gold Amount \r\n','This message should be used for manipulating the trading window, the Horadric Cube item window, and the Stash window.\r\n\r\nPossible Request ID\'s:\r\n[0x02] Cancel trade request\r\n[0x03] Accept trade request\r\n[0x04] Press Accept button (accept)\r\n[0x07] Press Accept button (unaccept) - This should be sent when placing items in the trade window as well.\r\n[0x08] Refresh window (See below for examples on usage)\r\n[0x12] Close stash window\r\n[0x13] Move gold (stash to inventory)\r\n[0x14] Move gold (inventory to stash)\r\n[0x17] Close Horadric Cube window\r\n\r\nExamples of using Refresh ID:\r\nItem has been picked up to mouse cursor buffer.\r\nItem has been placed in the trade screen.\r\nAccepted or closed a trade window.\r\nManual unclick of Accept button.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (99,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,17,'MCP_REQUESTLADDERDATA','(BYTE) Ladder type\r\n(WORD) Starting position','This will request 16 ladder entries, starting at a zero-based location specified in \'Starting position\'. For example if this is 0, then ladder entries 1-16 are retrieved. If this is 17, then ladder entries 18-33 are retrieved. Note: The values that Diablo 2 sends for this are always perfectly divisible by 16. This might be a requirement.\r\n<dl>\r\n<dt>Possible ladder types:</dt>\r\n<dd>0x00: standard hardcore overall ladder</dd>\r\n<dd>0x01: standard hardcore amazon ladder</dd>\r\n<dd>0x02: standard hardcore sorceress ladder</dd>\r\n<dd>0x03: standard hardcore necromancer ladder</dd>\r\n<dd>0x04: standard hardcore paladin ladder</dd>\r\n<dd>0x05: standard hardcore barbarian ladder</dd>\r\n<dd>0x09: standard softcore overall ladder</dd>\r\n<dd>0x0A: standard softcore amazon ladder</dd>\r\n<dd>0x0B: standard softcore sorceress ladder</dd>\r\n<dd>0x0C: standard softcore necromancer ladder</dd>\r\n<dd>0x0D: standard softcore paladin ladder</dd>\r\n<dd>0x0E: standard softcore barbarian ladder</dd>\r\n<dd>0x13: expansion hardcore overall ladder</dd>\r\n<dd>0x14: expansion hardcore amazon ladder</dd>\r\n<dd>0x15: expansion hardcore sorceress ladder</dd>\r\n<dd>0x16: expansion hardcore necromancer ladder</dd>\r\n<dd>0x17: expansion hardcore paladin ladder</dd>\r\n<dd>0x18: expansion hardcore barbarian ladder</dd>\r\n<dd>0x19: expansion hardcore druid ladder</dd>\r\n<dd>0x1A: expansion hardcore assassin ladder</dd>\r\n<dd>0x1B: expansion softcore overall ladder</dd>\r\n<dd>0x1C: expansion softcore amazon ladder</dd>\r\n<dd>0x1D: expansion softcore sorceress ladder</dd>\r\n<dd>0x1E: expansion softcore necromancer ladder</dd>\r\n<dd>0x1F: expansion softcore paladin ladder</dd>\r\n<dd>0x20: expansion softcore barbarian ladder</dd>\r\n<dd>0x21: expansion softcore druid ladder</dd>\r\n<dd>0x22: expansion softcore assassin ladder</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (100,NULL,'2012-05-30 19:25:03',0,0,NULL,1,1,2,4,'SID_SERVERLIST','(DWORD) Server version\r\n(STRING) [] Server list','Client should set the internal Battle.net server list from the contents of this message.\r\n\r\nThis packet is supported by all Battle.snp games and Diablo II and its expansion.\r\n\r\nNote that this packet is defunct. The clients (including Warcraft III) all ask Battle.net for the FILETIME structure of the gateway list file and then compares that structure to its own cached copy. If the FILETIMEs do not match, then it goes to BNFTP and downloads a new one and stores it to the Windows Registry (or on file, if using Mac) while also caching the new FILETIME for later. They do not ever under any circumstance store the actual file data, and therefore as long as the cache is not changed and Battle.net always reports the same FILETIME, you can modify your gateway list without it knowing it changed.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (101,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,70,'SID_NEWS_INFO','(BYTE) Number of entries\r\n(DWORD) Last logon timestamp\r\n(DWORD) Oldest news timestamp\r\n(DWORD) Newest news timestamp\r\n\r\nFor each entry:<dl><dd>(DWORD) Timestamp</dd>\r\n<dd>(STRING) News</dd></dl>','This packet contains news from battle.net. Timestamps are in C/Unix format, biased for UTC.\r\n\r\nMultiple separate news messages can be received. These should be treated the same as if one message was sent with several news entries.\r\n\r\nIf the \'Timestamp\' field for an entry is zero, then that entry is the message of the day (and not a news entry in the normal respect).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (102,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,74,'SID_OPTIONALWORK','(STRING) MPQ Filename','Using SID_GETFILETIME with request ID 0x80000005, the client should download this file and call the ExtraWork function inside of it. Generally, this message is used for the optional ExtraWork DLL.\r\n\r\nThe client will only execute this entirely if the registry key HKCU\\Software\\Battle.net\\Optimize\\SysDesc [REG_DWORD] is set to 1.');
