INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (104,NULL,'2012-02-26 04:20:13',0,0,NULL,1,7,2,10,'BNLS_CONFIRMLOGON','(BOOLEAN) Success','Success is TRUE if the server knows your password, FALSE otherwise. If this is FALSE, the Battle.net connection should be closed by the client.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (105,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,91,'SID_CHANGEEMAIL','(STRING) Account Name\r\n(STRING) Old Email Address\r\n(STRING) New Email Address','Requests Battle.net to change the email address bound to an account. This packet must be sent before logon.\r\n\r\nThis packet used to be named SID_AUTH_DISCONNECT, however Blizzard never had it implemented, and so reused the packet ID for their email system.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (106,NULL,'2011-06-15 01:44:58',0,0,NULL,1,1,2,81,'SID_AUTH_CHECK','(DWORD) Result\r\n(STRING) Additional Information','Reports success/failure on version & CD Key check.\r\n\r\nResult:\r\n0x000: Passed challenge\r\n0x100: Old game version (Additional info field supplies patch MPQ filename)\r\n0x101: Invalid version\r\n0x102: Game version must be downgraded \r\n(Additional info field supplies patch MPQ filename)\r\n0x0NN: (where NN is the version code supplied in SID_AUTH_INFO): Invalid version code (note that 0x100 is not set in this case).\r\n0x200: Invalid CD key *\r\n0x201: CD key in use (Additional info field supplies name of user)\r\n0x202: Banned key\r\n0x203: Wrong product\r\nThe last 4 codes also apply to the second CDKey, as indicated by a bitwise combination with 0x010.\r\n\r\nIf a patch file cannot be found, additional info is set to \'non-existent\'. If either the executable size/date or the version code is wrong, the server will typically return a failure status.\r\n\r\n* If you receive this status, official Battle.net servers ban you for 24 hours (as of 06/15/2011). It used to be 2 weeks, but they changed recently.\r\n\r\nMore:\r\nWhile doing a little research on how Battle.net autoupdates it\'s clients, I found that it (STAR, anyway) does not use the provisions of the SID_AUTH login system to tell clients which update file to download. Instead, it sends a SID_REPORTVERSION (from the previous logon system) containing the equivalent error code and the name of the patch file to download. This seems strange, and makes me think that the part of Battle.net responsible for updating clients is isolated from the part responsible for processing logon requests. If this is the case, it makes sense that that system was never updated, since it must still support legacy clients. In addition, this would explain why most Blizzard clients retain obsolete packet processing code.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (107,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,4,'MCP_JOINGAME','(WORD) Request ID\r\n(WORD) Game token\r\n(WORD) Unknown (0)\r\n(DWORD) IP of D2GS Server\r\n(DWORD) Game hash\r\n(DWORD) Result','<dl>\r\n<dt>Possible values for result:</dt>\r\n<dd>0x00: Game joining succeeded. In this case, Diablo 2 terminates the connection with the MCP and initiates the connection with the D2GS.</dd>\r\n<dd>0x29: Password incorrect.</dd>\r\n<dd>0x2A: Game does not exist.</dd>\r\n<dd>0x2B: Game is full.</dd>\r\n<dd>0x2C: You do not meet the level requirements for this game.</dd>\r\n<dd>0x6E: A dead hardcore character cannot join a game.</dd>\r\n<dd>0x71: A non-hardcore character cannot join a game created by a Hardcore character.</dd>\r\n<dd>0x73: Unable to join a Nightmare game.</dd>\r\n<dd>0x74: Unable to join a Hell game.</dd>\r\n<dd>0x78: A non-expansion character cannot join a game created by an Expansion character.</dd>\r\n<dd>0x79: A Expansion character cannot join a game created by a non-expansion character.</dd>\r\n<dd>0x7D: A non-ladder character cannot join a game created by a Ladder character.</dd>\r\n</dl>');
