INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (110,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,7,'PACKET_BROADCASTMESSAGE','(STRING) User\r\n(STRING) Command \r\n','Sends command to all bots on the BotNet. This functionality can only be used by Administrators. If a user without Administrator privileges sends this message, it is treated as PACKET_MESSAGE (0x04).\r\n\r\nWhen a bot sends this packet, other bots recieve 0x04.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (111,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,15,'D2GS_RIGHTSKILLONLOCATIONEX','(WORD) X coordinate\r\n(WORD) Y coordinate \r\n','Uses the currently selected skill at the specified location. \r\n\r\nThis packet is sent repeatedly when the mouse button is held down after the initial packet has been sent. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (112,NULL,'2011-06-02 18:51:40',0,0,NULL,1,1,2,38,'SID_READUSERDATA','(DWORD) Number of accounts\r\n(DWORD) Number of keys\r\n(DWORD) Request ID\r\n(STRING) [] Requested Key Values ','Contains profile information as requested by the client.\r\n\r\nIf you plan to graphically display this data, note that the fields may contain characters that are otherwise not alphanumeric, such as tab or enter keys. In addition, some bots allow the user to put new lines into fields that the official clients only allow one line for, such as a profile\'s Location.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (113,NULL,'2011-02-04 22:33:58',0,0,NULL,1,7,1,10,'BNLS_CONFIRMLOGON','(DWORD) [5] Password proof from Battle.net','This message will confirm that the server really knows your password.\r\n\r\nThis packet may only be used after \"proof\" messages: BNLS_LOGONPROOF (0x03), BNLS_CHANGEPROOF (0x06), BNLS_UPGRADEPROOF (0x08).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (114,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,37,'D2GS_SWITCHBELTITEM','(DWORD) Item ID - Cursor buffer\r\n(DWORD) Item ID - Item to be replaced \r\n','Replaces item in belt with item in player\'s cursor buffer.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (115,NULL,'2011-02-04 22:07:52',0,0,NULL,1,7,2,5,'BNLS_CHANGECHALLENGE','(DWORD) [8] Data for SID_AUTH_ACCOUNTCHANGE (0x55)','This message will give you data you need for SID_AUTH_ACCOUNTCHANGE (0x55).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (116,NULL,'2011-07-20 01:44:10',0,0,NULL,1,1,2,127,'SID_CLANMEMBERSTATUSCHANGE','(STRING) Username\r\n(BYTE) Rank\r\n(BYTE) Status\r\n(STRING) Location','This message is received when a member changes their status (by going online, offline, being promoted, etc).\r\n\r\nUsername:<dl><dd>The name of the member whose status is changing (by going online, offline, being promoted, etc).</dd></dl>\r\nRank:<dl>\r\n<dd>0x00: Initiate</dd>\r\n<dd>0x01: Peon</dd>\r\n<dd>0x02: Member</dd>\r\n<dd>0x03: Officer</dd>\r\n<dd>0x04: Leader</dd></dl>\r\nStatus:<dl>\r\n<dd>0x00: Offline</dd>\r\n<dd>0x01: Online (not in either channel or game)</dd>\r\n<dd>0x02: In a channel</dd>\r\n<dd>0x03: In a public game</dd>\r\n<dd>0x05: In a private game</dd></dl>\r\nLocation:<dl><dd>This field is where the user is, i.e., game name, channel name.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (117,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,105,'SID_FRIENDSPOSITION','(BYTE) Old Position\r\n(BYTE) New Position','Sent to clients supporting this packet when a friend is promoted up or demoted down the friend\'s list.\r\n\r\nSee SID_FRIENDSLIST for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (118,NULL,'2011-06-02 21:49:59',0,0,NULL,1,1,2,103,'SID_FRIENDSADD','(STRING) Account\r\n(BYTE) Status\r\n(BYTE) Location\r\n(DWORD) ProductID\r\n(STRING) Location \r\n','Sent to clients supporting this packet when an account is added to the client\'s friendlist. New friends are appended to the list.\r\n\r\nBitwise-values and meanings for Status:\r\n\r\n0x01: Mutual\r\n0x02: DND\r\n0x04: Away\r\n\r\nValue and meanings for Location:\r\n\r\n0x00: Offline\r\n0x01: Not in chat\r\n0x02: In chat\r\n0x03: In public game\r\n0x04: In private game, not on your friends list\r\n0x05: In private game, is on your friends list\r\n\r\nSee SID_FRIENDSLIST for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (119,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,23,'MCP_CHARLIST','(DWORD) Number of characters to list','Requests a character list.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (120,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,45,'SID_GETICONDATA','(FILETIME) Filetime\r\n(STRING) Filename ','Returns filename and filetime of default icons file.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (121,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,45,'SID_GETICONDATA','[blank] \r\n','Requests the filename & time of the default icons file for the current game. This message must not be sent after receiving SID_ENTERCHAT or Battle.net will terminate the connection.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (122,NULL,'2011-06-02 18:47:11',0,0,NULL,1,1,1,39,'SID_WRITEUSERDATA','(DWORD) Number of accounts\r\n(DWORD) Number of keys\r\n(STRING) [] Accounts to update\r\n(STRING) [] Keys to update\r\n(STRING) [] New values','Updates the Client\'s profile.\r\nCurrently, the allowed keys for this are Sex, Location, and Description. The maximum length for the values is 512, including the null terminator.\r\n\r\nSee SID_READUSERDATA for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (123,NULL,'2011-02-04 22:00:22',0,0,NULL,1,7,2,1,'BNLS_CDKEY','<dl>\r\n<dd>(BOOLEAN) Result</dd>\r\n<dd>(DWORD) Client Token</dd>\r\n<dd>(DWORD) CD-Key Length</dd>\r\n<dd>(DWORD) CD-Key Product Value</dd>\r\n<dd>(DWORD) CD-Key Public Value</dd>\r\n<dd>(DWORD) Unknown (0)</dd>\r\n<dd>(DWORD) [5] CD-Key Hash* (CDKey data for SID_AUTH_CHECK)</dd>','Contains encryption result and data to be sent to Battle.net.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (124,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,5,'PACKET_CYCLE','(STRING) Channel','Contains encryption result and data to be sent to Battle.net.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (125,NULL,'2011-09-30 08:15:04',0,0,NULL,1,7,2,26,'BNLS_VERSIONCHECKEX2','(BOOLEAN) Success *\r\n(DWORD) Version\r\n(DWORD) Checksum\r\n(STRING) Version check stat string\r\n(DWORD) Cookie\r\n(DWORD) The latest version code for this product\r\n','* Success is TRUE if successful, FALSE otherwise. If this is FALSE, the next DWORD is the provided cookie, following which the message ends. Do not mistake this value for a BYTE, it is actually a DWORD.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (126,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,29,'D2GS_SWITCHBODYITEM','(DWORD) Item ID\r\n(DWORD) Body Location \r\n','Swaps item in player\'s cursor buffer with item in the body location.\r\n\r\nPossible Body Locations:\r\n0x01 - Helm\r\n0x02 - Amulet\r\n0x03 - Armor\r\n0x04 - Left-hand weapon\r\n0x05 - Right-hand weapon\r\n0x06 - Left-hand ring\r\n0x07 - Right-hand ring\r\n0x08 - Belt\r\n0x09 - Boots\r\n0x0A - Gloves\r\n\r\nPlease note: This message\'s official name is not known, and has been invented. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (127,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,6,'SID_STARTVERSIONING','(FILETIME) MPQ Filetime\r\n(STRING) MPQ Filename\r\n(STRING) ValueString \r\n','Contains CheckRevision values.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (128,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,1,'D2GS_WALKTOLOCATION','(WORD) X coordinate\r\n(WORD) Y coordinate','Walk to a specified (X,Y) map coordinate.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (129,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,115,'SID_CLANDISBAND','(DWORD) Cookie','Disbands the clan of which the client is a member. You must be a leader to send this packet.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (130,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,2,'MCP_CHARCREATE','(DWORD) Character class\r\n(WORD) Character flags\r\n(STRING) Character name','Creates a character on the Realm.\r\n\r\nCharacter Classes are the same as in D2 users\' Statstrings:\r\n<dl><dd>0x00: Amazon\r\n0x01: Sorceress\r\n0x02: Necromancer\r\n0x03: Paladin\r\n0x04: Barbarian\r\n0x05: Druid\r\n0x06: Assassin</dd></dl>\r\nFlag values should be OR\'d together. The only flags that can be set for character creation are classic, hardcore, expansion, and ladder, but the other flags are included here for completeness:\r\n<dl><dd>0x00: Classic\r\n0x04: Hardcore\r\n0x08: Dead\r\n0x20: Expansion\r\n0x40: Ladder</dd>\r\nSending 0x05 or 0x06 in character class or 0x20 in character flags while on D2DV will disconnect and temporarily ban you from the realm. Likewise, sending 0x05 or 0x06 in character class without setting 0x20 in character flags will result in a disconnect and ban.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (131,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,104,'D2GS_GAMELOGON','(DWORD) D2GS Server Hash\r\n(WORD) D2GS Server Token\r\n(BYTE) Character ID\r\n(DWORD) Version byte (Currently 0x0B)\r\n(DWORD) Unknown - Suggested Const (0xED5DCC50)\r\n(DWORD) Unknown - Suggested Const (0x91A519B6)\r\n(BYTE) Unknown - Suggested (0x00)\r\n(STRING) Character name\r\n(VOID) *See user-comment below \r\n','Possible Character IDs:\r\n0x00 - Amazon\r\n0x01 - Sorceress\r\n0x02 - Necromancer\r\n0x03 - Paladin\r\n0x04 - Barbarian\r\n0x05 - Druid\r\n0x06 - Assassin\r\n\r\nThe \"Character name\" field is a buffer of 15 characters with a null-terminator. This string should always be filled with 15 characters (plus terminator), padded with 0\'s for unused bytes.\r\n\r\nNote: If the character ID does not match the type of character you\'re attempting to logon with, and the character is nealy created (with 0 play time), then that character will automatically be changed or \"morphed\" into the character ID sent. \r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (132,NULL,'2011-02-04 22:07:35',0,0,NULL,1,7,1,5,'BNLS_CHANGECHALLENGE','(STRING) Account name\r\n(STRING) Account old password\r\n(STRING) Account new password','This message will give you data you need for SID_AUTH_ACCOUNTCHANGE (0x55). This message is used to change the password of an existing account.\r\n\r\nYou must send this before you can send BNLS_CHANGEPROOF (0x06).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (133,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,31,'D2GS_SWITCHINVENTORYITEM','(DWORD) Item ID - Item to place in inventory (cursor buffer)\r\n(DWORD) Item ID - Item to be replaced\r\n(DWORD) X coordinate for replace\r\n(DWORD) Y coordinate for replace \r\n','Swaps the item in the player\'s cursor buffer with one in the player\'s inventory. Please note: \r\nThis message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (134,NULL,'2010-09-26 23:55:16',0,0,NULL,1,7,2,16,'BNLS_REQUESTVERSIONBYTE','(DWORD) Product\r\n\r\nIf Product is nonzero:\r\n(DWORD) Version byte \r\n','On failure, Product is 0. On success, this is equal to the requested Product.\r\n\r\nIf Product is 0, the Version byte DWORD is not included in the message.\r\n\r\nPossible values for Product:\r\n\r\n0x01: Starcraft\r\n0x02: Starcraft Brood War\r\n0x03: Warcraft II Battle.net Edition\r\n0x04: Diablo II\r\n0x05: Diablo II: Lord of Destruction\r\n0x06: Starcraft Japanese\r\n0x07: Warcraft III\r\n0x08: Warcraft III: The Frozen Throne\r\n\r\nJBLS adds these products:\r\n\r\n0x09: Diablo Retail\r\n0x0A: Diablo Shareware\r\n0x0B: Starcraft Shareware\r\n\r\nBNLS# adds this product:\r\n\r\n0x0C: Warcraft III: Demo');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (135,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,22,'SID_CLICKAD','(DWORD) Ad ID\r\n(DWORD) Request type','The clients send this when an ad is clicked.\r\n\r\nRequest Type is 0 if the client used SID_QUERYADURL to get the ad\'s data, 1 otherwise. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (136,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,22,'D2GS_PICKUPITEM','(DWORD) Unit Type\r\n(DWORD) Unit ID\r\n(DWORD) Action ID\r\n','Pick up a ground item to cursor buffer/inventory.\r\n\r\nPossible action IDs:\r\n0x00 - Move item to inventory\r\n0x01 - Move item to cursor buffer \r\n\r\nPlease note: This message\'s official name is not known, and has been invented. \r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (137,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,80,'D2GS_DROPGOLD','(DWORD) PlayerID\r\n(DWORD) GoldAmount \r\n','Drops a pile of gold to the ground. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (138,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,82,'SID_AUTH_ACCOUNTCREATE','(DWORD) Status','The message reports the success or failure of an account creation attempt. \r\n\r\nStatus:<dl><dd>0x00: Successfully created account name.</dd>\r\n<dd>0x04: Name already exists.</dd>\r\n<dd>0x07: Name is too short/blank.</dd>\r\n<dd>0x08: Name contains an illegal character.</dd>\r\n<dd>0x09: Name contains an illegal word.</dd>\r\n<dd>0x0a: Name contains too few alphanumeric characters.</dd>\r\n<dd>0x0b: Name contains adjacent punctuation characters.</dd>\r\n<dd>0x0c: Name contains too many punctuation characters.</dd>\r\n<dd>Any other: Name already exists.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (139,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,120,'SID_CLANREMOVEMEMBER','(DWORD) Cookie\r\n(STRING) Username','Kick a member out of the clan. Only clan leaders and officers may perform this action successfully. Members can only be removed if they\'ve been in the clan for over one week.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (140,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,4,'PACKET_MESSAGE','(STRING) User\r\n(STRING) Command \r\n','Command from bot on current database. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (141,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,65,'SID_QUERYADURL','(DWORD) Ad ID ','Requests the URL for an ad if none is given.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (142,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,119,'D2GS_TRADEACTION','(BYTE) Request Type \r\n','A character is trading with you.\r\n\r\nPossible Request Type values:\r\n0x01 - Player requests trade initiation\r\n0x05 - Player presses \'Accept\'\r\n0x06 - \'Accept\' button disabled for both players\r\n0x09 - Out of inventory space\r\n0x0C - Player cancels trade\r\n0x0D - You accepted the trade agreement\r\n0x0E - Your \'Accept\' button is disabled\r\n0x0F - \'Accept\' buttons re-enabled Please note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (143,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,33,'SID_DISPLAYAD','(DWORD) Platform ID\r\n(DWORD) Product ID\r\n(DWORD) Ad ID\r\n(STRING) Filename\r\n(STRING) URL ','Sent when an ad is displayed. Perhaps for statistics?\r\n\r\nNull strings are now sent in place of Filename and URL, since the need to truncate long strings to 128 characters was causing inaccuracies.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (144,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,62,'SID_LOGONREALMEX','(DWORD) Client Token\r\n(DWORD) [5] Hashed realm password\r\n(STRING) Realm title','Realm password is always \"password\". The password hash is created the same way the hash is for logging on to an account.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (145,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,10,'SID_ENTERCHAT','(STRING) Username *\r\n(STRING) Statstring **\r\n','Joins Chat. \r\n\r\n* Null on WAR3/W3XP.\r\n\r\n** Null on CDKey Products, except for D2DV and D2XP when on realm characters..');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (146,NULL,'2011-05-29 16:11:46',0,0,NULL,1,1,2,80,'SID_AUTH_INFO','(DWORD) Logon Type\r\n(DWORD) Server Token\r\n(DWORD) UDPValue *\r\n(FILETIME) MPQ filetime\r\n(STRING) IX86ver filename\r\n(STRING) ValueString\r\n\r\nWAR3/W3XP Only:\r\n(VOID) 128-byte Server signature','Contains the Server Token, and the values used in CheckRevision.\r\n\r\nPossible Logon Type values:<dl>\r\n<dd>0x00: Broken SHA-1 (STAR/SEXP/D2DV/D2XP)</dd>\r\n<dd>0x01: NLS version 1 (War3Beta)</dd>\r\n<dd>0x02: NLS Version 2 (WAR3/W3XP)</dd></dl>\r\n* UDPValue\r\nNo one really knows what this is, however, it is used in 2nd DWORD of the UDP packet PKT_CONNTEST2. It is also the second part of MCP Chunk 1 in MCP_STARTUP.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (147,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,81,'D2GS_WORLDOBJECT','(BYTE)  Object Type - Any information appreciated\r\n(DWORD) Object ID\r\n(WORD)  Object unique code\r\n(WORD)  X Coordinate\r\n(WORD)  Y Coordinate\r\n(BYTE)  State *\r\n(BYTE)  Interaction Condition \r\n','Server-assigned coordinate, object ID, and state/interaction properties.\r\n\r\nStates:\r\n0x00 - Object\'s state can be changed. (Confirm?)\r\n0x02 - Object\'s state cannot be changed with (0x13) interaction. (Confirm?)\r\n\r\nInteraction Conditions:\r\n0x00 - General object. E.G.: Stash, chests, etc.\r\n0x01 - Refresh shrine\r\n0x02 - Health shrine\r\n0x05 - Chest will catch fire (upon interaction)\r\n0x08 - Monster contained within chest (sarcophagus)\r\n0x0D - Mana recharge shrine\r\n0x0E - Stamina shrine\r\n0x0F - Experience shrine\r\n0x13 - Fire shrine\r\n0x80 - Chest is locked (State will be 0)\r\n\r\n*If this value is 0, expect no packet 0x0E (status) to follow, if 2, then there will be.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (148,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,8,'SID_STARTADVEX','(DWORD) Status ','Status\r\n<dl><dd>0x00: Failed</dd>\r\n<dd>0x01: Success</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (149,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,117,'SID_CLANINFO','(BYTE) Unknown (0)\r\n(DWORD) Clan tag\r\n(BYTE) Rank','This message is received when logging on, if you are a member of a clan. It is also sent when you join a clan.\r\n\r\nPossible values for Rank:<dl>\r\n<dd>0x00: Initiate that has been in the clan for less than one week</dd>\r\n<dd>0x01: Initiate that has been in the clan for over one week</dd>\r\n<dd>0x02: Member</dd>\r\n<dd>0x03: Officer</dd>\r\n<dd>0x04: Leader</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (150,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,16,'D2GS_RIGHTSKILLONENTITYEX2','(DWORD) Entity Type\r\n(DWORD) Entity ID \r\n\r\nEntity types\r\n--------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances','Uses your right skill repeatedly on the Entity specefied in Entity ID.\r\nThis packet is sent repeatedly when the mouse button is held down after the initial packet has been sent.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (151,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,112,'SID_CLANFINDCANDIDATES','(DWORD) Cookie\r\n(DWORD) Clan Tag','This message is sent to the server to check for viable candidates in the channel and friend list, and also to check the availability of the specified clan tag. If 10 or more candidates are found, the official client would prompt with a selection of users that he wants to invite to start a clan.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (152,NULL,'2010-07-15 02:47:39',0,0,NULL,1,7,1,3,'BNLS_LOGONPROOF','(DWORD) [16] Data from SID_AUTH_ACCOUNTLOGON','This message will parse data from SID_AUTH_ACCOUNTLOGON (0x53) and will reply with data to send in SID_AUTH_ACCOUNTLOGONPROOF (0x54). You must send BNLS_LOGONCHALLENGE (0x02) before you can send this.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (153,NULL,'2011-06-02 21:39:30',0,0,NULL,1,1,2,101,'SID_FRIENDSLIST','(BYTE) Number of Entries\r\nFor each entry:\r\n(STRING) Account\r\n(BYTE) Location\r\n(BYTE) Status\r\n(DWORD) ProductID\r\n(STRING) Location name\r\n','This packet contains the current user\'s Friends List. If the friend is offline, their ProductID is 0. Location is only supplied when it is relevant - for example, if Status is set to offline (0x00), the location string will be empty.\r\n\r\nPossible bitwise values for Status:\r\n0x01: Mutual\r\n0x02: DND\r\n0x04: Away\r\nPossible values for Location:\r\n0x00: Offline\r\n0x01: Not in chat\r\n0x02: In chat\r\n0x03: In a public game\r\n0x04: In a private game, and you are not that person\'s friend.\r\n0x05: In a private game, and you are that person\'s friend.\r\n\r\nWhether you are a particular user\'s friend causes different behavior when receiving information about the type of game the user is in (this is true when receiving friend updates, too). When the Location field is 0x04, the user is in a password-protected game, and you are not on that user\'s friends list. When it is 0x05, the user is in a password-protected game, and you are on that user\'s friends list.\r\n\r\nFor example, let\'s say MyndFyre is logging on and Arta[vL] is on his friends list. Arta[vL] is also in a password-protected game. MyndFyre is not on Arta[vL]\'s friends list. MyndFyre will receive location 0x04 for Arta[vL] and the Channel parameter will be a NULL string. And, if MyndFyre were to type in /f l, Battle.net would respond with SID_CHATEVENT saying \"1. Arta[vL] is in a private game.\"\r\n\r\nOn the other hand, if Arta[vL] had listed MyndFyre as a friend, then on logon, MyndFyre will receive location 0x05 for Arta[vL] and the name of the password-protected game will come through the Channel parameter of the message. If MyndFyre were to type in /f l, Battle.net would respond with \"1. Arta[vL] is in the private game vL.\"');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (154,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,7,'MCP_CHARLOGON','(STRING) Character name','Logs onto the realm.\r\n\r\nNote that attempting to log on using an expansion character on D2DV will result in an IPBan by both Battle.net and the Realm.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (155,NULL,'2011-09-30 08:11:16',0,0,NULL,1,7,1,24,'BNLS_VERSIONCHECKEX','(DWORD) Product ID*\r\n(DWORD) Version DLL digit\r\n(DWORD) Flags**\r\n(DWORD) Cookie\r\n(STRING) Checksum formula','This message will request a fast version check. Now works with all products.\r\nVersion DLL Digit must be in the range 0-7. (For example, for IX86Ver1.mpq this is 1)\r\n\r\n* Valid Product IDs are:\r\n\r\n0x01: Starcraft\r\n0x02: Starcraft: Broodwar\r\n0x03: Warcraft II: BNE\r\n0x04: Diablo II\r\n0x05: Diablo II: LoD\r\n0x06: Starcraft: Japanese\r\n0x07: Warcraft III\r\n0x08: Warcraft III: The Frozen Throne\r\n\r\nView consts: [pas cpp vb]\r\n\r\n** Currently there are no flags defined so this must be set to zero.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (156,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,9,'D2GS_LEFTSKILLONENTITYEX2','(DWORD) *Entity Type\r\n(DWORD) Entity ID\r\n\r\n*Entity Types\r\n  -------------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n','Uses your left skill on the Entity specified in Entity ID.\r\n \r\nThis packet is sent repeatedly when the mouse button is held down after the initial packet has been sent.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (157,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,114,'SID_CLANCREATIONINVITATION','(DWORD) Cookie\r\n(DWORD) Clan Tag\r\n(STRING) Clan Name\r\n(STRING) Inviter\'s username\r\n(BYTE) Number of users being invited\r\n(STRING) [] List of users being invited \r\n','Received when a user is inviting you to create a new clan on Battle.net.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (158,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,130,'SID_CLANMEMBERINFORMATION','(DWORD) Cookie\r\n(BYTE) Status code\r\n(STRING) Clan name\r\n(BYTE) User\'s rank\r\n(FILETIME) Date joined','Status code:\r\nThis message follows the same status codes as listed on the Clan Message Codes page.\r\n<dd>0x00 = success</dd>\r\n<dd>0x0C = user not found in that clan</dd>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (159,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,25,'D2GS_PICKUPBUFFERITEM','(DWORD) Item ID \r\n','Pickup an item from the possible buffers below, moving it to the cursor buffer.\r\n\r\nPossible Buffers:\r\n0 - Inventory\r\n1 - NPC Trade & Other player\'s Trade Window?\r\n2 - Trade Screen\r\n3 - Horadric Cube\r\n4 - Stash\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (160,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,130,'SID_CLANMEMBERINFORMATION','(DWORD) Cookie\r\n(DWORD) User\'s clan tag\r\n(STRING) Username','This packet requests information about a user and their current status within their clan. If the user is in a clan, it\'ll return what clan they\'re in, their rank, along with the time they joined it in a FILETIME structure.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (161,NULL,'2011-06-02 18:35:28',0,0,NULL,1,1,2,40,'SID_LOGONCHALLENGE','(DWORD) Server Token ','Contains the server token.\r\n\r\nThis is sent after a request to version check with the old version check system.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (162,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,6,'MCP_GAMEINFO','(WORD) Request ID\r\n(DWORD) Status *\r\n(DWORD) Game Uptime (seconds)\r\n(WORD) Unknown\r\n(BYTE) Maximum players allowed\r\n(BYTE) Number of characters in the game\r\n(BYTE) [16] Classes of ingame characters **\r\n(BYTE) [16] Levels of ingame characters **\r\n(BYTE) Unused (0)\r\n(STRING) [16] Character names **','* Usually 0x00300004, but rarely 0. If it is 0, the packet contains no useful information and the server is probably down.\r\n\r\n** Internally, there are 16 character slots, but the last 8 are always empty. \r\nThis value sometimes includes some empty character slots. \r\nThen, some empty strings are added to the end of the packet. \r\nTo determine the number of characters really in the game: \r\n\r\nCharsInGameReal = CharsInGameFake - AmountOfEmptyCharNames;\r\n\r\nByte N here refers to character in slot N, or 0 if the slot is empty.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (163,4955,'2010-07-15 02:47:39',0,1,'2014-06-01 18:47:39',1,1,1,26,'SID_STARTADVEX2','(DWORD) Password Protected\r\n(DWORD) Unknown\r\n(DWORD) Unknown\r\n(DWORD) Unknown\r\n(DWORD) Unknown\r\n(DWORD) Port\r\n(STRING) Game name\r\n(STRING) Game password\r\n(STRING) Unknown\r\n(STRING) Game stats - Flags, Creator, Statstring ','This message is used by Diablo to create a game. It was also used by Starcraft 1.03.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (164,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,37,'SID_PING','(DWORD) Ping Value','Used to calculate Client\'s ping. The received DWORD should be sent directly back to Battle.net.\r\n\r\nThe ping displayed when in chat can be artificially inflated by delaying before sending this packet, or deflated by responding before requested.\r\nPing can be set to -1 (Strictly speaking, 0xFFFFFFFF, since ping is unsigned) by not responding to this packet at all.\r\n\r\nThe received DWORD is not what determines your ping, but it is actually a cookie for the Battle.net server. You should never change the DWORD.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (165,NULL,'2012-02-26 04:25:06',0,0,NULL,2,2,2,5,'PKT_SERVERPING','(DWORD) UDP Code ','This packet contains the UDP code to be sent to Battle.net in SID_UDPPINGRESPONSE (0x14). ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (166,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,43,'SID_SYSTEMINFO','(DWORD) Number of processors\r\n(DWORD) Processor architecture\r\n(DWORD) Processor level\r\n(DWORD) Processor timing\r\n(DWORD) Total physical memory\r\n(DWORD) Total page file\r\n(DWORD) Free disk space ','Contains system information. This packet was sent during the connection process for STAR/SEXP/DRTL/DSHR clients prior to version 1.07. It is now only used by JSTR and SSHR. For information on how to emulate this system, please see <a href=\"http://forum.valhallalegends.com/index.php?topic=16255.0\">this topic</a>.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (167,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,123,'SID_CLANSETMOTD','(DWORD) Cookie\r\n(STRING) MOTD','Sets your clan\'s Message of the Day.');
