INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (169,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,41,'D2GS_SCROLLTOTOME','(DWORD) Item ID - Scroll\r\n(DWORD) Item ID - Tome \r\n','Places a scroll into a tome of scrolls.\r\n\r\nNote: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (170,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,48,'SID_CDKEY','(DWORD) Spawn (0/1)\r\n(STRING) CDKey\r\n(STRING) Key Owner','Contains unhashed CD key information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (171,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,2,'PACKET_STATSUPDATE','(STRING) Unique username on Battle.net\r\n(STRING) Current channel on Battle.net\r\n(DWORD) Battle.net server IP address\r\n(STRING) DatabaseID\r\n(DWORD) Cycle status (0: Not Cycling, 1: Cycling) \r\n','This should be sent when any of the values in the format change.\r\n\r\nThe DatabaseID field also includes database password - use the format: \'name password\'.\r\n\r\nWith the demise of Operators in private channels, Cycling is now defunct.\r\n\r\nThe Username and Channel fields in this packet cannot be blank, and are limited to 16 characters in length, including the null-terminator. There is currently no provision in the protocol to notify Botnet that you are not logged on. The generally accepted standard is to set Server IP to the last Battle.net server you successfully connected to, Channel to \'<Not Logged On>\', and Username to the last username the client used to log onto Battle.net.\r\n\r\nThe DatabaseID field may be blank, in which case Botnet places the user in the Public Database. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (172,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,18,'MCP_MOTD','[blank]','Requests the realm\'s MOTD.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (173,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,94,'D2GS_PARTY','(WORD)  Action ID\r\n(DWORD) Player ID \r\n','Possible Action IDs:\r\n\r\n0x06 - Invite player to party with you\r\n0x07 - Cancel invite to player\r\n0x08 - Accept invite from player\r\n0x09 - Leave party');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (174,NULL,'2010-08-06 01:55:17',0,0,NULL,1,1,1,0,'SID_NULL','[blank] ','Keeps the connection alive. This message should be sent to the server every 8 minutes (approximately).\r\n\r\nThe server will send this to you automatically, you do not have to reply to it. You should send this on your own never-ending timer for at least as often as Battle.net does (give or take a few seconds).\r\n\r\nThis packet is used to detect if your TCP connection has gone dead, to the point where you will never receive data from the server ever again until you reconnect your connection. A situation such as this can be created by unplugging your internet connection for a few minutes, or if your internet is dropped for whatever reason.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (175,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,6,'MCP_GAMEINFO','(WORD) Request ID\r\n(STRING) Game name','Requests information about a game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (176,NULL,'2011-02-04 22:39:14',0,0,NULL,1,7,2,20,'BNLS_SERVERLOGONPROOF','(DWORD) Slot index\r\n(BOOLEAN) Success (32-bit)\r\n(DWORD) [5] Data for server\'s SID_AUTH_ACCOUNTLOGONPROOF (0x54) response.','After this message is received, the logon checking sequence for a particular logon session is complete. This message can also be used to calculate the server\'s SID_AUTH_ACCOUNTCHANGEPROOF (0x56) response, and check the client\'s change password request. Simply substitute the SID_AUTH_ACCOUNTLOGONPROOF (0x54) data with the SID_AUTH_ACCOUNTCHANGEPROOF (0x56) data.\r\n\r\nIf the Success code is TRUE, then the client\'s logon information was valid. Otherwise, if it is FALSE, the client\'s logon information was invalid, and the logon request must be denied.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (177,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,3,'MCP_CREATEGAME','(WORD) Request Id *\r\n(DWORD) Difficulty\r\n(BYTE) Unknown - 1\r\n(BYTE) Player difference **\r\n(BYTE) Maximum players\r\n(STRING) Game name\r\n(STRING) Game password\r\n(STRING) Game description','<dl>\r\n<dt>Difficulty:</dt>\r\n<dd>0x0000: Normal</dd>\r\n<dd>0x1000: Nightmare</dd>\r\n<dd>0x2000: Hell</dd>\r\n</dl>\r\n\r\n* This value starts at 0x02 at first game creation, and increments by 0x02 each consecutive game creation.\r\n\r\n** A value of 0xFF indicates that the game is not restricted by character difference.\r\n\r\nBefore sending the game name and password, Diablo II automatically changes their case. For example if the string \"aBc DeF\" is typed in Diablo II, then the string sent is \"Abc Def\". This does not apply to the game description.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (178,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,28,'SID_STARTADVEX3','(DWORD) Status ','<dl><dt>Possible values for Status:</dt>\r\n<dd>0x00: Ok</dd>\r\n<dd>0x01: Failed</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (179,NULL,'2010-07-15 02:47:39',0,0,NULL,1,7,1,1,'BNLS_CDKEY','<dl>\r\n<dd>(DWORD) Server Token</dd>\r\n<dd>(STRING) CD key</dd>\r\n</dl>\r\n','This message will encrypt your CD-key, and will reply with the properly encoded CD-key as it is supposed to be sent in the message SID_AUTH_CHECK (0x51). It now works with CD-keys of all products. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (181,NULL,'2010-09-26 23:50:44',0,0,NULL,1,7,1,16,'BNLS_REQUESTVERSIONBYTE','(DWORD) Product ID \r\n','This message requests the latest version byte for a given product.\r\nThe version byte is sent to Battle.net in SID_AUTH_INFO (0x50).\r\n\r\nValid Product IDs are:\r\n\r\n0x01: Starcraft\r\n0x02: Starcraft Brood War\r\n0x03: Warcraft II Battle.net Edition\r\n0x04: Diablo II\r\n0x05: Diablo II: Lord of Destruction\r\n0x06: Starcraft Japanese\r\n0x07: Warcraft III\r\n0x08: Warcraft III: The Frozen Throne\r\n\r\nJBLS adds these products:\r\n\r\n0x09: Diablo Retail\r\n0x0A: Diablo Shareware\r\n0x0B: Starcraft Shareware\r\n\r\nBNLS# adds this product:\r\n\r\n0x0C: Warcraft III: Demo');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (182,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,76,'SID_REQUIREDWORK','(STRING) ExtraWork MPQ FileName','Using SID_GETFILETIME with request ID 0x80000006, the client should download the specified MPQ file, extract ExtraWork.dll and call the ExtraWork function inside of it.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (183,NULL,'2011-02-04 22:04:23',0,0,NULL,1,7,2,4,'BNLS_CREATEACCOUNT','(DWORD) [16] Data for Data for SID_AUTH_ACCOUNTCREATE (0x52)','This message will give you data you need for SID_AUTH_ACCOUNTCREATE (0x52).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (184,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,54,'SID_CDKEY2','(DWORD) Result\r\n(STRING) Key owner','This packet is identical to SID_CDKEY (0x30)\r\n\r\n<dl><dt>Possible values for Result:</dt>\r\n<dd>0x01: OK</dd>\r\n<dd>0x02: Invalid key</dd>\r\n<dd>0x03: Bad product</dd>\r\n<dd>0x04: Banned</dd>\r\n<dd>0x05: In use</dd></dl>\r\n\r\nIn addition, the Key Owner field has two special values:\r\n<ul><li>\'TOO MANY SPAWNS\': Exceeded spawn limit for this CD-Key</li>\r\n<li>\'NO SPAWNING\': Spawns are not supported for this CD-Key</li></ul>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (185,NULL,'2011-02-04 22:36:52',0,0,NULL,1,7,1,15,'BNLS_AUTHORIZEPROOF','(DWORD) Checksum','This is sent to the server when receiving the status code in BNLS_AUTHORIZE (0x0E).\r\n\r\nFor more info, see the [BNLS Checksum Algorithm] document.\r\n\r\nThis message is no longer required. See BNLS_AUTHORIZE for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (186,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,10,'SID_ENTERCHAT','(STRING) Unique name\r\n(STRING) Statstring\r\n(STRING) Account name \r\n','Contains Client product, realm, statstring, and is sent as the response when the client sends SID_ENTERCHAT. Unique name is the users unique name in chat (Which may be Arta, Arta#2, Arta#3, etc).  Account name is the users account name (Which in all 3 previous examples would be Arta).\r\n\r\nOnce you receive this packet, you are not in a channel, but can join/host games and join channels. Because you are not in a channel, you cannot send text messages yet (but you will not be disconnected if you do). See SID_JOINCHANNEL.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (187,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,44,'SID_GAMERESULT','(DWORD) Game type\r\n(DWORD) Number of results - always 8 \r\n(DWORD) [8] Results\r\n(STRING) [8] Game players - always 8 \r\n(STRING) Map name\r\n(STRING) Player score ','Contains end-of-game statistics. Player Score is a string containing right-aligned lines separated by 0x0A. The positions in the \'Results\' array and the \'Players\' array are equivalent.\r\n<dl><dt>Possible values for Game type:</dt>\r\n<dd>0x00: Normal</dd>\r\n<dd>0x01: Ladder</dd>\r\n<dd>0x03: Ironman (W2BN only)</dd>\r\n<dt>Possible values for Result:</dt>\r\n<dd>0x00: No player</dd>\r\n<dd>0x01: Win</dd>\r\n<dd>0x02: Loss</dd>\r\n<dd>0x03: Draw</dd>\r\n<dd>0x04: Disconnect</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (188,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,48,'SID_CDKEY','(DWORD) Result\r\n(STRING) Key owner','This packet is identical to SID_CDKEY (0x30)\r\n\r\n<dl><dt>Possible values for Result:</dt>\r\n<dd>0x01: Ok\r\n<dd>0x02: Invalid key</dd>\r\n<dd>0x03: Bad product</dd>\r\n<dd>0x04: Banned</dd>\r\n<dd>0x05: In use</dd></dl>\r\n\r\nIn addition, the Key Owner field has two special values:\r\n<ul><li>\'TOO MANY SPAWNS\': Exceeded spawn limit for this CD-Key</li>\r\n<li>\'NO SPAWNING\': Spawns are not supported for this CD-Key</li></ul>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (189,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,25,'MCP_CHARLIST2','(WORD) Number of characters requested\r\n(DWORD) Number of characters that exist on this account\r\n(WORD) Number of characters returned\r\n\r\n<dl>\r\n<dt>For each character:</dt>\r\n<dd>(DWORD) Expiration Date</dd>\r\n<dd>(STRING) Character name</dd>\r\n<dd>(STRING) Character statstring</dd>\r\n</dl>','Lists characters.\r\n\r\nThe expiration date is a second count. To determine when the character will expire, add this time to  January 1 00:00:00 UTC 1970 and determine the difference between that value and now (all in seconds).\r\n\r\nThe statstrings in this packet  do not contain the product or character name. Everything else is the same as a character statstring you would receive from a character entering the channel.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (190,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,13,'D2GS_RIGHTSKILLONENTITY','(DWORD) Entity Type\r\n(DWORD) Entity ID \r\n\r\nEntity types\r\n--------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n','Uses your right skill on the Entity specefied in Entity ID\r\n\r\nRefer to the D2GS Entity Types document for more information about Entities');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (191,NULL,'2011-06-02 18:28:52',0,0,NULL,1,1,1,101,'SID_FRIENDSLIST','[blank]','Requests a friends list from Battle.net.\r\n\r\nWorks for all clients, however D2DV/D2XP will not receive friend updates automatically. For those clients, you must request the list at your own intervals if you wish to know about any updates.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (192,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,6,'D2GS_LEFTSKILLONENTITY','(DWORD) *Entity Type\r\n(DWORD) Entity ID\r\n\r\n*Entity Types\r\n  -------------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n','Uses your left skill on the Entity specefied in Entity ID.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (193,NULL,'2011-09-12 04:01:05',0,0,NULL,1,1,1,122,'SID_CLANRANKCHANGE','(DWORD) Cookie\r\n(STRING) Username\r\n(BYTE) New rank \r\n','Used by leaders and officers to change the rank of a clan member.\r\n\r\nNew rank:\r\n<dl>\r\n<dd>0x00: Initiate</dd>\r\n<dd>0x01: Peon</dd>\r\n<dd>0x02: Grunt</dd>\r\n<dd>0x03: Shaman</dd>\r\n<dd>0x04: Chieftain</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (194,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,34,'SID_NOTIFYJOIN','(DWORD) Product ID *\r\n(DWORD) Product version\r\n(STRING) Game Name\r\n(STRING) Game Password','Notifies Battle.net that the client has joined a game. This is what causes you to receive \"Your friend _ entered a _ game called _.\" from Battle.net if you are mutual friends with this client.\r\n\r\nSID_LEAVECHAT (0x10) does not need to be sent after this, since this does what LEAVECHAT does but with an added notification.\r\n\r\n* This can be any valid Product ID, even if you are not connected with that ID.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (195,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,51,'SID_GETFILETIME','(DWORD) Request ID\r\n(DWORD) Unknown\r\n(FILETIME) Last update time\r\n(STRING) Filename ','Contains the latest filetime for the requested file. If the file does not exist, the returned filetime is null.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (196,NULL,'2012-02-26 04:22:27',0,0,NULL,1,7,2,15,'BNLS_AUTHORIZEPROOF','(DWORD) Status code','If the client sent a valid account name, but the password checksum is incorrect, the connection is terminated. Otherwise, this response is sent. The following status codes are defined:\r\n\r\n0x00: Authorized\r\n0x01: Unauthorized\r\n\r\nView consts: [pas cpp vb]\r\n\r\nAuthorized means the login was performed as a registered account.\r\nUnauthorized means the bot ID provided was invalid, so an anonymous login was performed instead.\r\n\r\nThis indicates a success condition, and the client is now allowed to send other messages. More status codes may be added in the future.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (197,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,16,'PACKET_CHATDROPOPTIONS','(BYTE) Subcommand\r\n\r\nFor subcommand 0:\r\n(BYTE) Setting for broadcast\r\n(BYTE) Setting for database\r\n(BYTE) Setting for whispers\r\n(BYTE) Refuse all whispers \r\n','This message is only received if it is first sent to the server.\r\n\r\nPossible values:\r\n0: Allow all chat to be received (Default)\r\n1: Refuse chat from users not on an account\r\n2: Refuse all chat');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (198,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,6,'PACKET_USERINFO','(DWORD) Bot number\r\n(STRING) Bot name\r\n(STRING) Bot channel\r\n(DWORD) Bot server\r\n\r\nAdded in Revision 0x02: \r\n(STRING) Unique account name\r\n\r\nAdded in Revision 0x03: \r\n(STRING) Current database','This packet is sent to request a list of all users currently logged onto the BotNet.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (199,NULL,'2012-03-20 12:53:58',0,0,NULL,2,2,1,9,'PKT_CONNTEST2','(DWORD) Server Token\r\n(DWORD) UDP Token* ','This packet is sent to establish that the client supports UDP. In particular, when the client is using a non-standard port (e.g. not port 6112), this message is used to tell the server where it should send PKT_CONNTEST2 messages.\r\n\r\n* UDP Value is received from Battle.net in DWORD 3 of SID_AUTH_INFO, or DWORD 1 of SID_LOGONCHALLENGEEX.\r\n\r\nThis message should be sent in response to either SID_LOGONCHALLENGEEX or SID_AUTH_INFO.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (200,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,87,'SID_AUTH_ACCOUNTUPGRADE','[blank]','This message is sent to upgrade an old account to an NLS-style account. It should be sent when SID_AUTH_ACCOUNTLOGON or SID_AUTH_ACCOUNTCHANGE indicates that an account upgrade is required.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (201,NULL,'2011-02-04 22:11:07',0,0,NULL,1,7,2,8,'BNLS_UPGRADEPROOF','(DWORD) [22] Data for SID_AUTH_ACCOUNTUPGRADEPROOF (0x58)','This message contains the data to send in SID_AUTH_ACCOUNTUPGRADEPROOF (0x58).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (202,4955,'2010-07-15 02:47:39',0,1,'2014-06-01 18:38:54',1,1,1,27,'SID_GAMEDATAADDRESS','(WORD) Unknown 1\r\n(SOCKADDR) Address &amp; Port\r\n<dl><dd>SOCKADDR structure is as follows:</dd>\r\n<dd>(WORD) Port</dd>\r\n<dd>(DWORD) Address</dd></dl>\r\n(DWORD) Unknown 2\r\n(DWORD) Unknown 3','Specifies host &amp; port that a game creator is using for a game.\r\n\r\nThe three unknowns come from PvPGN documentation. The first unknown appears to always be 0x02, which is also seen in game listings. More info: <a href=\"https://pastebin.com/iDtEmhpr\">https://pastebin.com/iDtEmhpr</a>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (203,NULL,'2010-08-06 01:40:48',0,0,NULL,1,7,2,6,'BNLS_CHANGEPROOF','(DWORD) [21] Data for SID_AUTH_ACCOUNTCHANGEPROOF (0x56)','This message contains the data to send in SID_AUTH_ACCOUNTCHANGEPROOF (0x56).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (204,NULL,'2011-08-21 19:25:06',0,0,NULL,1,6,2,1,'PACKET_LOGON','(DWORD) Result *\r\n(DWORD) Your IP [protocol version 4]','* Possible values:\r\n 0: Failed\r\n 1: Succeeded\r\nIn server version 4, BotNet will always send Success.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (205,NULL,'2010-08-06 01:34:49',0,0,NULL,1,7,1,8,'BNLS_UPGRADEPROOF','(DWORD) Session key from SID_AUTH_ACCOUNTUPGRADE (0x57)','This message will parse data from SID_AUTH_ACCOUNTUPGRADE (0x57) and will reply with data to send in SID_AUTH_ACCOUNTUPGRADEPROOF (0x58).\r\n\r\nYou must send BNLS_UPGRADECHALLENGE (0x07) before you can send this.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (206,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,47,'D2GS_NPCINIT','(DWORD) Entity Type\r\n(DWORD) Entity ID \r\n\r\nEntity types\r\n--------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances','Initiate an NPC sesstion, sent following:\r\nC->S 0x13\r\nIt indicates that you are now interacting with an NPC, and a dialog window is opened.\r\n\r\nThis is prior to any choices being made to talk or trade etc.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (207,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,11,'PACKET_BOTNETCHAT','(DWORD) Command\r\n(DWORD) Action\r\n(DWORD) ID of source bot\r\n(STRING) Message ','Possible Command values:\r\n0: Message to all bots\r\n1: Message to bots on current database\r\n2: Message from a bot specified by ID. \r\n\r\nPossible Action values:\r\n0: Talk\r\n1: Emote');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (208,NULL,'2011-07-28 16:52:14',0,0,NULL,1,7,2,13,'BNLS_CHOOSENLSREVISION','(BOOLEAN) Success code (32-bit)','If the Success code is TRUE, the revision number was recognized by the server and will be used. If it is FALSE, the revision number was rejected by the server and this request is ignored.\r\n\r\nNOTE: The default revision number is 1.\r\nTherefore, if Battle.net reports a revision number of 1, this message may be omitted.\r\nIn almost all cases, you will have to send this to BNLS before any NLS work.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (209,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,13,'PACKET_ACCOUNT','(DWORD) Command\r\n\r\nFor Command 0x00 (Login):\r\n(STRING) Account name\r\n(STRING) Account password\r\n\r\nFor Command 0x01 (Change password):\r\n(STRING) Account name\r\n(STRING) Old password\r\n(STRING) New password\r\n\r\nFor Command 0x02 (Create account):\r\n(STRING) Account name\r\n(STRING) Account password\r\n','Command values other than those listed are reserved for future use.\r\n\r\nPossible CommandIDs:\r\n\r\n0x00: Login\r\n0x01: Change password\r\n0x02: Create account');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (210,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,83,'SID_AUTH_ACCOUNTLOGON','(DWORD) Status\r\n(BYTE) [32] Salt (s)\r\n(BYTE) [32] Server Key (B)','Reports the success or failure of the logon request.\r\n\r\nPossible status codes:<dl><dd>0x00: Logon accepted, requires proof.</dd>\r\n<dd>0x01: Account doesn\'t exist.</dd>\r\n<dd>0x05: Account requires upgrade.</dd>\r\n<dd>Other: Unknown (failure).</dd></dl>\r\n\r\nSee the [NLS/SRP Protocol] document for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (211,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,119,'SID_CLANINVITATION','(DWORD) Cookie\r\n(BYTE) Result','This packet is sent to inform the client of the reply to an invitation.\r\n\r\nResult:<dl>\r\n<dd>0x00: Invitation accepted</dd>\r\n<dd>0x04: Invitation declined</dd>\r\n<dd>0x05: Failed to invite user</d>\r\n<dd>0x09: Clan is full</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (212,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,63,'D2GS_CHARACTERPHRASE','(WORD) Phrase ID \r\n','All phrases sent to the server will be heard by all players in your vicinity.\r\n\r\nPhrase IDs: (Note: The deciding of which of the two phrases are used is random)\r\n[0x19] \"Help!\" / \"Help me!\"\r\n[0x1A] \"Follow me!\" / \"Come on!\"\r\n[0x1B] \"This is yours.\" / \"This is for you.\"\r\n[0x1C] \"Thanks!\" / \"Thank you.\"\r\n[0x1D] \"Uh, oops.\" / \"Forgive me.\"\r\n[0x1E] \"Bye!\" / \"Good Bye!\"\r\n[0x1F] \"Die!\" / \"Time to die!\"\r\n[0x20] \"Run!\" / \"Run away!\" Please note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (213,NULL,'2011-02-04 22:31:32',0,0,NULL,1,7,2,9,'BNLS_VERSIONCHECK','(BOOLEAN) Success\r\n\r\nIf Success is TRUE:\r\n(DWORD) Version\r\n(DWORD) Checksum\r\n(STRING) Version check stat string','This message contains the information required for the specified product.\r\n\r\nSuccess is TRUE if successful, FALSE otherwise. If this is FALSE, there is no more data in this message.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (214,NULL,'2011-02-04 22:04:06',0,0,NULL,1,7,1,4,'BNLS_CREATEACCOUNT','(STRING) Account name\r\n(STRING) Account password','This message will request the data you need for SID_AUTH_ACCOUNTCREATE (0x52).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (215,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,50,'D2GS_NPCBUY','(DWORD) NPC ID - Unconfirmed\r\n(DWORD) Item ID - Unconfirmed\r\n(DWORD) Buffer Type - Unconfirmed\r\n(DWORD) Cost \r\n','Buys an item from a Non Player Character.\r\n\r\nPossible Buffer Types:\r\n0x00 - Regular (ordinary item)\r\n0x02 - Gambled \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.\r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (216,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,125,'SID_CLANMEMBERLIST','(DWORD) Cookie\r\n(BYTE) Number of Members\r\nFor each member:<dl>\r\n<dd>(STRING) Username</dd>\r\n<dd>(BYTE) Rank</dd>\r\n<dd>(BYTE) Online Status</dd>\r\n<dd>(STRING) Location</dd></dl>','Contains the members of a clan.\r\n\r\nOnline Status:<dl><dd>0x00: Offline\r\n0x01: Online</dd></dl>\r\nRank:<dl><dd>0x00: Initiate that has been in the clan for less than one week</dd>\r\n<dd>0x01: Initiate that has been in the clan for over one week</dd>\r\n<dd>0x02: Member</dd>\r\n<dd>0x03: Officer</dd>\r\n<dd>0x04: Leader</dd></dl>\r\nLocation:<dl><dd>Where the user is, i.e., game name, channel name, or this may be null if the user is not online.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (218,NULL,'2011-06-25 04:36:04',0,0,NULL,1,1,1,121,'SID_CLANINVITATIONRESPONSE','(DWORD) Cookie\r\n(DWORD) Clan tag\r\n(STRING) Inviter\r\n(BYTE) Status \r\n','This packet is sent to accept or decline an invitation to a clan.\r\n\r\nYou must reply to this packet using one of two statuses:\r\n<dl>\r\n<dd>0x04: Declined</dd>\r\n<dd>0x06: Accepted</dd>\r\n</dl>\r\nIf you do not reply after about 30 seconds (give or take), the server assumes declined.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (219,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,88,'SID_AUTH_ACCOUNTUPGRADEPROOF','(DWORD) Status\r\n(DWORD) [5] Password proof','Status codes:<dl><dd>0x00: Password changed</dd>\r\n<dd>0x02: Incorrect old password</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (220,NULL,'2011-06-06 01:39:18',0,0,NULL,1,1,2,49,'SID_CHANGEPASSWORD','(DWORD) Result','Reports whether the password was changed or not. This is used for the old logon system.\r\n\r\n<dl>\r\n<dd>0x00: Success</dd>\r\n<dd>0x01: Failure</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (221,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,29,'D2GS_SETBYTEATTR','(BYTE) Attribute\r\n(BYTE) Amount \r\n','Sets the current (base) amount of the specified attribute.\r\n\r\nNote: Since these are only base amounts, items that give increase/decrease the attribute should be added/subtracted to/from the base value sent in the message.\r\n\r\nPossible Attributes:\r\n\r\n0x00 - Strength \r\n0x01 - Energy \r\n0x02 - Dexterity \r\n0x03 - Vitality \r\n0x04 - Stat remaining \r\n0x05 - Skill remaining \r\n0x0C - Level \r\n0x0D - Experience \r\n0x0E - Gold on Person \r\n0x0F - Gold in Stash \r\n0x10 - Defense \r\n0x11 - Max Attack Damage \r\n0x12 - Min Attack Damage \r\n0x13 - Attack Rating \r\n0x15 - Min Attack Damage \r\n0x16 - Max Attack Damage \r\n0x19 - Damage \r\n0x1f - Defense \r\n0x27 - Fire Resistance \r\n0x28 - +Max Fire Resistance \r\n0x29 - Cold Resistance \r\n0x2A - +Max Cold Resistance \r\n0x2B - Lightning Resistance \r\n0x2C - +Max Lightning Resistance \r\n0x2D - Poison Resistance \r\n0x2E - +Max poison Resistance \r\n0x31 - Add Fire Damage \r\n0x33 - Add Lightning damage \r\n0x35 - Add Damage for Weapon/Items probably (one of these may be cold damage) \r\n0x37 - Add Damage for Weapon/Items probably (one of these may be cold damage) \r\n0x3A - Add Poison Damage \r\n0x4C - Add Max Health for Weapon/Items probably \r\n0x4D - Add Max Mana for Weapons/Items probably \r\n0xA2 - Add Max Stamina for Weapons/Items probably \r\n0xA3 - Add Max Stamina for Weapons/Items probably \r\n0xAB - Add to Defense\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (222,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,30,'D2GS_SETWORDATTR','(BYTE) Attribute\r\n(WORD) Amount \r\n','Sets the current (base) amount of the specified attribute.\r\n\r\nNote: Since these are only base amounts, items that give increase/decrease the attribute should be added/subtracted to/from the base value sent in the message.\r\n\r\nFor attributes, see D2GS_SETBYTEATTR.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (223,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,89,'SID_SETEMAIL','[blank]','Requests the client to bind an email address to the account.\r\n\r\nSee SID_SETEMAIL for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (224,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,4,'MCP_JOINGAME','(WORD) Request ID\r\n(STRING) Game name\r\n(STRING) Game Password','This must be sent after a successful game creation.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (225,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,58,'SID_LOGONRESPONSE2','(DWORD) Client Token\r\n(DWORD) Server Token\r\n(DWORD) [5] Password Hash\r\n(STRING) Username','This packet is the same as SID_LOGONRESPONSE, but has additional response codes.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (226,NULL,'2011-07-21 12:40:58',0,0,NULL,1,1,1,61,'SID_CREATEACCOUNT2','(DWORD) [5] Password hash\r\n(STRING) Username','Creates a Battle.net account. Usernames longer than 15 characters are truncated.\r\n\r\nPasswords should be converted to lower case before hashing, and are only hashed once (unlike SID_LOGONRESPONSE).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (227,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,12,'SID_JOINCHANNEL','(DWORD) Flags\r\n(STRING) Channel \r\n','Joins a channel after entering chat.\r\n\r\nThe Channel name must be no greater than 31 characters, otherwise it becomes trimmed by Battle.net.\r\n\r\nThe flags field may contain the following values:\r\n0x00: NoCreate join\r\n0x01: First join\r\n0x02: Forced join\r\n0x05: D2 first join\r\n\r\n<dt>NoCreate Join:</dt>\r\n<dd>This will only join the channel specified if it is not empty, and is used by clients when selecting a channel from the channels menu. If the channel is empty, Battle.net sends a SID_CHATEVENT of type EID_CHANNELDOESNOTEXIST, upon which official clients prompt for confirmation that the user wishes to create the channel, in which case, it resends this packet with Flags set to Forced Join (0x02).</dd>\r\n<dt>First Join:</dt>\r\n<dd>Places user in a channel starting with their product and country, followed by a number, ie \'Brood War GBR-1\'. Also automatically sends MOTD after entering the channel. When using this type, the Channel variable has no effect, but must be present anyway to avoid an IP ban. This is sent when first logging onto Battle.net</dd>\r\n<dt>Forced Join:</dt>\r\n<dd>This is sent when leaving a game, and joins the specified channel without an supplying an MOTD.</dd>\r\n<dt>D2 First Join:</dt>\r\n<dd>The same as First join, but is used for D2DV/D2XP clients.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (228,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,42,'SID_CREATEACCOUNT','(DWORD) Result','Results:\r\n<dl><dd>0x00: Fail</dd>\r\n<dd>0x01: Success</dd></dl>\r\nReports success/fail on account creation.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (229,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,7,'PACKET_USERLOGGINGOFF','(DWORD) Bot id \r\n','This message is sent from the server when another client has disconnected from the BotNet. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (230,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,16,'D2GS_CHARTOOBJ','(BYTE) Unknown\r\n(DWORD) Player ID\r\n(BYTE) Movement Type\r\n(BYTE) Destination Type\r\n*(DWORD) Object ID \r\n*(WORD) X Coordinate\r\n*(WORD) Y Coordinate \r\n','A character moves to an object within your view range.\r\n\r\n* - These are what the player is moving to.\r\n\r\nPossible values for Destination Type:\r\n0x00 - Another player\r\n0x01 - NPC or Monster\r\n0x02 - Object such as Stash, Waypoint, or a Town Portal\r\n0x04 - Item\r\n0x05 - Doorway\r\n\r\n\r\nPossible value for Movement Type:\r\n\r\n0x18 - Run\r\n0x00 - Walk Please note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (231,NULL,'2011-08-21 19:55:49',0,0,NULL,1,6,1,1,'PACKET_LOGON','(STRING) BotID\r\n(STRING) Bot Password \r\n','This message is sent in order to identify the client to the Botnet server. In order to get a BotID and password, contact the BotNet server admin. For botnet.valhallalegends.com, the admin was Skywing[vL] by visiting op [vL] on Battle.net (USEast). This may also be called the \"hub ID\".');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (232,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,3,'D2GS_RUNTOLOCATION','(WORD) X coordinate\r\n(WORD) Y coordinate \r\n','Run to a specified (X,Y) map coordinate. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (233,NULL,'2012-06-01 21:11:16',0,0,NULL,1,1,1,47,'SID_FINDLADDERUSER','(DWORD) Product ID\r\n(DWORD) League\r\n(DWORD) Sort method\r\n(STRING) Username ','Requests a user\'s status on ladder.\r\n\r\nLeague is 1 for Ladder, 3 for Iron Man.\r\n\r\n<dl><dt>Sort methods:</dt>\r\n<dd>0x00: Highest Rating</dd>\r\n<dd>0x01: Fastest Climbers (Unused)</dd>\r\n<dd>0x02: Most Wins</dd>\r\n<dd>0x03: Most Games Played</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (234,NULL,'2012-03-20 12:55:09',0,0,NULL,2,2,1,8,'PKT_CONNTEST','(DWORD) Server Token ','This packet is sent to establish that the client supports UDP. In particular, when the client is using a non-standard port (e.g. not port 6112), this message is used to tell the server where it should send PKT_CONNTEST messages.\r\n\r\nThis message should be sent in response to SID_LOGONCHALLENGE.\r\n\r\nNot used by official clients in favor of PKT_CONNTEST2.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (235,NULL,'2011-06-25 04:29:40',0,0,NULL,1,1,2,121,'SID_CLANINVITATIONRESPONSE','(DWORD) Cookie\r\n(DWORD) Clan tag\r\n(STRING) Clan name\r\n(STRING) Inviter \r\n','This packet is received when someone invites you to join a clan.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (236,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,23,'MCP_CHARLIST','(WORD) Number of characters requested\r\n(DWORD) Number of characters that exist on this account\r\n(WORD) Number of characters returned\r\n<dl>\r\n<dt>For each character:</dt>\r\n<dd>(STRING) Character name</dd>\r\n<dd>(STRING) Character statstring</dd>\r\n</dl>','Lists characters.\r\n\r\nThe statstrings in this packet  do not contain the product or character name. Everything else is the same as a character statstring you would receive from a character entering the channel.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (237,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,62,'SID_LOGONREALMEX','(DWORD) MCP Cookie\r\n(DWORD) MCP Status\r\n(DWORD) [2] MCP Chunk 1\r\n(DWORD) IP\r\n(DWORD) Port\r\n(DWORD) [12] MCP Chunk 2\r\n(STRING) Battle.net unique name','This packet supplies the data necessary to connect to an MCP server. The cookie value is defined in the first DWORD of SID_LOGONREALMEX and is simply returned by Battle.net. If the length of the message (not including header) is greater than 8, the client should continue to logon to the MCP. Otherwise, the Status field is interpreted as an error code:\r\n\r\n0x80000001: Realm is unavailable\r\n0x80000002: Realm logon failed\r\n\r\nAny other value indicates failure.\r\n\r\nThe first part of the MCP Chunk that must be sent to the MCP server includes the Status and Cookie DWORDS, making MCP Chunk 1 the first 4 DWORDS of the packet.\r\n\r\nThere used to be a WORD at the end of this packet, which was apparently random.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (238,NULL,'2010-08-06 01:12:25',0,0,NULL,1,7,2,17,'BNLS_VERIFYSERVER','(BOOLEAN) Success (32-bit)','If Success is TRUE, the signature matches the server\'s IP. If Success is FALSE, the signature does not match.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (239,NULL,'2011-02-04 22:37:44',0,0,NULL,1,7,1,18,'BNLS_RESERVESERVERSLOTS','(DWORD) Number of slots to reserve \r\n','This message reserves a number of slots for concurrent NLS checking operations. No other NLS checking messages can be sent before this message has been sent.This message cannot be sent more than once per connection.\r\n\r\nBNLS may limit the number of slots to a reasonable value.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (240,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,75,'SID_EXTRAWORK','(WORD) Game type\r\n(WORD) Length\r\n(STRING) Work returned data \r\n','Response for both SID_OPTIONALWORK and SID_REQUIREDWORK.\r\n\r\nGame type:\r\n<dl><dd>0x01: Diablo II</dd>\r\n<dd>0x02: Warcraft III</dd>\r\n<dd>0x03: Starcraft</dd>\r\n<dd>0x04: World of Warcraft (Reason for this is not known, but most recent libraries have included it)</dd></dl>\r\n\r\nLength:<dl><dd>The length is returned from the call to ExtraWork in the ExtraWork DLL. Traditionally, the library responsible for all ExtraWork requests has been IX86ExtraWork.dll.</dd></dl>\r\nWork returned data:<dl><dd>This data is based on a 1024-byte buffer. The call to ExtraWork takes in a structure and returns the length and buffer based on the game type.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (241,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,96,'SID_GAMEPLAYERSEARCH','(BYTE) Number of players\r\n(STRING) [] Player names \r\n','Returns a list of available players for an arranged team ladder game. Players who are considered available: have no away message on, are mutual friends in the chat environment, or non-friend listed users in the same channel as you.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (242,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,19,'SID_FLOODDETECTED','[blank] ','Sent prior to a disconnect along with SID_MESSAGEBOX to indicate that the client has flooded off. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (243,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,3,'PACKET_DATABASE','(DWORD) command\r\n\r\nCommand 2:\r\n(STRING) usermask\r\n(STRING) flags\r\n\r\nCommand 3:\r\n(STRING) usermask\r\n','Response to C>0x03.\r\n\r\nPossible values for Command:\r\n2: New access list user/modify existing access list user.\r\n3: Remove a database entry');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (244,NULL,'2012-04-01 18:42:34',0,0,NULL,1,1,1,5,'SID_CLIENTID','(DWORD) Registration Version\r\n(DWORD) Registration Authority\r\n(DWORD) Account Number\r\n(DWORD) Registration Token\r\n(STRING) LAN Computer Name\r\n(STRING) LAN Username','This packet was used to ensure that the client\'s account number was valid. All but the last two fields in this message are now ignored, and may be set to zero.\r\n\r\nThe \'LAN Computer Name\' field is the NetBIOS name of the computer. It can be retrieved using the <a href=\"http://msdn2.microsoft.com/en-us/library/ms724295.aspx\">GetComputerName</a> API.\r\n\r\nThe \'Lan Username\' field is the name of the currently logged on user, and may be retrieved using the <a href=\"http://msdn2.microsoft.com/en-us/library/ms724432.aspx\">GetUsername</a> API.\r\n\r\nThe following information is historical:\r\n\r\nThe client would supply this data as issued by a Battle.net server. If the Registration Version, Registration Authority, and Client Token values equated to the account number supplied (Client ID), as determined by an unknown formula, the server would respond with the same values. If they were invalid, the server would assign new values. Registration Version was always 1, Authority was the IP address of the server that issued the account number. Thus, the Client Token was the secret value, used to prove that the client really owned the account in question.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (245,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,175,'D2GS_STARTLOGON','blank* \r\n','Permission to initiate Game Server Logon.\r\n\r\n* - Although this packet is blank, the data received will still be compressed. Meaning the actual message size will be two bytes in length. (See [Sizes & Types]) Please note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (246,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,2,'MCP_CHARCREATE','(DWORD) Result ','The return value for character creation.\r\n<dl>\r\n<dt>Known values:</dt>\r\n<dd>0x00: Success</dd>\r\n<dd>0x14: Character already exists, or maximum number of characters (currently 8) reached.</dd>\r\n<dd>0x15: Invalid name</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (247,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,70,'SID_NEWS_INFO','(DWORD) News timestamp','Requests news and MOTD from battle.net.\r\n\r\nThe news timestamp specifies the starting date for the news. To retrieve all available news entries, set this to zero. Timestamps are given in C/Unix format -- that is, the number of seconds since January 1, 1970 0:00:00.000 -- and should be biased to UTC.\r\n\r\nThis message should be sent when you receive SID_ENTERCHAT. The official client stops processing messages after the user joins a game or enters a channel, and discards messages with more than 127 entries.\r\n\r\nNews can be requested for older products, but Battle.net will only return the server\'s Message-of-the-Day. However, this behavior has not been observed in official clients, and for an accurate protocol emulation, its use is not recommended.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (248,NULL,'2011-05-22 22:07:04',0,0,NULL,1,1,2,122,'SID_CLANRANKCHANGE','(DWORD) Cookie\r\n(BYTE) Status','This message returns the result of the clan rank change request.\r\n\r\nResult:<dl>\r\n<dd>0x00: Successfully changed rank</dd>\r\n<dd>0x01: Failed to change rank</dd>\r\n<dd>0x02: Cannot change user\'s rank yet</dd>\r\n<dd>0x07: Not authorized to change user rank *</dd>\r\n<dd>0x08: Not allowed to change user rank **</dd>\r\n</dl>\r\n* This will be received when you are not a shaman/chieftain and you are trying to change the rank of another user.\r\n\r\n** This will be received when you are trying to change rank of someone who is your rank or higher such as a chieftain, or you are trying to change the rank of an initiate during their probation period.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (249,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,53,'SID_PROFILE','(DWORD) Cookie\r\n(BYTE) Success\r\n(STRING) Profile\\Description value\r\n(STRING) Profile\\Location value\r\n(DWORD) Clan Tag','If the status code is 0, the request was successful; otherwise, the lookup failed and the message length will be five bytes long (not counting the four byte header).\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (250,NULL,'2011-06-02 18:42:14',0,0,NULL,1,1,1,21,'SID_CHECKAD','(DWORD) Platform ID\r\n(DWORD) Product ID\r\n(DWORD) ID of last displayed banner\r\n(DWORD) Current time','Requests ad banner information from Battle.net.\r\n\r\nOfficial clients send this packet every 20 seconds (give or take). You do not have to send this at all.\r\n\r\nIn addition, Battle.net will not reply unless any of the fields above are updated from the last time you checked. If you have never checked before, Battle.net will always reply back.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (251,NULL,'2010-08-06 01:10:57',0,0,NULL,1,7,1,17,'BNLS_VERIFYSERVER','(DWORD) Server IP\r\n(BYTE) [128] Signature','This message verifies a server\'s signature, which is based on the server\'s IP. The signature is optionally (currently sent only with Warcraft 3) sent in SID_AUTH_INFO (0x50).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (252,NULL,'2011-02-04 22:38:46',0,0,NULL,1,7,1,20,'BNLS_SERVERLOGONPROOF','(DWORD) Slot index\r\n(DWORD) [5] Data from client\'s SID_AUTH_ACCOUNTLOGONPROOF (0x54)\r\n(STRING) Client\'s account name','This message performs two operations. First, it checks if the client\'s logon was successful. Second, it calculates the data for the server\'s reply to SID_AUTH_ACCOUNTLOGONPROOF (0x54). If this data is not correct, then the client will not accept the logon attempt as valid.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (253,NULL,'2011-06-02 18:32:22',0,0,NULL,1,1,1,102,'SID_FRIENDSUPDATE','(BYTE) Index on friends list','This message requests an update on a specific user on the friends list. Battle.net should reply immediately with the same packet (see related).\r\n\r\nThe indexes are 0-based, so friend #1 on the friends list would have a value of 0 in this message, friend #2 would be 1, and so on.\r\n\r\nWorks for all clients, however D2DV/D2XP will not receive friend updates automatically. For those clients, you must request the list at your own intervals if you wish to know about any updates.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (254,NULL,'2012-06-01 21:11:02',0,0,NULL,1,1,2,46,'SID_GETLADDERDATA','(DWORD) Product ID\r\n(DWORD) League\r\n(DWORD) Sort method\r\n(DWORD) Starting rank\r\n(DWORD) Number of ranks listed (Count of items in list)\r\n<dl><dt>For each list item:</dt>\r\n<dd>(DWORD) Wins</dd>\r\n<dd>(DWORD) Losses</dd>\r\n<dd>(DWORD) Disconnects</dd>\r\n<dd>(DWORD) Rating</dd>\r\n<dd>(DWORD) Rank</dd>\r\n<dd>(DWORD) Official wins</dd>\r\n<dd>(DWORD) Official losses</dd>\r\n<dd>(DWORD) Official disconnects</dd>\r\n<dd>(DWORD) Official rating</dd>\r\n<dd>(DWORD) Unknown</dd>\r\n<dd>(DWORD) Official rank</dd>\r\n<dd>(DWORD) Unknown</dd>\r\n<dd>(DWORD) Unknown</dd>\r\n<dd>(DWORD) Highest rating</dd>\r\n<dd>(DWORD) Unknown</dd>\r\n<dd>(DWORD) Season</dd>\r\n<dd>(FILETIME) Last game time</dd>\r\n<dd>(FILETIME) Official last game time</dd>\r\n<dd>(STRING) Name</dd></dl>','Contains the requested ladder data. \r\n\r\nLeague is 1 for Ladder, 3 for Iron Man.\r\n\r\n<dl><dt>Sort methods:</dt>\r\n<dd>0x00: Highest Rating</dd>\r\n<dd>0x01: Fastest Climbers (Unused)</dd>\r\n<dd>0x02: Most Wins</dd>\r\n<dd>0x03: Most Games Played</dd></dl>\r\n\r\nAll unknowns are 0.\r\nRank is 0-based. A rank of 0xFFFFFFFF is no rank.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (255,NULL,'2011-05-01 00:30:26',0,0,NULL,1,1,2,61,'SID_CREATEACCOUNT2','(DWORD) Status\r\n(STRING) Account name suggestion','Account creation result.\r\n<dl><dt>Result:</dt>\r\n<dd>0x00: Account created</dd>\r\n<dd>0x01: Name is too short</dd>\r\n<dd>0x02: Name contained invalid characters</dd>\r\n<dd>0x03: Name contained a banned word</dd>\r\n<dd>0x04: Account already exists</dd>\r\n<dd>0x05: Account is still being created</dd>\r\n<dd>0x06: Name did not contain enough alphanumeric characters</dd>\r\n<dd>0x07: Name contained adjacent punctuation characters</dd>\r\n<dd>0x08: Name contained too many punctuation characters</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (256,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,104,'SID_FRIENDSREMOVE','(BYTE) Entry Number','Sent to clients supporting this packet when an entry is removed from the client\'s friendlist. Accounts positioned after the specified Entry Number in the friendlist are moved up by one.\r\n\r\nSee SID_FRIENDSLIST for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (257,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,116,'SID_CLANMAKECHIEFTAIN','(DWORD) Cookie\r\n(STRING) New Cheiftain','Changes the clan\'s chieftain.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (258,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,27,'D2GS_SWAP2HANDEDITEM','(DWORD) Item ID\r\n*(DWORD)  Body Location\r\n\r\n*0x04 is left hand\r\n*0x05 is right hand','Moves item from body location to player\'s cursor buffer.\r\n\r\nBody locations are the same as in D2GS_ITEMTOBODY\r\n\r\nPlease note: This message\'s official name is not known, and has been invented. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (259,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,113,'SID_CLANINVITEMULTIPLE','(DWORD) Cookie\r\n(STRING) Clan name\r\n(DWORD) Clan tag\r\n(BYTE) Number of users to invite\r\n(STRING) [] Usernames to invite \r\n','This message is used to invite the initial 9 required members to a new clan. The users specified in this packet will receive 0x72.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (260,NULL,'2010-07-15 02:47:39',0,0,NULL,1,7,1,26,'BNLS_VERSIONCHECKEX2','(DWORD) Product ID.*\r\n(DWORD) Flags.**\r\n(DWORD) Cookie.\r\n(FILETIME) Timestamp for version check archive.\r\n(STRING) Version check archive filename.\r\n(STRING) Checksum formula. \r\n','This message will request a fast version check and parses the version check filename so the client does not have to. Now works with all products.\r\n\r\n* Valid Product IDs are:\r\n\r\n0x01: Starcraft\r\n0x02: Starcraft: Broodwar\r\n0x03: Warcraft II: BNE\r\n0x04: Diablo II\r\n0x05: Diablo II: LoD\r\n0x06: Starcraft: Japanese\r\n0x07: Warcraft III\r\n0x08: Warcraft III: The Frozen Throne\r\n\r\nView consts: [pas cpp vb]\r\n\r\n** Currently there are no flags defined, this must be set to zero.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (261,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,25,'MCP_CHARLIST2','(DWORD) Number of characters to list.','Requests a character list. \r\n\r\nUp to a maximum of 8 characters can be requested.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (262,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,41,'SID_LOGONRESPONSE','(DWORD) Client Token\r\n(DWORD) Server Token\r\n(DWORD) [5] Password Hash\r\n(STRING) Username ','Contains Client\'s username & hashed password.\r\n\r\nBattle.net password hashes are hashed twice. First, the password is hashed by itsself, then the following data is hashed again and sent to Battle.net:\r\n\r\nClient Token\r\nServer Token\r\nFirst password hash (20 bytes)\r\n\r\nPasswords should be converted to lower case before hashing.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (263,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,4,'PACKET_MESSAGE','(STRING) User\r\n(STRING) Command \r\n','Send command to bots on current database. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (264,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,122,'D2GS_LOGONRESPONSE','(DWORD) Unknown - Possible acceptance/request ID \r\n','This message is originally received compressed, so the message ID will correspond with D2GS compressed format.\r\n\r\nThis message is received if your logon is accepted by the D2GS.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (265,NULL,'2010-10-07 17:37:56',0,0,NULL,1,1,1,28,'SID_STARTADVEX3','(DWORD) Game State\r\n(DWORD) Game Uptime in seconds\r\n(WORD) Game Type\r\n(WORD) Sub Game Type\r\n(DWORD) Provider Version Constant (0xFF)\r\n(DWORD) Ladder Type\r\n(STRING) Game Name\r\n(STRING) Game Password\r\n(STRING) Game Statstring','Used by clients to inform the server that a game has been created, or that the state of a created game has changed.\r\n<dl><dt>Bitwise flags for State:</dt>\r\n<dd>0x01: Game is private</dd>\r\n<dd>0x02: Game is full</dd>\r\n<dd>0x04: Game contains players (other than creator)</dd>\r\n<dd>0x08: Game is in progress</dd>\r\n<dd>0x80: Game is a replay</dd></dl>\r\n<dl><dt>Possible values for Game Type (Sub Game Type):</dt>\r\n<dd>0x02: Melee</dd>\r\n<dd>0x03: Free for All</dd>\r\n<dd>0x04: 1 vs 1</dd>\r\n<dd>0x05: Capture The Flag</dd>\r\n<dd>0x06: Greed (Resources, 0x01: 2500, 0x02: 500, 0x03: 7500, 0x04: 10000)</dd>\r\n<dd>0x07: Slaughter (Minutes, 0x01: 15, 0x02: 30, 0x03: 45, 0x04: 60)</dd>\r\n<dd>0x08: Sudden Death</dd>\r\n<dd>0x09: Ladder (Disconnects, 0x00: Not a loss, 0x01: Counts as a loss)</dd>\r\n<dd>0x0A: Use Map Settings</dd>\r\n<dd>0x0B: Team Melee (Number Of Teams, 0x01: 2 Teams, 0x02: 3 Teams, etc.)</dd>\r\n<dd>0x0C: Team Free For All (Number Of Teams, 0x01: 2 Teams, 0x02: 3 Teams, etc.)</dd>\r\n<dd>0x0D: Team Capture The Flag (Number Of Teams, 0x01: 2 Teams, 0x02: 3 Teams, etc.)</dd>\r\n<dd>0x0F: Top vs. Bottom (Number Of Teams, 1-7 specifies the ratio of players belonging to both teams)</dd>\r\n<dd>0x10: Iron Man Ladder (W2BN only)</dd>\r\n<dd>0x20: PGL</dd></dl>\r\n\r\nProvider Version Constant: The Current version of the game (1.16.1) uses 0xFF, previous versions have used 0x1F, but this may vary depending on game version.\r\n\r\n<dl><dt>Possible values for Ladder:</dt>\r\n<dd>0x00: Game is Normal (Non-Ladder)</dd>\r\n<dd>0x01: Game is Ladder</dd>\r\n<dd>0x03: Game is Iron Man Ladder (W2BN only)</dd></dl>\r\nIt could be that the ladder is bitwise as well, and that 0x02 means Iron Man and 0x03 just means Iron Man + Ladder.<br>\r\nSub Game Type appears to be 0x01 for all game types except Top vs Bottom, where it seems to depend on the size of each team. More research will be needed to confirm.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (266,NULL,'2011-02-19 04:13:29',0,1,'2014-05-25 11:17:53',1,1,2,9,'SID_GETADVLISTEX','(DWORD) Number of games\r\n<dl><dt>If count is 0:</dt>\r\n<dd>(DWORD) Status</dd>\r\n<dt>Otherwise, games are listed thus:</dt>\r\n<dd><dl><dt>For each list item:</dt>\r\n<dd><dl><dt><b>WarCraft III:</b></dt>\r\n<dd>(DWORD) Game Type</dd>\r\n<dd>(DWORD) Language ID</dd>\r\n<dd>(WORD) Address Family (Always AF_INET)</dd>\r\n<dd>(WORD) Port</dd>\r\n<dd>(DWORD) Host\'s IP</dd>\r\n<dd>(DWORD) sin_zero (0)</dd>\r\n<dd>(DWORD) sin_zero (0)</dd>\r\n<dd>(DWORD) Unknown (Public = 0x10, Private = 0x11)</dd>\r\n<dd>(DWORD) Elapsed time (in seconds)</dd>\r\n<dd>(STRING) Game name *</dd>\r\n<dd>(STRING) Game password (always empty)</dd>\r\n<dd>(BYTE) Free slots (character representing hexadecimal number)</dd>\r\n<dd>(BYTE)[8] Host counter (characters representing reversed hexadecimal number, ex: \'20000000\' for second time this host has hosted during his session)</dd>\r\n<dd>(STRING) Encoded map information</dd></dl>\r\n<dd><dl><dt><b>All Other Clients:</b></dt>\r\n<dd>(WORD) Game Type</dd>\r\n<dd>(WORD) Sub Game Type</dd>\r\n<dd>(DWORD) Language ID</dd>\r\n<dd>(WORD) Address Family (Always AF_INET)</dd>\r\n<dd>(WORD) Port</dd>\r\n<dd>(DWORD) Host\'s IP</dd>\r\n<dd>(DWORD) sin_zero (0)</dd>\r\n<dd>(DWORD) sin_zero (0)</dd>\r\n<dd>(DWORD) Game Status</dd>\r\n<dd>(DWORD) Elapsed time (in seconds)</dd>\r\n<dd>(STRING) Game name *</dd>\r\n<dd>(STRING) Game password</dd>\r\n<dd>(STRING) Game statstring</dd></dl></dd>\r\n</dl></dd></dl></dd></dl>\r\n','Returns a list of available games and their information. Varies depending on product.\r\n\r\n\r\n* This field is UTF-8 encoded.\r\n\r\nFor WarCraft III, map information is encoded using the function found <a href=\"http://forum.valhallalegends.com/index.php?topic=15419.5\">here</a>:\r\n<dl><dt>Encoded data:</dt>\r\n<dd>(DWORD) Map Flags</dd>\r\n<dd>(BYTE) Map Null 1</dd>\r\n<dd>(BYTE) Map Width (playable area)</dd>\r\n<dd>(BYTE) Map Null 2</dd>\r\n<dd>(BYTE) Map Height (playable area)</dd>\r\n<dd>(BYTE) Map Null 3</dd>\r\n<dd>(DWORD) Map CRC</dd>\r\n<dd>(STRING) Map Path</dd>\r\n<dd>(STRING) Game Host</dd>\r\n<dd>(BYTE) Map Unknown (possibly a STRING with just the null terminator)</dd>\r\n<dd>(BYTE)[20] Unknown (probably a SHA1 hash)</dd></dl>\r\n<dl><dt>Possible flags (combine the below settings) for Map Flags:</dt>\r\n<dd><dl><dt>Game Speed (mask 0x00000003, unique)</dt>\r\n<dd>0x00000000: Slow</dd>\r\n<dd>0x00000001: Normal</dd>\r\n<dd>0x00000002: Fast</dd></dl></dd>\r\n<dd><dl><dt>Visibility setting (mask 0x00000F00, unique)</dt>\r\n<dd>0x00000100: Hide Terrain</dd>\r\n<dd>0x00000200: Map Explored</dd>\r\n<dd>0x00000400: Always Visible</dd>\r\n<dd>0x00000800: Default</dd></dl></dd>\r\n<dd><dl><dt>Observers setting (mask 0x40003000, unique)</dt>\r\n<dd>0x00000000: No Observers</dd>\r\n<dd>0x00002000: Observers on Defeat</dd>\r\n<dd>0x00003000: Full Observers</dd>\r\n<dd>0x40000000: Referees</dd></dl></dd>\r\n<dd><dl><dt>Other advanced host settings (mask 0x07064000, combinable)</dt>\r\n<dd>0x00004000: Teams Together (team members are placed at neighbored starting locations)</dd>\r\n<dd>0x00060000: Lock Teams</dd>\r\n<dd>0x01000000: Full Shared Unit Control</dd>\r\n<dd>0x02000000: Random Hero</dd>\r\n<dd>0x04000000: Random Races</dd></dl></dd></dl>\r\nNon-WarCraft III Game Statstring Field:\r\nFor other clients, game statring follows the format found <a href=\"?op=doc&did=13\">here</a>.\r\n\r\nWarCraft III Game Type Field (combine the below settings):\r\n<dl><dt>Game Type (mask 0x000000FF, unique)</dt>\r\n<dd>0x00000001: Custom</dd>\r\n<dd>0x00000009: Ladder</dd></dl>\r\n<dl><dt>Map Author (mask 0x00006000, combinable)</dt>\r\n<dd>0x00002000: Blizzard</dd>\r\n<dd>0x00004000: Custom</dd></dl>\r\n<dl><dt>Battle/Scenario (mask 0x00018000, unique)</dt>\r\n<dd>0x00000000: Battle</dd>\r\n<dd>0x00010000: Scenario</dd></dl>\r\n<dl><dt>Map Size (mask 0x000E0000, combinable)</dt>\r\n<dd>0x00020000: Small</dd>\r\n<dd>0x00040000: Medium</dd>\r\n<dd>0x00080000: Huge</dd></dl>\r\n<dl><dt>Observers (mask 0x00700000, unique)</dt>\r\n<dd>0x00100000: Allowed observers (\"Full Observers\" and \"Referees\" options)</dd>\r\n<dd>0x00200000: Observers on defeat</dd>\r\n<dd>0x00400000: No observers</dd></dl>\r\n<dl><dt>Public/Private (mask 0x00000800, unique)</dt>\r\n<dd>0x00000000: Public game</dd>\r\n<dd>0x00000800: Private game</dd></dl>\r\nSTAR/SEXP/SSHR/JSTR/W2BN Game Type Field:\r\n<dl><dt>Game Type (unique)</dt>\r\n<dd>0x01: Custom (WAR3/W3XP)</dd>\r\n<dd>0x02: Melee</dd>\r\n<dd>0x03: Free For All</dd>\r\n<dd>0x04: One vs One</dd>\r\n<dd>0x05: Capture the Flag</dd>\r\n<dd>0x06: Greed</dd>\r\n<dd>0x07: Slaughter</dd>\r\n<dd>0x08: Sudden Death</dd>\r\n<dd>0x09: Ladder</dd>\r\n<dd>0x10: Iron man ladder (W2BN)</dd>\r\n<dd>0x0A: Use Map Settings</dd>\r\n<dd>0x0B: Team Melee</dd>\r\n<dd>0x0C: Team FFA</dd>\r\n<dd>0x0D: Team CTF</dd>\r\n<dd>0x0F: Top vs. Bottom</dd></dl>\r\nSTAR/SEXP/SSHR/JSTR/W2BN Sub Game Type Field:\r\n<dl><dt>For Greed:</dt>\r\n<dd>0x01: 2500 resources</dd>\r\n<dd>0x02: 5000 resources</dd>\r\n<dd>0x03: 7500 resources</dd>\r\n<dd>0x04: 10000 resources</dd></dl>\r\n<dl><dt>For Slaughter:</dt>\r\n<dd>0x01: 15 minutes</dd>\r\n<dd>0x02: 30 minutes</dd>\r\n<dd>0x03: 45 minutes</dd>\r\n<dd>0x04: 60 minutes</dd></dl>\r\n<dl><dt>For Team Melee, Team Free For All, and Team Capture the Flag:</dt>\r\n<dd>0x01: 2 teams</dd>\r\n<dd>0x02: 3 teams</dd>\r\n<dd>0x03: 4 teams</dd></dl>\r\n<dl><dt>For Top vs. Bottom:</dt>\r\n<dd>0x01: 1 v 7</dd>\r\n<dd>0x02: 2 v 6</dd>\r\n<dd>0x03: 3 v 5</dd>\r\n<dd>0x04: 4 v 4</dd>\r\n<dd>0x05: 5 v 3</dd>\r\n<dd>0x06: 6 v 2</dd>\r\n<dd>0x07: 7 v 1</dd></dl>\r\n<dl><dt>For Ladder:</dt>\r\n<dd>0x00: Disconnects do not count as loss</dd>\r\n<dd>0x01: Disconnects count as loss</dd></dl>\r\n<dl><dt>For Other Game Types:</dt>\r\n<dd>0x01: No sub-game type</dd></dl>\r\nDRTL/DSHR Game Type Field:\r\n<dl><dt>Game Type is used to specify a \'level range\'. This ensures that clients receive a list of games containing players whose experience is similar to their own.</dt>\r\n<dd>0x00: Level 1</dd>\r\n<dd>0x01: Level 2 or 3</dd>\r\n<dd>0x02: Level 4 or 5</dd>\r\n<dd>0x03: Level 6 or 7</dd>\r\n<dd>0x04: Level 8 or 9</dd>\r\n<dd>0x05: Level 10 - 12</dd>\r\n<dd>0x06: Level 13 - 16</dd>\r\n<dd>0x07: Level 17 - 19</dd>\r\n<dd>0x08: Level 20 - 24</dd>\r\n<dd>0x09: Level 25 - 29</dd>\r\n<dd>0x0A: Level 30 - 34</dd>\r\n<dd>0x0B: Level 35 - 39</dd>\r\n<dd>0x0C: Level 40 - 47</dd>\r\n<dd>0x0D: Level 48 - 50</dd></dl>\r\n\r\nNote that fields from Address Family to sin_zero form a sockaddr_in structure.\r\n\r\nValid status codes:\r\n<dl><dt>This field is the \'Game Status\' for non-WarCraft III and the \'Status\' when no games are listed for both WarCraft III and other clients.</dt>\r\n<dd>0x00: OK</dd>\r\n<dd>0x01: Game doesn\'t exist</dd>\r\n<dd>0x02: Incorrect password</dd>\r\n<dd>0x03: Game full</dd>\r\n<dd>0x04: Game already started</dd>\r\n<dd>0x05: Spawned CD-Key not allowed</dd>\r\n<dd>0x06: Too many server requests</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (267,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,45,'D2GS_UNSELECTOBJ','blank \r\n','Unselects the selected object. Please note: This message\'s official name is not known, and has been invented. The contents of this packet may be incomplete or incorrect, and your comments and contributions are requested.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (268,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,37,'SID_PING','(DWORD) Ping Value','Ping response. Ping Value is the DWORD received in the server\'s initial ping message.\r\n\r\nThe ping displayed when in chat can be artificially inflated by delaying before sending this packet, or deflated by responding before requested.\r\nPing can be set to -1 (Strictly speaking, 0xFFFFFFFF, since ping is unsigned) by not responding to this packet at all.\r\n\r\nThe DWORD received from the server is not what determines your ping, but it is actually a cookie for the Battle.net server. You should never change the DWORD.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (269,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,25,'D2GS_SMALLGOLDPICKUP','(BYTE) Amount \r\n','Sent when you pick up a BYTE (255) of gold(254 or less).\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (270,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,86,'SID_AUTH_ACCOUNTCHANGEPROOF','(BYTE) [20] Old password proof\r\n(BYTE) [32] New password\'s salt (s)\r\n(BYTE) [32] New password\'s verifier (v) \r\n','This message is sent after receiving a successful SID_AUTH_ACCOUNTCHANGE message, and contains the proof for the client\'s new password.\r\n\r\nSee [NLS/SRP Protocol] for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (271,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,2,'SID_STOPADV','[blank]','This message is sent to inform the server that a game should no longer be advertised to other users. It is sent when a game starts, or when a game is aborted (the host leaves).\r\n\r\nAll Battle.snp clients (DRTL, DSHR, STAR/SEXP, JSTR, SSHR, and W2BN) always send this message when logging off, even if it not in a game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (272,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,21,'SID_CHECKAD','(DWORD) Ad ID\r\n(DWORD) File extension\r\n(FILETIME) Local file time\r\n(STRING) Filename\r\n(STRING) Link URL','Contains information needed to download and display an ad banner. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (273,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,92,'D2GS_(COMP)STARTGAME','Blank \r\n','This packet is part of the logon sequence, not to be confused with the other 0x5C.\r\nThis packet is originally received compressed, so the message ID will correspond with [Protocol Headers] D2GS compressed format.\r\nThis message is received with the \'OK\' that you can go ahead and enter the gaming environment. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (274,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,82,'SID_AUTH_ACCOUNTCREATE','(BYTE) [32] Salt (s)\r\n(BYTE) [32] Verifier (v)\r\n(STRING) Username','This message is sent to create an NLS-style account. It contains the client\'s salt and verifier values, which are saved by the server for use with future logons.\r\n\r\nSee the [NLS/SRP Protocol] page for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (275,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,23,'D2GS_DROPITEM','(DWORD) Item ID \r\n','Drops the item in the player\'s cursor buffer to the ground. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (276,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,36,'D2GS_REMOVEBELTITEM','(DWORD) Item ID \r\n','Moves the specified item from the belt to the player\'s cursor buffer. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (277,NULL,'2012-03-31 13:55:39',0,0,NULL,1,1,2,64,'SID_QUERYREALMS2','(DWORD) Unknown\r\n(DWORD) Count\r\n<dl><dt>For Each Realm:</dt>\r\n<dd>(DWORD) Unknown</dd>\r\n<dd>(STRING) Realm title</dd>\r\n<dd>(STRING) Realm description</dd></dl>','Realm list.\r\n\r\nThe first Unknown is usually 0. The second [and subsequent] Unknown is usually 1.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (278,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,112,'SID_CLANFINDCANDIDATES','(DWORD) Cookie\r\n(BYTE) Status\r\n(BYTE) Number of potential candidates\r\n(STRING) [] Usernames','Contains the list of potential candidates.\r\n\r\nValid Status codes:<dl>\r\n<dd>0x00: Successfully found candidate(s)</dd>\r\n<dd>0x01: Clan tag already taken</dd>\r\n<dd>0x08: Already in clan</dd>\r\n<dd>0x0A: Invalid clan tag specified</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (279,NULL,'2011-05-31 02:31:46',0,0,NULL,1,1,1,80,'SID_AUTH_INFO','(DWORD) Protocol ID (0)\r\n(DWORD) Platform ID \r\n(DWORD) Product ID \r\n(DWORD) Version Byte\r\n(DWORD) Product language*\r\n(DWORD) Local IP for NAT compatibility*\r\n(DWORD) Time zone bias*\r\n(DWORD) Locale ID*\r\n(DWORD) Language ID*\r\n(STRING) Country abreviation \r\n(STRING) Country','Sends information about the Client to Battle.net.\r\n\r\n*These fields can be set to zero without breaking logon.\r\n\r\nProtocol ID:<dl><dd>Battle.net\'s current Protocol ID is 0.</dd></dl>\r\nPlatform ID:<dl><dd>A DWORD specifying the client\'s platform (IX86, PMAC, XMAC, etc).</dd></dl>\r\nProduct ID:<dl><dd>A DWORD specifying the client\'s game.</dd></dl>\r\nVersion:<dl><dd>The client\'s version byte.</dd></dl>\r\nProduct Language:<dl><dd>This field is under investigation. It can safely be set to 0.</dd></dl>\r\nMPQ Locale ID:<dl><dd>This field is part of Blizzards multi-lingual MPQ system. Is used to specify which version of an MPQ should be used when the MPQ is available in multiple languages.</dd></dl>\r\nLocal IP:<dl><dd>This is the local network IP of the client, in network byte order.</dd></dl>\r\nTimezone bias:<dl><dd>The difference, in minutes, between UTC and local time. The client calculates this value by subtracting the Local Time from the System Time, having converted both to Filetime structures, and then converting the resultant offset to minutes by diving it by 600,000,000. If you wish to understand the mechanism involved, read Microsoft\'s documentation on File times.</dd></dl>\r\nLanguage ID, Country Abbreviation, and Country:<dl><dd>These values can be retrieved by querying the system\'s locale information.</dd></dl>\r\nLanguage ID can be retrieved using the <a href=\"http://msdn2.microsoft.com/en-us/library/ms776265.aspx\">GetUserDefaultLangID</a> API.\r\nCountry Abbreviation & Country can be retrieved using the <a href=\"http://msdn2.microsoft.com/en-us/library/ms776270.aspx\">GetLocaleInfo</a> API to request the LOCALE_SABBREVCTRYNAME and LOCALE_SENGCOUNTRY, respectively.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (280,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,60,'SID_CHECKDATAFILE2','(DWORD) File size in bytes\r\n(DWORD) File hash [5]\r\n(STRING) Filename','Verifies that a file is authentic, by producing a hash of that file and sending it to the server for comparison to the original.\r\n\r\nThe hash is a standard SHA1 hash.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (281,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,73,'D2GS_WAYPOINT','(BYTE) Waypoint ID\r\n(BYTE) Unknown - Appears to be random\r\n(WORD) Unknown - 0x00\r\n(BYTE) Level number\r\n(WORD) Unknown - 0x00 \r\n','Requests to go to a waypoint if it was already activated.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (282,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,12,'D2GS_RIGHTSKILLONLOCATION','(WORD) X coordinate\r\n(WORD) Y coordinate \r\n','Uses the currently selected skill at the specified location. \r\n\r\nThis packet is sent when the location is first clicked with the mouse. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (283,NULL,'2011-02-04 21:57:07',0,0,NULL,1,7,1,13,'BNLS_CHOOSENLSREVISION','(DWORD) NLS revision number','This message instructs the server which revision of NLS you want to use.\r\n\r\nThe NLS revision number is given by Battle.net in SID_AUTH_INFO (0x50).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (284,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,35,'D2GS_ITEMTOBELT','(DWORD) Item ID\r\n(DWORD) Belt Location \r\n','Moves an item into the player\'s belt.\r\n\r\nExtra Info: The status of this information is: Public Belt locations start at 0x00 and go from right to left, starting at the lowest row and moving across, then going up one row. For example, location 0x03 would be at the bottom right hand corner and location 0x0C would be at the top left corner in a 4-slot belt. This can be calculated as (row*4+column).\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
