INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (286,NULL,'2012-05-10 04:34:12',0,0,NULL,1,1,2,29,'SID_LOGONCHALLENGEEX','(DWORD) UDP Token\r\n(DWORD) Server Token ','Informs the client of the UDP Token and the Server Token.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (287,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,18,'SID_LOCALEINFO','(FILETIME) System time\r\n(FILETIME) Local time\r\n(DWORD) Timezone bias\r\n(DWORD) SystemDefaultLCID\r\n(DWORD) UserDefaultLCID\r\n(DWORD) UserDefaultLangID\r\n(STRING) Abbreviated language name\r\n(STRING) Country name\r\n(STRING) Abbreviated country name\r\n(STRING) Country (English) ','Informs the server of the client\'s locale information. Much of this functionality has been incorporated into SID_AUTH_INFO, and more in-depth remarks can be found there. ');
