INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (289,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,125,'SID_CLANMEMBERLIST','(DWORD) Cookie','Requests a clan memberlist.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (290,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,65,'SID_QUERYADURL','(DWORD) Ad ID\r\n(STRING) Ad URL ','Reports the Adbanner\'s URL. This message will only be received if the client sent it to the server. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (291,NULL,'2010-10-07 17:36:25',0,0,NULL,1,1,1,8,'SID_STARTADVEX','(DWORD) State\r\n(DWORD) Time since creation\r\n(WORD) Game Type\r\n(WORD) Parameter\r\n(DWORD) Unknown (0x00)\r\n(DWORD) Unknown (Likely ladder, but will always be 0x00 because there is no SSHR ladder)\r\n(STRING) Game name\r\n(STRING) Game password\r\n(STRING) Game Statstring','Creates a game in a manner similar to SID_STARTADVEX2 and SID_STARTADVEX3. This is only used by Starcraft Shareware.\r\nSee documentation on SID_STARTADVEX3 for details on the values and their meanings.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (292,NULL,'2011-01-06 15:54:20',0,0,NULL,1,1,2,68,'SID_WARCRAFTGENERAL','(BYTE) Subcommand ID\r\n<dl>\r\n <dt>0x00 (WID_GAMESEARCH):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(DWORD) Status\r\n  <dl>\r\n   <dd>0x00000000: Search Started</dd>\r\n   <dd>0x00000004: Banned CD-Key</dd>\r\n  </dl>\r\n </dd>\r\n\r\n <dt>0x02 (WID_MAPLIST):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(BYTE) Number of responses</dd>\r\n <dd><dl>\r\n  <dt>For each reponse:</dt>\r\n  <dd>(DWORD) ID</dd>\r\n  <dd>(DWORD) Checksum</dd>\r\n  <dd>(WORD) Decompressed Length</dd>\r\n  <dd>(WORD) Compressed Length</dd>\r\n  <dd>(VOID) Compressed Data</dd>\r\n </dl></dd>\r\n <dd>(BYTE) Remaining Packets</dd>\r\n  \r\n <dt>0x03 (WID_CANCELSEARCH):</dt>\r\n <dd>(DWORD) Cookie from WID_GAMESEARCH</dd>\r\n\r\n <dt>0x04 (WID_USERRECORD):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(DWORD) Icon ID (based on identifier; for example, \"Orc Peon\" is \'W3O1\')</dd>\r\n <dd>(BYTE) Number of ladder records to read; this will be between 0 and 3.</dd>\r\n <dd><dl>\r\n  <dt>For each ladder record:</dt>\r\n  <dd>(DWORD) Ladder type; valid types are \'SOLO\', \'TEAM\', or \'FFA \' (where the last character of \'FFA \' is a space, 0x20).</dd>\r\n  <dd>(WORD) Number of wins</dd>\r\n  <dd>(WORD) Number of losses</dd>\r\n  <dd>(BYTE) Level</dd>\r\n  <dd>(BYTE) Unknown*</dd>\r\n  <dd>(WORD) Experience</dd>\r\n  <dd>(DWORD) Rank (will be 0 if unranked)</dd>\r\n </dl></dd>\r\n <dd>(BYTE) Number of race records to read; this will be 5 for WAR3 and 6 for W3XP.</dd>\r\n <dd><dl>\r\n  <dt>For each race record:</dt>\r\n  <dd>(WORD) Wins</dd>\r\n  <dd>(WORD) Losses</dd>\r\n </dl></dd>\r\n <dd>(BYTE) Number of team records to read.</dd>\r\n <dd><dl>\r\n  <dt>For each team record:</dt>\r\n  <dd>(DWORD) Type of team; valid types are \'2VS2\', \'3VS3\', and \'4VS4\'.</dd>\r\n  <dd>(WORD) Number of wins</dd>\r\n  <dd>(WORD) Number of losses</dd>\r\n  <dd>(BYTE) Level</dd>\r\n  <dd>(BYTE) Unknown*</dd>\r\n  <dd>(WORD) Experience</dd>\r\n  <dd>(DWORD) Rank (will be 0 if unranked)</dd>\r\n  <dd>(FILETIME) Time of last game played</dd>\r\n  <dd>(BYTE) Number of partners</dd>\r\n  <dd>(STRING)[] Names of partners</dd>\r\n </dl></dd>\r\n\r\n <dt>0x07 (WID_TOURNAMENT):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(Byte) Status\r\n  <dl>\r\n   <dd>0x00: No Tournament</dd>\r\n   <dd>0x01: Starting Soon</dd>\r\n   <dd>0x02: Ending Soon</dd>\r\n   <dd>0x03: Started</dd>\r\n   <dd>0x04: Last Call</dd>\r\n  </dl>\r\n </dd>\r\n <dd>(FILETIME) Last Status Update Time</dd>\r\n <dd>(WORD) Unknown</dd>\r\n <dd>(WORD) Unknown</dd>\r\n <dd>(BYTE) Wins</dd>\r\n <dd>(BYTE) Losses</dd>\r\n <dd>(BYTE) Draws</dd>\r\n <dd>(BYTE) Unknown</dd>\r\n <dd>(BYTE) Unknown</dd>\r\n <dd>(BYTE) Unknown</dd>\r\n <dd>(BYTE) Unknown</dd>\r\n\r\n <dt>0x08 (WID_CLANRECORD):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(BYTE) Number of ladder records to read; this will be between 0 and 4.</dd>\r\n <dd><dl>\r\n  <dt>For each ladder record:</dt>\r\n  <dd>(DWORD) Ladder type; valid types are \'CLNS\' (solo), \'CLN2\' (2v2), \'CLN3\' (3v3), or \'CLN4\' (4v4).</dd>\r\n  <dd>(DWORD) Number of wins</dd>\r\n  <dd>(DWORD) Number of losses</dd>\r\n  <dd>(BYTE) Level</dd>\r\n  <dd>(BYTE) Unknown*</dd>\r\n  <dd>(DWORD) Experience</dd>\r\n  <dd>(DWORD) Rank (will be 0 if unranked)</dd>\r\n </dl></dd>\r\n <dd>(BYTE) Number of race records to read; this will be 5 for WAR3 and 6 for W3XP.</dd>\r\n <dd><dl>\r\n  <dt>For each race record:</dt>\r\n  <dd>(DWORD) Wins</dd>\r\n  <dd>(DWORD) Losses</dd>\r\n </dl></dd>\r\n\r\n <dt>0x09 (WID_ICONLIST):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(DWORD) Selected Icon</dd>\r\n <dd>(BYTE) Tiers</dd>\r\n <dd>(BYTE) Count</dd>\r\n <dd><dl>\r\n  <dt>For each Icon:</dt>\r\n  <dd>(DWORD) Icon</dd>\r\n  <dd>(DWORD) Unit ID**</dd>\r\n  <dd>(BYTE) Race</dd>\r\n  <dd>(WORD) Wins required</dd>\r\n  <dd>(BYTE) Enabled</dd>\r\n </dl></dd>\r\n</dl>','This message is still being researched!\r\n\r\nThis message is used for multiple purposes on Warcraft III. Known and validated purposes are listed here.\r\n\r\n* The field was formerly labeled as \"Hours until XP decay\", but as no evidence to support this claim has ever been verified (and through personal testing has been found to be altogether false), it has been renamed as unknown for the time being. More information on this value would be appreciated.\r\n\r\n** This is the name of the unit for which this icon should be displayed for. The names can be found in the war3.mpq/Units/UnitData.slk.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (293,NULL,'2011-02-04 21:55:48',0,0,NULL,1,7,1,11,'BNLS_HASHDATA','(DWORD) Size of Data\r\n(DWORD) Flags\r\n(VOID) Data to be hashed\r\n\r\nOptional*:\r\n(DWORD) Client Key (Double Hash Only)\r\n(DWORD) Server Key (Double Hash Only)\r\n(DWORD) Cookie (Cookie Hash Only)','This message will calculate the hash of the given data.\r\nThe hashing algorithm used is the Battle.net standard hashing algorithm also known as \"Broken SHA-1\"\r\n\r\n*For clarity, the optional fields come after the VOID and not before.\r\n\r\nThe flags may be zero, or any bitwise combination of the following flags:\r\n\r\n0x01: Flags Unused\r\nThis flag has no effect.\r\n\r\n0x02: Double Hash\r\nIf this flag is present, the server will calculate a double hash.\r\nFirst it will calculate the hash of the data. Then it will prepend the client key and the server key to the resulting hash, and calculate the hash of the result. If this flag is present, the client key and server key DWORDs must be specified in the request after the data.\r\nThis may be used to calculate password hashes for the \"Old Logon System\".\r\n\r\n0x04: Cookie Hash\r\nIf this flag is present, a cookie DWORD is specified in the request.\r\nThis is an application-defined value that is echoed back to the client in the response.\r\n\r\nView consts: [pas cpp vb]');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (294,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,0,'PACKET_IDLE','[blank] \r\n','There is no response to this packet. You should send it once every 2-3 minutes.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (295,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,5,'MCP_GAMELIST','(WORD) Request ID\r\n(DWORD) Unknown (0)\r\n(STRING) Search String *','Requests a game listing.\r\n\r\n* Normally blank. If a non-empty string is sent, games will be returned that include this string in their names. This is not used by the client, but still exists.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (296,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,8,'D2GS_LEFTSKILLONLOCATIONEX','(WORD) X coordinate\r\n(WORD) Y coordinate \r\n','Uses Left skill on specified (X,Y) map coordinate.\r\n\r\nThis packet is sent repeatedly when the mouse button is held down after the initial packet has been sent. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (297,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,126,'SID_CLANMEMBERREMOVED','(STRING) Clan member name','Notifies the members of a clan that a user has been removed.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (298,NULL,'2012-06-01 21:12:30',0,0,NULL,1,1,1,46,'SID_GETLADDERDATA','(DWORD) Product ID\r\n(DWORD) League\r\n(DWORD) Sort method\r\n(DWORD) Starting rank\r\n(DWORD) Number of ranks to list ','Requests ladder listing.\r\n\r\nLeague is 1 for Ladder, 3 for Iron Man.\r\n\r\n<dl><dt>Sort methods:</dt>\r\n<dd>0x00: Highest Rating</dd>\r\n<dd>0x01: Fastest Climbers (Unused)</dd>\r\n<dd>0x02: Most Wins</dd>\r\n<dd>0x03: Most Games Played</dd></dl>\r\n\r\nRank is 0-based.\r\n\r\nMaximum number of ranks to list is 20.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (299,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,31,'D2GS_SETDWORDATTR','(BYTE)  Attribute - D2GS_SETWORDATTR\r\n(DWORD) Amount \r\n','Updates an attribute and sets it to the value sent, 4 bytes max.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (300,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,106,'D2GS_ENTERGAMEENVIRONMENT','blank \r\n','This byte should be sent in order to start receiving in-game messages and to interact with the world itself. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (301,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,116,'SID_CLANMAKECHIEFTAIN','(DWORD) Cookie\r\n(BYTE) Status','Notifies the sending client of the success/failure of its request.\r\nStatus:<dl>\r\n<dd>0x00: Success</dd>\r\n<dd>0x02: Cannot change until clan is a week old</dd>\r\n<dd>0x04: Declined</dd>\r\n<dd>0x05: Failed</dd>\r\n<dd>0x07: Not Authorized</dd>\r\n<dd>0x08: Not Allowed</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (303,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,20,'D2GS_OVERHEADMESSAGE','(WORD) Unknown - 0x00, 0x00\r\n(STRING) Message\r\n(BYTE) Unused - 0x00\r\n(WORD) Unknown - 0x00, 0x00\r\n','This message is used when you\'d like to put a message above a character\'s head as used in the client as \"![message]\".\r\n\r\nRestrictions: Total size of packet cannot be greater than 275 bytes. Message cannot be greater than 255 bytes. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented. \r\n\r\nExtra Info: The status of this information is: Public Colors can be created by adding hex FF 63 and a character 30 to 3C. \r\n\r\nExample:\r\nTo make red, use FF 63 31.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (304,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,118,'SID_CLANQUITNOTIFY','(BYTE) Status','The only known status code for this packet is 0x01 (Removed from clan).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (305,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,42,'SID_CREATEACCOUNT','(DWORD) [5] Hashed password\r\n(STRING) Username','Creates an account.\r\n\r\nUsernames longer than 15 characters are truncated, and the password is only hashed once (unlike SID_LOGONRESPONSE).\r\n\r\nThis packet is identical to SID_CREATEACCOUNT2, but the response is limited to success/fail. Developers who wish to provide a reason for account creation failure should use SID_CREATEACCOUNT2.\r\n\r\nCurrently, SID_CREATEACCOUNT2 may be used with any product, but the protocol-correct packet to use depends on the product you are emulating.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (306,NULL,'2011-02-04 22:02:54',0,0,NULL,1,7,1,2,'BNLS_LOGONCHALLENGE','(STRING) Account name\r\n(STRING) Password','This message will give you data you need for SID_AUTH_ACCOUNTLOGON (0x53). You must send this before you can send BNLS_LOGONPROOF (0x03).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (307,NULL,'2010-09-02 16:27:58',0,0,NULL,1,1,2,15,'SID_CHATEVENT','(DWORD) Event ID\r\n(DWORD) User\'s Flags\r\n(DWORD) Ping\r\n(DWORD) IP Address (Defunct)\r\n(DWORD) Account number (Defunct)\r\n(DWORD) Registration Authority (Defunct)\r\n(STRING) Username\r\n(STRING) Text *','Contains all chat events.\r\n\r\nFor STAR/SEXP/SSHR/JSTR, Text is UTF-8 encoded (WIDESTRING).\r\n\r\n* Text should be no longer than 255 characters. Game clients should only be able to send 224 characters.\r\n<dl><dt>Event IDs:</dt>\r\n<dd>[0x01] EID_SHOWUSER: User in channel</dd>\r\n<dd>[0x02] EID_JOIN: User joined channel</dd>\r\n<dd>[0x03] EID_LEAVE: User left channel</dd>\r\n<dd>[0x04] EID_WHISPER: Recieved whisper</dd>\r\n<dd>[0x05] EID_TALK: Chat text</dd>\r\n<dd>[0x06] EID_BROADCAST: Server broadcast</dd>\r\n<dd>[0x07] EID_CHANNEL: Channel information</dd>\r\n<dd>[0x09] EID_USERFLAGS: Flags update</dd>\r\n<dd>[0x0A] EID_WHISPERSENT: Sent whisper</dd>\r\n<dd>[0x0D] EID_CHANNELFULL: Channel full</dd>\r\n<dd>[0x0E] EID_CHANNELDOESNOTEXIST: Channel doesn\'t exist</dd>\r\n<dd>[0x0F] EID_CHANNELRESTRICTED: Channel is restricted</dd>\r\n<dd>[0x12] EID_INFO: Information</dd>\r\n<dd>[0x13] EID_ERROR: Error message</dd>\r\n<dd>[0x15] EID_IGNORE: Notifies that a user has been ignored (DEFUNCT)</dd>\r\n<dd>[0x16] EID_ACCEPT: Notifies that a user has been unignored (DEFUNCT)</dd>\r\n<dd>[0x17] EID_EMOTE: Emote</dd></dl>\r\n<dl><dt>EID_SHOWUSER:</dt>\r\n<dd>This is sent for each user who is already in a channel when you join it, as opposed to EID_JOIN, which is sent when a user joins a channel you have already joined. It is also sent when logged on using D2XP/D2DV and a user requires an update to their statstring - for example, by logging a different character onto a realm.</dd>\r\n<dl><dt>EID_JOIN:</dt>\r\n<dd>This is sent when a user enters the channel you are currently in.</dd>\r\n<dl><dt>EID_LEAVE:</dt>\r\n<dd>This is sent when a user exits the channel you are currently in.</dd>\r\n<dl><dt>EID_WHISPER:</dt>\r\n<dd>This is sent when a user whispers you.</dd>\r\n<dl><dt>EID_TALK:</dt>\r\n<dd>This is sent when a user (excluding self) in chat speaks.</dd>\r\n<dt>EID_BROADCAST:</dt>\r\n<dd>The username supplied for this event is now always \'Battle.net\'. Historically, username contained the name of the Battle.net Administrator who sent the broadcast.</dd>\r\n<dt>EID_CHANNEL:</dt>\r\n<dd>The flags field for this event is used and indicates what special conditions exist for the channel in question. See [Battle.net Flags] for more information.</dd>\r\n<dt>EID_USERFLAGS:</dt>\r\n<dd>This is sent to inform the client of an update to one or more user\'s flags. Battle.net usually sends this event for every user in the channel, even if only one user\'s flags have changed. This behavior can be exploited to detect invisible users, by performing an action (such as an unsquelch) to provoke a flags update. Users included in the flags update whose presence has not been indicated by EID_JOIN or EID_SHOWUSER can then be added to the userlist as invisible. Care should be taken, however, to account for the possibility that an asynchronous send error has occurred. Should an EID_JOIN or EID_SHOWUSER event occur for an invisible user, they should be marked as a normal user, not readded to the userlist.</dd>\r\n<dt>EID_WHISPERSENT:</dt>\r\n<dd>The Flags and Ping fields in this packet is equal to the originating user - the one who sent the whisper. In other words, EID_WHISPERSENT contains your flags & ping, not those of the person you whispered.</dd>\r\n<dl><dt>EID_CHANNELDOESNOTEXIST:</dt>\r\n<dd>See info on NoCreate Join in SID_JOINCHANNEL.</dd>\r\n<dl><dt>EID_CHANNELRESTRICTED:</dt>\r\n<dd>This is sent when attempting to join a channel which your client is not allowed to join.</dd>\r\n<dl><dt>EID_INFO:</dt>\r\n<dd>This is information supplied by Battle.net. This text is usually displayed by clients in yellow.</dd>\r\n<dl><dt>EID_Error:</dt>\r\n<dd>This is error information supplied by Battle.net. This text is usually displayed by clients in red.</dd>\r\n<dl><dt>EID_Emote:</dt>\r\n<dd>This is sent when any user (including self) uses the emote feature in chat.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (308,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,20,'MCP_CREATEQUEUE','(DWORD) Position','Position in line to create a game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (309,NULL,'2011-08-22 19:29:39',0,0,NULL,1,6,2,2,'PACKET_STATSUPDATE','(DWORD) Result \r\n(DWORD) Administrative access [optional]','Possible values for Result (boolean):\r\n0: Failed\r\n1: Succeeded\r\nIn server version 4, BotNet will always send Success.\r\n\r\n<b>Administrative access:</b>\r\nThis field is optional. It is only sent when the value has changed from the previous value.\r\n\r\nIt is a 32-bit mask specifying flags A-Z for the first 26 bits. Defined flags:\r\nA = superuser, can perform any administrative action\r\nB = broadcast, may use talk-to-all\r\nC = connection, may administer botnet connectivity\r\nD = database, may create and maintain databases\r\nI = ID control, may create and modify hub IDs\r\nS = botnet service');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (310,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,87,'SID_AUTH_ACCOUNTUPGRADE','(DWORD) Status\r\n(DWORD) Server Token \r\n','Status Codes:<dl><dd>0x00: Upgrade request accepted.</dd>\r\n<dd>Other: Upgrade request denied.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (311,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,0,'PACKET_IDLE','[blank] \r\n','BotNet sends this to the client when the connection has been idle. No response is needed.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (312,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,11,'PACKET_BOTNETCHAT','(DWORD) Command\r\n(DWORD) Action\r\n(DWORD) For Command 2, ID of destination bot - otherwise, ignored\r\n(STRING) Message ','Sends chat to Botnet as defined by the following fields:\r\n\r\nAllowed Command values:\r\n0: Message to all bots\r\n1: Message to bots on current database\r\n2: Message to bot specified by ID. Messages to a non-existant ID are silently ignored.\r\n\r\nAllowed Action values:\r\n0: Talk\r\n1: Emote');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (313,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,96,'SID_GAMEPLAYERSEARCH','[blank]','This message requests a list of players for an arranged team game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (314,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,41,'SID_LOGONRESPONSE','(DWORD) Result ','Reports success/fail on password authentication.\r\n<dl><dt>Result:</dt>\r\n<dd>0x00: Invalid password</dd>\r\n<dd>0x01: Success</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (315,NULL,'2011-02-04 22:38:16',0,0,NULL,1,7,1,19,'BNLS_SERVERLOGONCHALLENGE','(DWORD) Slot index\r\n(DWORD) NLS revision number\r\n(DWORD) [16] Data from account database\r\n(DWORD) [8] Data client\'s SID_AUTH_ACCOUNTLOGON (0x53) request','This message initializes a new logon checking session and calculates the values needed for the server\'s reply to SID_AUTH_ACCOUNTLOGON (0x53).\r\nBNLS_RESERVESERVERSLOTS(0x12) must be sent before this message to reserve slots for logon checking sessions.\r\n\r\nBoth the slot indicies and the NLS revision number follow their respective conventions covered in this documentation. The account database data is first received from the client\'s SID_AUTH_ACCOUNTCREATE (0x04) message. This information must be stored by the server\'s account database for logon checking. If the account database data is invalid, then the logon checking session will not succeed.\r\nThis message initializes a slot with all the information required for it to operate, including the NLS revision. Although BNLS supports switching the NLS revision of a given slot, it can respond to requests slightly faster if the same NLS revision is used for the same slots in a given connection.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (316,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,19,'MCP_CANCELGAMECREATE','[blank]','Notifies the server that you want to cancel the creation of your game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (317,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,26,'D2GS_ITEMTOBODY','(DWORD) Item ID\r\n(DWORD) Body Location \r\n','Moves item from player\'s cursor buffer to body location. Body locations are identified by the line number in the data file bodylocs.txt.\r\n\r\nPossible Body Locations:\r\n0x01 - Helm\r\n0x02 - Amulet\r\n0x03 - Armor\r\n0x04 - Left-hand weapon\r\n0x05 - Right-hand weapon\r\n0x06 - Left-hand ring\r\n0x07 - Right-hand ring\r\n0x08 - Belt\r\n0x09 - Boots\r\n0x0A - Gloves\r\n\r\nThese values have been recorded for mercenary body locations, but aren\'t confirmed: (Note, each location ID is prefixed with 0x61)\r\nExample: 1A 64 00 00 00 61 02 00 00 (Move item 0x64 to Mercenary Right-hand weapon)\r\n\r\n0x01 - Mercenary Helm\r\n0x02 - Mercenary Right-hand Weapon\r\n0x03 - Mercenary Armor\r\n0x04 - Mercenary Left-hand Weapon \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (318,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,60,'SID_CHECKDATAFILE2','(DWORD) Result','Result of file integrity query.\r\n<dl><dt>Result:</dt>\r\n<dd>0x00: Not approved</dd>\r\n<dd>0x01: Blizzard approved</dd>\r\n<dd>0x02: Approved for ladder</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (319,NULL,'2011-06-25 04:25:36',0,0,NULL,1,1,1,114,'SID_CLANCREATIONINVITATION','(DWORD) Cookie\r\n(DWORD) Clan tag\r\n(STRING) Inviter name\r\n(BYTE) Status','This message is used to reply to an invitation to create a new clan.\r\n\r\nYou must reply to this packet using one of two statuses:\r\n<dl>\r\n<dd>0x04: Declined</dd>\r\n<dd>0x06: Accepted</dd>\r\n</dl>\r\nIf you do not reply after about 30 seconds (give or take), the server assumes declined.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (320,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,1,1,'MCP_STARTUP','(DWORD) MCP Cookie\r\n(DWORD) MCP Status\r\n(DWORD) [2] MCP Chunk 1\r\n(DWORD) [12] MCP Chunk 2\r\n(STRING) Battle.net Unique Name','This packet authenticates the client with the MCP and allows character querying and logon to proceed.\r\nAll 16 DWORDs (Cookie, Status, Chunk 1, and Chunk 2) are received from the server via SID_LOGONREALMEX.\r\n\r\nNot much information is known about the DWORD values, other than that they\'re received from the server. The following information needs work:\r\n<dl>\r\n <dd>MCP Cookie: Client Token</dd>\r\n <dd>MCP Status: Unknown</dd>\r\n <dd>MCP Chunk 1 [01]: Server IP (BNCS)</dd>\r\n <dd>MCP Chunk 1 [02]: UDP Value *</dd>\r\n <dd>MCP Chunk 2 [01]: Unknown</dd>\r\n <dd>MCP Chunk 2 [02]: Unknown</dd>\r\n <dd>MCP Chunk 2 [03]: Something to do with the gateway</dd>\r\n <dd>MCP Chunk 2 [04]: Product (D2DV/D2XP)</dd>\r\n <dd>MCP Chunk 2 [05]: Platform (IX86/PMAC/XMAC)</dd>\r\n <dd>MCP Chunk 2 [06]: Unknown</dd>\r\n <dd>MCP Chunk 2 [07]: Language ID (1033 [0x409] for enUS)</dd>\r\n <dd>MCP Chunk 2 [08]: Unknown</dd>\r\n <dd>MCP Chunk 2 [09]: Unknown</dd>\r\n <dd>MCP Chunk 2 [10]: Unknown</dd>\r\n <dd>MCP Chunk 2 [11]: Unknown</dd>\r\n <dd>MCP Chunk 2 [12]: Unknown</dd>\r\n</dl>\r\n\r\nThis is purely speculation, but as there are 5 unknown DWORDs at the end of this chunk, it is possible that it is actually a hash of something.\r\n\r\n* UDPValue: No one really knows what this is, however, it is used in 2nd DWORD of the UDP packet PKT_CONNTEST2. The client receives it in SID_AUTH_INFO.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (321,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,124,'SID_CLANMOTD','(DWORD) Cookie\r\n(DWORD) Unknown (0)\r\n(STRING) MOTD','This message contains the clan\'s Message of the Day.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (322,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,64,'SID_QUERYREALMS2','[blank]','Requests a realm listing.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (323,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,83,'SID_AUTH_ACCOUNTLOGON','(BYTE) [32] Client Key (\'A\')\r\n(STRING) Username \r\n','This message is sent to the server to initiate a logon. It consists of the client\'s public key and their UserName.\r\n\r\nThe client\'s public key is a value calculated by the client and used for a single logon. For more information, see [NLS/SRP Protocol].');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (324,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,52,'SID_QUERYREALMS','(DWORD) Unknown\r\n(DWORD) Count\r\n<dl><dt>For Each Realm:</dt>\r\n<dd>(DWORD) Unknown</dd>\r\n<dd>(STRING) Realm title</dd>\r\n<dd>(STRING) Realm description</dd></dl>','Realm list.\r\n\r\nThe first Unknown is usually 0. The second [and subsequent] Unknown is usually 1.\r\n\r\nThis packet is no longer used. SID_QUERYREALMS2 is used instead.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (325,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,18,'MCP_MOTD','(BYTE) Unknown\r\n(STRING) MOTD','Contains the realm\'s MOTD. The Byte at the beginning is apparently ignored.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (326,NULL,'2011-02-04 22:08:19',0,0,NULL,1,7,1,6,'BNLS_CHANGEPROOF','(DWORD) [16] Data from SID_AUTH_ACCOUNTCHANGE (0x55)','This message will parse data from SID_AUTH_ACCOUNTCHANGE (0x55) and will reply with data to send in SID_AUTH_ACCOUNTCHANGEPROOF (0x56).\r\n\r\nYou must send BNLS_CHANGECHALLENGE (0x05) before you can send this.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (327,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,19,'D2GS_INTERACTWITHENTITY','(DWORD) Entity Type\r\n(DWORD) Entity ID \r\n\r\nEntity types\r\n--------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances','Interacts with the specified Entity. \r\nFor players and npc\'s, it will send a request to interact. \r\nThe interaction depends on the type of the unit or object. \r\nFor others it will trigger the object, for example using a shrine, looting a corpse you have permission to loot, or opening and closing a door.\r\n\r\nThis packet is always followed by other\'s that relate to the type of interaction.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (328,NULL,'2012-02-26 04:26:54',0,0,NULL,2,2,3,3,'PKT_CLIENTREQ','(DWORD) Code ','This message is used to determine the latency to a game on Battle.net.\r\n\r\nIf code is zero, then the message is a ping request from another client (and this client should respond with a PKT_CLIENTREQ using a non-zero code). Otherwise, the message is a ping response. \r\n\r\nIn previous versions of UDP enabled clients they would use GetTickCount() as the return value (the recieving end would then do math on that value to get the latency).\r\nClients now use 0x01 for the value and do all the math locally, this resolves time zone issues and other stuffs.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (329,NULL,'2011-02-04 22:03:36',0,0,NULL,1,7,2,2,'BNLS_LOGONCHALLENGE','(DWORD)[8] Data for SID_AUTH_ACCOUNTLOGON','Contains data for SID_AUTH_ACCOUNTLOGON (0x53).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (330,NULL,'2012-02-26 02:11:20',0,0,NULL,1,1,2,84,'SID_AUTH_ACCOUNTLOGONPROOF','(DWORD) Status\r\n(BYTE) [20] Server Password Proof (M2)\r\n(STRING) Additional information','Statuses:<dl>\r\n<dd>0x00: Logon successful.</dd>\r\n<dd>0x02: Incorrect password.</dd>\r\n<dd>0x06: Account closed.</dd>\r\n<dd>0x0E: An email address should be registered for this account.</dd>\r\n<dd>0x0F: Custom error. A string at the end of this message contains the error.</dd>\r\n</dl>\r\n\r\nThis message confirms the validity of the client password proof and supplies the server password proof. See [NLS/SRP Protocol] for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (331,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,63,'SID_STARTVERSIONING2','(FILETIME) MPQ Filetime\r\n(STRING) MPQ Filename\r\n(STRING) ValueString','Contains CheckRevision values.\r\n\r\nHas anyone seen this packet in use? It\'s currently being marked as defunct, as I don\'t know of any clients that receive it.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (332,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,89,'SID_SETEMAIL','(STRING) Email Address','Binds an email address to your account.\r\n\r\nSending this message is optional. However, you should only send it when either you receive SID_SETEMAIL from the server or you receive status 0x0E from SID_AUTH_ACCOUNTLOGONPROOF.\r\n\r\nThis packet used to be named SID_AUTH_RECONNECT, however Blizzard never had it implemented, and so reused the packet ID for their email system.');
