INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (335,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,16,'PACKET_CHATDROPOPTIONS','(BYTE) Subcommand\r\n\r\nFor subcommand 0:\r\n(BYTE) Setting for broadcast\r\n(BYTE) Setting for database\r\n(BYTE) Setting for whispers\r\n(BYTE) Refuse all whispers \r\n','This message is used to either notify or set your chat drop options. The server may notify you of your chat drop options by sending subcommand 0 without the four trailing value bytes.\r\n\r\nPossible values:\r\n0: Allow all chat to be received (Default)\r\n1: Refuse chat from users not on an account\r\n2: Refuse all chat');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (336,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,32,'D2GS_USEITEM','(DWORD) Item ID\r\n(DWORD) X coordinate\r\n(DWORD) Y coordinate \r\n','Uses the specified item (such as a potion, town portal scroll, etc.).\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (337,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,7,'MCP_CHARLOGON','(DWORD) Result','Logon response.\r\n<dl>\r\n<dt>Known values:</dt>\r\n<dd>0x00: Success</dd>\r\n<dd>0x46: Player not found</dd>\r\n<dd>0x7A: Logon failed</dd>\r\n<dd>0x7B: Character expired</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (338,NULL,'2011-06-06 01:49:02',0,0,NULL,1,1,1,49,'SID_CHANGEPASSWORD','(DWORD) Client Token\r\n(DWORD) Server Token\r\n(DWORD) [5] Old password hash\r\n(DWORD) [5] New password hash\r\n(STRING) Account name ','Changes Battle.net account password. This message must be sent before you logon.\r\n\r\nPasswords should be converted to lower case before hashing.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (339,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,16,'SID_LEAVECHAT','[blank] \r\n','Leaves chat mode but does not disconnect. Generally sent when entering a game. This is also sent by D2DV/D2XP when switching characters, and by all products when logging off.\r\n\r\nAfter you send this, normal chat functions to not work. In order to enter a channel when you are not in chat mode, you must send SID_JOINCHANNEL.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (340,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,31,'SID_LEAVEGAME','[blank] ','Notifies Battle.net that you have left a game. \r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (341,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,50,'SID_CHECKDATAFILE','(DWORD) [5] File checksum\r\n(STRING) File name','This message was used to check a digest of a game file. This message is no longer used; developers should use the SID_CHECKDATAFILE2 message.\r\n\r\nThe digest is created by using the broken SHA-1 hash on the first 64-bytes of the (filesize % 64) times. This due to a bug in the client. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (342,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,24,'MCP_CHARUPGRADE','(DWORD) Result','Converts a non-expansion character to expansion.\r\n<dl>\r\n<dt>Known return values:</dt>\r\n<dd>0x00: Success</dd>\r\n<dd>0x46: Character not found</dd>\r\n<dd>0x7A: Upgrade failed</dd>\r\n<dd>0x7B: Character is expired</dd>\r\n<dd>0x7C: Already expansion character</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (343,NULL,'2011-09-27 02:58:07',0,0,NULL,1,7,1,12,'BNLS_CDKEY_EX','(DWORD) Cookie\r\n(BYTE) Number of CD-keys to encrypt\r\n(DWORD) Flags\r\n(DWORD) [] Server session key(s), depending on flags\r\n(DWORD) [] Client session key(s), depending on flags\r\n(STRING) [] CD-keys. No dashes or spaces','The Cookie has no special meaning to the server and will simply be echoed to the client in the response. The client can use multiple types of CD-keys in the same packet. The number of CD Keys to encrypt should be between 1 and 32.\r\n\r\nThe flags may be zero, or any bitwise combination of the defined flags.\r\nCurrently, the following flags are defined:\r\n\r\n0x01: CDKEY_SAME_SESSION_KEY\r\n0x02: CDKEY_GIVEN_SESSION_KEY\r\n0x04: CDKEY_MULTI_SERVER_SESSION_KEYS\r\n0x08: CDKEY_OLD_STYLE_RESPONSES\r\n\r\nView consts: [pas cpp vb]\r\n\r\nCDKEY_SAME_SESSION_KEY (0x01):\r\nThis flag specifies that all the returned CD-keys will use the same client session key. When used in combination with CDKEY_GIVEN_SESSION_KEY (0x02), a single client session key is specified immediately after the server session key(s). When used without CDKEY_GIVEN_SESSION_KEY (0x02), a client session key is not sent in the request, and the server will create one.\r\nWhen not used, each CD-key gets its own client session key. This flag has no effect if the amount of CD-keys to encrypt is 1.\r\n\r\nCDKEY_GIVEN_SESSION_KEY (0x02):\r\nThis flag specifies that the client session keys to be used are specified in the request. When used in combination with CDKEY_SAME_SESSION_KEY (0x01), a single client session key is specified immediately after the server session key(s). When used without CDKEY_SAME_SESSION_KEY (0x01), an array of client session keys (as many as the amount of CD-keys) is specified. When not used, client session keys are not included in the request.\r\n\r\nCDKEY_MULTI_SERVER_SESSION_KEYS (0x04):\r\nThis flag specifies that each CD-key has its own server session key. When specified, an array of server session keys (as many as the amount of CD-keys) is specified. When not specified, a single server session key is specified.\r\nThis flag has no effect if the amount of CD-keys to encrypt is 1.\r\n\r\nCDKEY_OLD_STYLE_RESPONSES (0x08):\r\nSpecifies that the response to this packet is a number of BNLS_CDKEY (0x01) responses, instead of a BNLS_CDKEY_EX (0x0c) response. The responses are guaranteed to be in the order of the CD-keys\' appearance in the request. Note that when this flag is specified, the Cookie cannot be echoed. (It must still be included in the request.)\r\n\r\n\r\n\r\nNote: When using Lord of Destruction, two CD-keys are encrypted, and they must share the same client session key. There are several ways to do this. One way is to provide both CD-keys in this packet, using the flag CDKEY_SAME_SESSION_KEY (0x01). Another way is to use BNLS_CDKEY (0x01) to encrypt the first CD-key, then use this packet with the flag CDKEY_GIVEN_SESSION_KEY (0x02) to encrypt the second CD-key with the same client session key.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (344,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,5,'SID_CLIENTID','(DWORD) Registration Version\r\n(DWORD) Registration Authority\r\n(DWORD) Account Number\r\n(DWORD) Registration Token ','The following information is historical:\r\n\r\nThis message used to be used to issue new values to the client for the above fields. New values were issued when the values supplied in SID_CLIENTID were found to be invalid.\r\n\r\nSince these fields are no longer used, the server now sets them to zero.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (345,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,40,'D2GS_INSERTSOCKETITEM','(DWORD) Item ID - Item to place in socket\r\n(DWORD) Item ID - Socketed item \r\n','Inserts the specified item into a socketed item.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (346,NULL,'2010-07-15 02:47:39',0,1,'2014-05-24 20:27:10',1,1,2,25,'SID_MESSAGEBOX','(DWORD) Style\r\n(STRING) Text\r\n(STRING) Caption','Displays a message to the user. This message\'s fields are used as parameters for the Win32 API <a href=\"http://msdn.microsoft.com/en-us/library/windows/desktop/ms645505(v=vs.85).aspx\">MessageBox</a>, and can be passed directly to it. For more information about these parameters, see the documentation for <a href=\"http://msdn.microsoft.com/en-us/library/windows/desktop/ms645505(v=vs.85).aspx\">MessageBox</a> at MSDN.\r\n\r\nSome more research on the behavior of this packet in Starcraft Broodwar has been done by HarpyWar and xboi209. Their documentation can be found on <a href=\"https://github.com/HarpyWar/pvpgn/issues/15#issuecomment-44092744\">GitHub</a>. Specifically, their research shows how to display a small and large MessageBox inside the client. A large MessageBox also shows the Caption parameter, whereas a small MessageBox does not.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (347,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,7,'SID_REPORTVERSION','(DWORD) Platform ID\r\n(DWORD) Product ID\r\n(DWORD) Version Byte\r\n(DWORD) EXE Version\r\n(DWORD) EXE Hash\r\n(STRING) EXE Information ','Contains CheckRevision response, version & EXE info.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (348,NULL,'2010-08-06 01:38:27',0,0,NULL,1,7,2,7,'BNLS_UPGRADECHALLENGE','(BOOLEAN) Success code','If the success code is TRUE, you may send SID_AUTH_ACCOUNTUPGRADE (0x57).\r\n\r\nCurrently, no error conditions are defined, so this is always TRUE.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (349,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,69,'SID_NETGAMEPORT','(WORD) Port','Sets the port used by the client for hosting WAR3/W3XP games. This value is retreived from HKCU\\Software\\Blizzard Entertainment\\Warcraft III\\Gameplay\\netgameport, and is sent after the user logs on.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (350,NULL,'2010-07-15 02:47:39',0,0,NULL,1,7,2,3,'BNLS_LOGONPROOF','(DWORD)[5] Data for SID_AUTH_ACCOUNTLOGONPROOF','Data for SID_AUTH_ACCOUNTLOGONPROOF (0x54).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (351,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,3,'MCP_CREATEGAME','(WORD) Request Id\r\n(WORD) Game token\r\n(WORD) Unknown (0)\r\n(DWORD) Result','<dl>\r\n<dt>Result:</dt>\r\n<dd>0x00: Game creation succeeded. This does NOT automatically join the game - the client must also send packet MCP_JOINGAME.</dd>\r\n<dd>0x1E: Invalid game name.</dd>\r\n<dd>0x1F: Game already exists.</dd>\r\n<dd>0x20: Game servers are down.</dd>\r\n<dd>0x6E: A dead hardcore character cannot create games.</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (352,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,109,'D2GS_PING','(DWORD) Tick Count\r\n(DWORD) Null\r\n(DWORD) Null \r\n','This packet should be sent every five to seven seconds to avoid timeout.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (353,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,24,'D2GS_ITEMTOBUFFER','(DWORD) Item ID\r\n(DWORD) X coordinate\r\n(DWORD) Y coordinate\r\n(DWORD) Buffer Type\r\n\r\nPossible Buffer Types:\r\n0x00 - Character\'s inventory (backpack)\r\n0x01 - NPC and other Trade Windows\r\n0x02 - Trade window\r\n0x03 - Horadric Cube\r\n0x04 - Stash ','Moves item from the player\'s cursor buffer to an inventory space.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented. \r\n\r\nInventory coordinates:\r\n(0,0) - Top left\r\n(9,3) - Bottom right');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (354,NULL,'2013-06-22 19:03:53',0,0,NULL,1,3,2,17,'MCP_REQUESTLADDERDATA','(BYTE) Ladder type\r\n<dl>\r\n<dt>The 10-byte header:</dt>\r\n<dd>(WORD) Total response size</dd>\r\n<dd>(WORD) Current message size</dd>\r\n<dd>(WORD) Total size of unreceived messages</dd>\r\n<dd>(WORD) Rank of first entry</dd>\r\n<dd>(WORD) Unknown (0)</dd>\r\n<dt>Message data:</dt>\r\n<dd>(DWORD)Number of entries</dd>\r\n<dd>(DWORD)Unknown (0x10)</dd>\r\n<dt>For each entry:</dt>\r\n<dd>(QWORD) Character experience</dd>\r\n<dd>(BYTE) Character Flags</dd>\r\n<dd>(BYTE) Character title</dd>\r\n<dd>(WORD) Character level</dd>\r\n<dd>(BYTE) [16] Character name</dd>','<dl>\r\n<dt>Total Response Size:</dt>\r\n<dd>The size of the entire batch of SID_REQUESTLADDERDATA messages, excluding their headers and first bytes.</dd>\r\n<dt>Current Message Size:</dt>\r\n<dd>The size of the current message, excluding its header and first byte.</dd>\r\n<dt>Total size of unreceived messages:</dt>\r\n<dd>The total size of all the unreceived messages in the batch, excluding their headers and first bytes. In the last packet, this value is 0, since there are no unreceived messages.</dd>\r\n<dt>Rank of first entry:</dt>\r\n<dd>Always zero, except in the last message. In the last message, this specifies the zero-based rank of the first entry. (For example if this is 17 in the last packet, then ladder entries 18-33 were retrieved.)</dd>\r\n<dt>Character Flags (AND 0x07 to get character):</dt>\r\n<dd>0x00: Amazon</dd>\r\n<dd>0x01: Sorceress</dd>\r\n<dd>0x02: Necromancer</dd>\r\n<dd>0x03: Paladin</dd>\r\n<dd>0x04: Barbarian</dd>\r\n<dd>0x05: Druid</dd>\r\n<dd>0x06: Assassin</dd>\r\n<dd>\r\n<dl>\r\n<dd>0x08: highlight this (if response to <a href=\'http://bnetdocs.org/?op=packet&pid=481\' target=\'_blank\'>C > S [0x16] MCP_CHARRANK</a></dd>\r\n<dd>0x10: dead character (hardcore only)</dd>\r\n<dd>0x20: hardcore character</dd>\r\n<dd>0x40: expansion character</dd>\r\n</dl>\r\n</dd>\r\n<dt>Character Name:</dt>\r\n<dd>The character name is always 16 bytes. If the name is shorter than 16 bytes, the string is padded with nulls. The last byte is always null, since character names are limited to 15 chars.</dd>\r\n<dt>General Remarks:</dt>\r\n<dd>The server may respond to this packet with one or more of these messages. The client must not handle the data until the last packet in the batch is received.</dd>\r\n<dd>The messages\' data should be concatenated backwards. For example, if 3 packets were received, then the data buffer should contain the data of the 3rd packet, followed by the data of the 2nd packet, followed by the data of the 1st packet. Only after the last packet was received, the data buffer should be parsed.</dd>\r\n<dd><b>Important note:</b> If the entry is the last entry in the packet, the character name might be smaller than 16 bytes.In this case, the client MUST add null bytes to the end of the packet, BEFORE adding it to the data buffer.</dd>\r\n<dd>Ladder packets are NOT SENT IN ANY ORDER - They are often sent completely out of order and must be placed back into the proper order. You have to infer the sequencing based on the \'how big\' fields in the 10 byte header.</dd>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (355,NULL,'2011-06-19 19:11:56',0,0,NULL,1,7,2,18,'BNLS_RESERVESERVERSLOTS','(DWORD) Number of slots reserved','This may be equal to the number of slots requested, although it does not necessarily have to be the same value.\r\n\r\nValid slot indicies are in the range of [0, Slots reserved - 1]. Each slot stores state information about a NLS checking operation.\r\n\r\nA logon checking session must be finished on the same slot on which it was started. If a logon checking session is abandoned before it is completed, no special action is required. Starting a new logon checking session on a slot overwrites all previous state information. A logon checking session cannot be resumed if the connection to BNLS is interrupted before it is completed.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (356,NULL,'2010-07-15 02:47:39',0,0,NULL,1,7,1,0,'BNLS_NULL','[blank] \r\n','This message is empty and may be used to keep the connection alive. The client is not required to send this. There is no response from the server.\r\n\r\nIf the connection to the BNLS server is idle for more than one minute, the BNLS server will disconnect the client. To avoid that, use this message.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (357,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,8,'PACKET_COMMAND','(DWORD) Target BotID\r\n(STRING) Sending User\r\n(STRING) Command \r\n','This packet is used to send a command to a specific client on the Botnet. It results in PACKET_MESSAGE (0x04) being sent to the specified client. \r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (358,NULL,'2010-12-26 19:51:11',0,0,NULL,1,1,1,38,'SID_READUSERDATA','(DWORD) Number of Accounts\r\n(DWORD) Number of Keys\r\n(DWORD) Request ID\r\n(STRING)[] Requested Accounts\r\n(STRING)[] Requested Keys','Requests an extended profile.\r\n\r\nPlease note that if you provide more than one account, Battle.net will return your request as if you sent it with 0 accounts and 0 keys, indicating that no data is present other than the 3 DWORDs seen here. This means you cannot use this packet for multiple users all at once; you must submit this packet once for each user you wish to know about. This only applies to official Battle.net servers, it might not apply to PvPGN servers or equivalent software.\r\n\r\nAlso, requesting 0x20 (32) or more keys at one time makes Battle.net kick you (it does not ban you, however).\r\n\r\n<dl>\r\n<dt>Profile Keys: *</dt>\r\n<dt><b>User Profiles:</b></dt>\r\n<dd>profile\\sex **</dd>\r\n<dd>profile\\age  </dd>\r\n<dd>profile\\location</dd>\r\n<dd>profile\\description</dd>\r\n\r\n<dt><b>Account Info: ***</b></dt>\r\n<dd>System\\Account Created</dd>\r\n<dd>System\\AuthLevel</dd>\r\n<dd>System\\Flags</dd>\r\n<dd>System\\Friends</dd>\r\n<dd>System\\Icon</dd>\r\n<dd>System\\IP</dd>\r\n<dd>System\\Last Logoff</dd>\r\n<dd>System\\Last Logon</dd>\r\n<dd>System\\League</dd>\r\n<dd>System\\MFT Bytes</dd>\r\n<dd>System\\MFT Last Write</dd>\r\n<dd>System\\MFT Time Logged</dd>\r\n<dd>System\\Port</dd>\r\n<dd>System\\Time Logged</dd>\r\n<dd>System\\Username</dd>\r\n\r\n<dt><b>Normal Games:</b></dt>\r\n<dd>record\\GAME\\0\\wins</dd>\r\n<dd>record\\GAME\\0\\losses</dd>\r\n<dd>record\\GAME\\0\\disconnects</dd>\r\n<dd>record\\GAME\\0\\last game</dd>\r\n<dd>record\\GAME\\0\\last game result</dd>\r\n\r\n<dt><b>Ladder Games:</b></dt>\r\n<dd>record\\GAME\\1\\wins</dd>\r\n<dd>record\\GAME\\1\\losses</dd>\r\n<dd>record\\GAME\\1\\disconnects</dd>\r\n<dd>record\\GAME\\1\\rating</dd>\r\n<dd>record\\GAME\\1\\high rating</dd>\r\n<dd>DynKey\\GAME\\1\\rank</dd>\r\n<dd>record\\GAME\\1\\high rank</dd>\r\n<dd>record\\GAME\\1\\last game</dd>\r\n<dd>record\\GAME\\1\\last game result</dd>\r\n\r\n<dt><b>IronMan Ladder Games: </b></dt>\r\n<dd>record\\GAME\\3\\wins</dd>\r\n<dd>record\\GAME\\3\\losses</dd>\r\n<dd>record\\GAME\\3\\disconnects</dd>\r\n<dd>record\\GAME\\3\\last game</dd>\r\n<dd>record\\GAME\\3\\last game result</dd>\r\n<dd>record\\GAME\\3\\rating</dd>\r\n<dd>record\\GAME\\3\\high rating</dd>\r\n<dd>DynKey\\GAME\\3\\rank</dd>\r\n<dt>* This list is not complete, and could use adding to.</dt>\r\n<dt>** This field is defunct in STAR/SEXP/WAR3/W3XP.</dt>\r\n<dt>  This field is defunct.</dt>\r\n<dt>*** Battle.net returns blank values for anyone but yourself.</dt>\r\n<dt> W2BN only.</dt>\r\n<dt>Note that any profile\\ key can contain an 0x09 (tab) character.</dt>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (359,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,54,'SID_CDKEY2','(DWORD) Spawn (0/1)\r\n(DWORD) Key Length\r\n(DWORD) CDKey Product\r\n(DWORD) CDKey Value1\r\n(DWORD) Server Token\r\n(DWORD) Client Token\r\n(DWORD) [5] Hashed Data\r\n(STRING) Key owner','This packet is an updated version of SID_CDKEY (0x30), designed to prevent CDKeys from being stolen, by sending them hashed instead of plain-text.\r\n\r\nThe data that should be hashed is:<ol><li>Client Token</li><li>Server Token</li><li>Key Product (from decoded CD key)</li><li>Key Value1 (from decoded CD key)</li><li>Key Value2 (from decoded CD key)</li></ol>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (360,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,14,'SID_CHATCOMMAND','(STRING) Text *','Send text or a command to Battle.net using this packet.\r\n\r\nFor STAR/SEXP/SSHR/JSTR, Text is UTF-8 encoded (WIDESTRING).\r\n\r\nIt is generally accepted as unwise to send any character below a space (0x20): this includes line feeds, carriage returns & control characters. The maximum number of characters is 224 per message including the null-terminator (so really only 223 characters), any longer and it becomes trimmed by Battle.net.\r\n\r\nIf you send a line feed and/or a carriage return, Battle.net disconnects you and IP bans you for 5 minutes.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (361,NULL,'2011-09-30 08:04:20',0,0,NULL,1,7,1,9,'BNLS_VERSIONCHECK','(DWORD) Product ID\r\n(DWORD) Version DLL digit\r\n(STRING) Checksum formula.','This message will request a fast version check. Now works with all products.\r\n\r\nVersion DLL Digit must be in the range 0-7. (For example, for IX86Ver1.mpq this is 1)\r\n\r\nValid ProductIDs are:\r\n\r\n0x01: Starcraft\r\n0x02: Starcraft: Broodwar\r\n0x03: Warcraft II: BNE\r\n0x04: Diablo II\r\n0x05: Diablo II: LoD\r\n0x06: Starcraft: Japanese\r\n0x07: Warcraft III\r\n0x08: Warcraft III: The Frozen Throne\r\n\r\nIf you are not using BNLS, and are instead using JBLS or something equivalent, there are also:\r\n\r\n0x09: Diablo Retail\r\n0x0A: Diablo Shareware\r\n0x0B: Starcraft Shareware\r\n\r\nView consts: [pas cpp vb]');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (362,NULL,'2011-09-27 02:58:20',0,0,NULL,1,7,2,12,'BNLS_CDKEY_EX','(DWORD) Cookie\r\n(BYTE) Number of CD-keys requested\r\n(BYTE) Number of successfully encrypted CD-keys\r\n(DWORD) Bit mask\r\n\r\nFor each successful CD Key:\r\n(DWORD) Client session key\r\n(DWORD) [9] CD-key data','When the flags do not contain CDKEY_OLD_STYLE_RESPONSES (0x08), the response is this message.\r\n\r\nCookie is the same as the value sent to the server in the request.\r\n\r\nThe bit mask field contains the success code for each CD-key. Each bit of the 32 bits in this DWORD is 1 for success or 0 for failure. The least significant bit specifies the success code of the first CD-key provided. Bits that exceed the amount of CD-keys provided are set to 0');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (363,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,11,'SID_GETCHANNELLIST','(STRING) [] Channel names, each terminated by a null string.','Contains a list of available channels.\r\n\r\nFor some reason, Diablo II adds extra data to the channel list (as can be seen in game), and older clients list blocked channels, such as Warez and Backstage.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (364,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,33,'D2GS_STACKITEM','(DWORD) Item ID - Stack item\r\n(DWORD) Item ID - Target item \r\n','Stacks one item such as a key onto another item.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (365,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,2,'D2GS_WALKTOENTITY','(DWORD) *Entity Type\r\n(DWORD) Entity ID\r\n\r\n*Entity Types\r\n  -------------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n\r\n','Makes your character walk to the Entity specified in Entity ID.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (366,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,13,'PACKET_ACCOUNT','(DWORD) Command\r\n(DWORD) Result (0: Failed, 1: Succeeded) ','Command indicates the commandID that is being responded to.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (367,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,5,'MCP_GAMELIST','(WORD) Request Id\r\n(DWORD) Index\r\n(BYTE) Number of players in game\r\n(DWORD) Status\r\n(STRING) Game name\r\n(STRING) Game description','Instead of receiving a single response that has a list of all the games, the client will receive this packet once for every game listed by the server.\r\n<dl>\r\n<dt>Request Id:</dt>\r\n<dd>Like a cookie. This value will be whatever you sent the server in MCP_GAMELIST.</dd>\r\n<dt>Index:</dt>\r\n<dd>The game\'s index on the server.</dd>\r\n<dt>Number of players in game:</dt>\r\n<dd>Self explanatory.</dd>\r\n<dt>Status:</dt>\r\n<dd>0x00300004: Game is available to join</dd>\r\n<dd>0xFFFFFFFF: Server is down</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (368,NULL,'2011-09-30 08:15:28',0,0,NULL,1,7,2,24,'BNLS_VERSIONCHECKEX','(BOOLEAN) Success *\r\n(DWORD) Version\r\n(DWORD) Checksum\r\n(STRING) Version check stat string\r\n(DWORD) Cookie\r\n(DWORD) The latest version code for this product','* Success is TRUE if successful, FALSE otherwise. If this is FALSE, the next DWORD is the provided cookie, following which the message ends. Do not mistake this value for a BYTE, it is actually a DWORD.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (369,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,6,'PACKET_USERINFO','[blank] \r\n','This packet is sent to request a list of all users currently logged onto the BotNet.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (370,NULL,'2010-07-14 17:18:43',0,0,NULL,1,1,2,0,'SID_NULL','[blank]','Keeps the connection alive. Clients do not need to respond to this message.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (371,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,97,'D2GS_POTIONTOMERCENARY','(WORD) Unknown - 0x00 \r\n','Takes the potion your cursor holds and gives it to the mercenary. Please note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (372,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,6,'SID_STARTVERSIONING','(DWORD) Platform ID\r\n(DWORD) Product ID\r\n(DWORD) Version Byte\r\n(DWORD) Unknown (0) ','This message is sent to the server to start the process of checking the game files. This message is part of the old logon process for products before Starcraft.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (373,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,51,'D2GS_NPCSELL','(DWORD) NPC ID - Unconfirmed\r\n(DWORD) Item ID - Unconfirmed\r\n(DWORD) Buffer ID - Unconfirmed - Possible value 0x04\r\n(DWORD) Cost \r\n','Sell an item to a Non Player Character. \r\n\r\nThe Buffer ID refers to the windows in the NPC trade screen, depending on what you are selling the Buffer ID will match the item type.\r\nAfter selling the item it will appear in the given Buffer ID.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (374,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,11,'SID_GETCHANNELLIST','(DWORD) Product ID ','Requests a list of channels that the client is permitted to enter.\r\n\r\nIn the past this packet returned a product list for the specified Product ID, however, the Product ID field is now ignored -- it does not need to be a valid Product ID, and can be set to zero. The list of channels returned will be for the client\'s product, as specified during the client\'s logon.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (375,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,1,'MCP_STARTUP','(DWORD) Result \r\n','Contains the return value from MCP Startup.\r\n<dl>\r\n<dt>Known return values:</dt>\r\n<dd>0x02, 0x0A-0x0D: Realm Unavailable: No Battle.net connection detected.</dd>\r\n<dd>0x7E: CDKey banned from realm play.</dd>\r\n<dd>0x7F: Temporary IP ban \"Your connection has been temporarily restricted from this realm. Please try to log in at another time.\"</dd>\r\n<dd>Else: Success.</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (376,NULL,'2011-07-20 01:42:36',0,0,NULL,1,1,2,120,'SID_CLANREMOVEMEMBER','(DWORD) Cookie\r\n(BYTE) Status \r\n','Response when removing a member from your clan.\r\n\r\nStatus constants:<dl>\r\n<dd>0x00: Removed</dd>\r\n<dd>0x01: Removal failed</dd>\r\n<dd>0x02: Cannot be removed yet</dd>\r\n<dd>0x07: Not authorized to remove</dd>\r\n<dd>0x08: Not allowed to remove</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (377,NULL,'2012-02-26 02:13:13',0,0,NULL,1,1,2,58,'SID_LOGONRESPONSE2','(DWORD) Status\r\n(STRING) Additional information','<dl><dt>Status:</dt>\r\n<dd>0x00: Success.</dd>\r\n<dd>0x01: Account does not exist.</dd>\r\n<dd>0x02: Invalid password.</dd>\r\n<dd>0x06: Account closed.</dd></dl>\r\n\r\nThe string containing the reason is only present when your account is closed. If not, the entire field is omitted including the null-terminator.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (378,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,84,'SID_AUTH_ACCOUNTLOGONPROOF','(BYTE) [20] Client Password Proof (M1)','This message is sent to the server after a successful SID_AUTH_ACCOUNTLOGON. It contains the client\'s password proof. See [NLS/SRP Protocol] for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (379,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,86,'SID_AUTH_ACCOUNTCHANGEPROOF','(DWORD) Status code\r\n(BYTE) [20] Server password proof for old password (M2) \r\n','This message reports success or failure for a password change operation.\r\n\r\nStatus codes:<dl><dd>0x00: Password changed</dd>\r\n<dd>0x02: Incorrect old password</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (380,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,5,'D2GS_LEFTSKILLONLOCATION','(WORD) X coordinate\r\n(WORD) Y coordinate\r\n','Uses Left skill on specified (X,Y) map coordinate.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (381,NULL,'2012-04-01 18:41:40',0,0,NULL,1,1,1,30,'SID_CLIENTID2','(DWORD) Server Version<dl><dt>For server version 1:</dt>\r\n<dd>(DWORD) Registration Version</dd>\r\n<dd>(DWORD) Registration Authority</dd>\r\n<dt>For server version 0:</dt>\r\n<dd>(DWORD) Registration Authority</dd>\r\n<dd>(DWORD) Registration Version</dd></dl>\r\n(DWORD) Account Number\r\n(DWORD) Registration Token\r\n(STRING) LAN computer name\r\n(STRING) LAN username ','See related link for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (382,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,51,'SID_GETFILETIME','(DWORD) Request ID\r\n(DWORD) Unknown\r\n(STRING) Filename','This packet seems to request the current filetime for the specified file. Purpose of first 2 DWORDs is unknown, however, both are echoed back to the client by Battle.net and do not seem to affect the reply. Because of this it is reasonable to assume that first DWORD at least is a request ID of some kind. This is called into question, however, by the fact that the replying packet also contains the requested filename. The game (STAR/SEXP) always sends the same number in DWORD 1 for the file in question. DWORD 2 seems to be null.\r\n\r\n<dl><dt>Known codes for DWORD 1:</dt>\r\n<dd>0x01: tos_usa.txt\r\n0x03: bnserver-WAR3.ini\r\n0x1A: tos_USA.txt\r\n0x1B: bnserver.ini\r\n0x1D: icons_STAR.bni\r\n0x80000006: (0x4C) IX86ExtraWork.mpq\r\n0x80000005: (0x4A) IX86ExtraWork.mpq\r\n0x80000004: bnserver-D2DV.ini</dd>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (383,NULL,'2011-02-04 21:56:28',0,0,NULL,1,7,2,11,'BNLS_HASHDATA','(DWORD) [5] The data hash.\r\n(DWORD) Cookie (Optional). Same as the cookie from the request. \r\n','This message contains the hashed data.\r\n\r\nCookie is only in this packet if you supplied it first.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (384,NULL,'2011-06-02 18:33:52',0,0,NULL,1,1,2,102,'SID_FRIENDSUPDATE','(BYTE) Entry number\r\n(BYTE) Location\r\n(BYTE) Status\r\n(DWORD) ProductID\r\n(STRING) Location','Sent to clients supporting this packet when the friendlisted account\'s Status or Location changes. The first friend is always entry 0. \r\n\r\nNote that there is a Battle.net server bug in which when you are automatically sent this packet, the Product ID is your own Product ID instead of your friend\'s. So if you were to be using WAR3, for example, and a friend signs on using SEXP, the Product ID in this packet will be WAR3. To receive the *correct* Product ID for the user, you may request another update for this user (by sending 0x66 back to the server with the same Entry Number), or request the full list again.\r\n\r\nIn addition, this packet is not sent to you automatically if the friendship is not mutual.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (385,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,2,10,'PACKET_BOTNETVERSION','(DWORD) Server Version \r\n','Version information:\r\nVersion 1 supports all packets 0x00 through 0x0B.\r\nVersion 2 supports messages 0x0C and 0x0D.\r\nVersion 3 adds the \'Database\' field to PACKET_USERINFO (0x06).\r\n\r\nVersion 4 is currently under development by [vL]Kp. It contains significant improvements over Version 3, including measures to make it easier for bot developers to troubleshoot their clients. A test version of the server is running at www.valhallalegends.com on port 0x5554 . Additions to the Botnet protocol will not be documented here until they are finalised, however, current documentation can be found here. It is subject to change without notice. Developers should feel free to add support for for Version 4 to their bots, are welcome to use the test server and are encouraged to provide feedback.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (386,NULL,'2010-10-03 18:04:18',0,0,NULL,1,1,1,9,'SID_GETADVLISTEX','(WORD) Game Type\r\n(WORD) Sub Game Type\r\n(DWORD) Viewing Filter\r\n(DWORD) Reserved (0)\r\n(DWORD) Number of Games\r\n(STRING) Game Name\r\n(STRING) Game Password\r\n(STRING) Game Statstring','Retrieves a list of games.\r\n\r\n<b>Game Type:</b>\r\n<dl><dd>For STAR/SEXP/SSHR/JSTR and W2BN, a value of 0 indicates that any type is acceptable.\r\n<dl>\r\n<dt>Possible game types:</dt>\r\n<dd>0x00: All</dd>\r\n<dd>0x02: Melee</dd>\r\n<dd>0x03: Free for all</dd>\r\n<dd>0x04: one vs one</dd>\r\n<dd>0x05: CTF</dd>\r\n<dd>0x06: Greed</dd>\r\n<dd>0x07: Slaughter</dd>\r\n<dd>0x08: Sudden Death</dd>\r\n<dd>0x09: Ladder</dd>\r\n<dd>0x10: Iron man ladder</dd>\r\n<dd>0x0A: Use Map Settings</dd>\r\n<dd>0x0B: Team Melee</dd>\r\n<dd>0x0C: Team FFA</dd>\r\n<dd>0x0D: Team CTF</dd>\r\n<dd>0x0F: Top vs. Bottom</dd>\r\n</dl>\r\n\r\nFor DRTL/DSHR, Game Type is used to specify a \'level range\'. This ensures that clients receive a list of games containing players whose experience is similar to their own.\r\n<dl>\r\n<dt>Possible ranges:</dt>\r\n<dd>0x00: Level 1</dd>\r\n<dd>0x01: 2 - 3</dd>\r\n<dd>0x02: 4 - 5</dd>\r\n<dd>0x03: 6 - 7</dd>\r\n<dd>0x04: 8 - 9</dd>\r\n<dd>0x05: 10 - 12</dd>\r\n<dd>0x06: 13 - 16</dd>\r\n<dd>0x07: 17 - 19</dd>\r\n<dd>0x08: 20 - 24</dd>\r\n<dd>0x09: 25 - 29</dd>\r\n<dd>0x0A: 30 - 34</dd>\r\n<dd>0x0B: 35 - 39</dd>\r\n<dd>0x0C: 40 - 47</dd>\r\n<dd>0x0D: 48 - 50</dd>\r\n</dl>\r\nFor all other games, Game Type can be set to 0x00.</dd></dl>\r\n<b>Sub Game Type:</b>\r\n<dl>\r\n<dd>For Greed, this is resources in multiples of 2500 (0x01 = 2500, 0x02 = 5000, etc).</dd>\r\n<dd>For Slaughter, this is minutes in multiples of 15 (0x01 = 15, 0x02 = 30, etc).</dd>\r\n<dd>For Ladder, this indicates if disconnects count as a loss (0x01 = Yes, 0x00 = No).</dd>\r\n<dd>For Team Melee, this is the number of teams (0x01 = 2, 0x02 = 3, etc).</dd>\r\n<dd>For Team Free For All, this is the number of teams (0x01 = 2, 0x02 = 3, etc).</dd>\r\n<dd>For Team Capture The Flag, this is the number of teams (0x01 = 2, 0x02 = 3, etc).</dd>\r\n<dd>For Top vs. Bottom, this is the players to team ratio (1-7).</dd>\r\n<dd>For any other game type, it is 0x01 which indicates no sub game type.</dd>\r\n</dl>\r\n<b>Viewing Filter:</b>\r\n<dl>\r\n<dd>0xFFFF is used to use the combination of values in this packet.</dd>\r\n<dd>0xFF80 is used to show all games.</dd>\r\n<dd>For STAR/SEXP/SSHR/JSTR, viewing filter is set to 0x30.</dd>\r\n<dd>For DRTL/DSHR, viewing filter is set to 0xFFFF by the game, but setting it to 0x00 will disable any viewing limitations, letting you view all games.</dd>\r\n</dl>\r\n<b>Reserved (0):</b>\r\n<dl><dd>This value is hardcoded to 0x00 by all games.</dd></dl>\r\n<b>Number of Games:</b>\r\n<dl><dd>This is the number of games to list. For a full listing, it\'s safe to use 0xFF. By default, DRTL/DSHR sets this to 0x19.</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (387,NULL,'2012-02-26 04:25:36',0,0,NULL,2,2,1,7,'PKT_KEEPALIVE','(DWORD) Tick count ','This packet is used to keep firewalls happy and improve NAT support. It should be sent every 3-5 minutes.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (388,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,24,'SID_REGISTRY','(DWORD) Cookie\r\n(DWORD) HKEY\r\n(STRING) Registry path\r\n(STRING) Registry key \r\n','<dl><dt>HKEY is the registry key root.</dt><dd><dl><dt>The values are standard:</dt>\r\n<dd>0x80000000: HKEY_CLASSES_ROOT</dd>\r\n<dd>0x80000001: HKEY_CURRENT_USER</dd>\r\n<dd>0x80000002: HKEY_LOCAL_MACHINE</dd>\r\n<dd>0x80000003: HKEY_USERS</dd>\r\n<dd>0x80000004: HKEY_PERFORMANCE_DATA</dd>\r\n<dd>0x80000005: HKEY_CURRENT_CONFIG</dd>\r\n<dd>0x80000006: HKEY_DYN_DATA</dd></dl></dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (389,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,38,'D2GS_USEBELTITEM','(DWORD) Item ID\r\n(DWORD) Unknown - Possibly unused\r\n(DWORD) Unknown - Possibly unused \r\n','Uses the specified item in the player\'s belt.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (390,NULL,'2012-06-01 21:13:12',0,0,NULL,1,1,2,47,'SID_FINDLADDERUSER','(DWORD) Rank.','Contains the rank of the user specified by the request.\r\n\r\nRank is 0-based. A rank of 0xFFFFFFFF is no rank.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (391,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,34,'D2GS_REMOVESTACKITEM','(DWORD) Item ID \r\n','Removes an item from the stack Please note: This message\'s official name is not known, and has been invented. The contents of this packet may be incomplete or incorrect, and your comments and contributions are requested.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (392,NULL,'2011-05-21 04:56:32',0,0,NULL,1,1,2,129,'SID_CLANMEMBERRANKCHANGE','(BYTE) Old rank\r\n(BYTE) New rank\r\n(STRING) Clan member whose rank changed','Received from the server when a user is promoted or demoted.\r\n\r\nFor information about the allowed values for the old and new rank fields, see [Clan Message Codes].');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (393,NULL,'2010-12-14 14:16:57',0,0,NULL,1,1,1,68,'SID_WARCRAFTGENERAL','(BYTE) Subcommand ID\r\n<dl>\r\n <dt>0x00 (WID_GAMESEARCH):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(DWORD) Unknown</dd>\r\n <dd>(BYTE) Unknown</dd>\r\n <dd>(BYTE) Game Type\r\n  <dl>\r\n   <dd>0x00: 1vs1</dd>\r\n   <dd>0x01: 2vs2</dd>\r\n   <dd>0x02: 3vs3</dd>\r\n   <dd>0x03: 4vs4</dd>\r\n   <dd>0x04: Free for All</dd>\r\n  </dl>\r\n </dd>\r\n <dd>(WORD) Enabled Maps (every bit is one map, from 0x0000 to 0x0FFF)</dd>\r\n <dd>(WORD) Unknown</dd>\r\n <dd>(BYTE) Unknown</dd>\r\n <dd>(DWORD) Tick Count</dd>\r\n <dd>(DWORD) Race\r\n  <dl>\r\n   <dd>0x00000001: Human</dd>\r\n   <dd>0x00000002: Orc</dd>\r\n   <dd>0x00000004: Night Elf</dd>\r\n   <dd>0x00000008: Undead</dd>\r\n   <dd>0x00000020: Random</dd>\r\n  </dl>\r\n </dd>\r\n\r\n <dt>0x02 (WID_MAPLIST):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(BYTE) Number of types requested</dd>\r\n <dd>\r\n  <dl>\r\n   <dt>For each type requested:</dt>\r\n   <dd>(DWORD) ID*</dd>\r\n   <dd>(DWORD) Checksum*</dd>\r\n  </dl>\r\n </dd>\r\n\r\n <dt>0x03 (WID_CANCELSEARCH):</dt>\r\n <dd>[blank]</dd>\r\n\r\n <dt>0x04 (WID_USERRECORD):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(STRING) Account name</dd>\r\n <dd>(DWORD) Product ID (\'WAR3\' or \'W3XP\')</dd>\r\n\r\n <dt>0x07 (WID_TOURNAMENT)</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n\r\n <dt>0x08 (WID_CLANRECORD):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n <dd>(DWORD) Clan Tag</dd>\r\n <dd>(DWORD) Product ID (\'WAR3\' or \'W3XP\')</dd>\r\n\r\n <dt>0x09 (WID_ICONLIST):</dt>\r\n <dd>(DWORD) Cookie</dd>\r\n\r\n <dt>0x0A (WID_SETICON):</dt>\r\n <dd>(DWORD) Icon</dd>\r\n</dl>','This packet is used for multiple purposes on Warcraft III. Known and validated purposes are listed here.\r\n\r\n*Not fully known yet.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (394,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,56,'D2GS_NPCTRADE','(DWORD) Trade Type - Unconfirmed\r\n(DWORD) NPC ID - Unconfirmed\r\n(DWORD) Unknown - Unconfirmed - Possible value 0x00 \r\n','This packet\'s use is currently unconfirmed.\r\n\r\nPossible Trade Types:\r\n0x01 - Trade\r\n0x02 - Gamble\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (395,NULL,'2011-02-04 22:10:56',0,0,NULL,1,7,1,7,'BNLS_UPGRADECHALLENGE','(STRING) Account name\r\n(STRING) Account old password\r\n(STRING) Account new password (May be identical to old password but still must be provided)','This message will give you data you need for SID_AUTH_ACCOUNTUPGRADE (0x57).\r\nThis message is used to upgrade an existing account from Old Logon System to New Logon System.\r\nYou must send this before you can send BNLS_UPGRADEPROOF (0x08).\r\n\r\nImportant: You must send BNLS_LOGONCHALLENGE (0x02) or BNLS_CHANGECHALLENGE (0x05) before sending this. Otherwise, the results are meaningless.\r\n\r\nNote: Since Old Logon System and New Logon System are incompatible, you can change the password and upgrade the account at the same time.\r\nThis is not required - the old password and the new password may be identical for this message.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (396,NULL,'2011-02-04 22:36:25',0,0,NULL,1,7,2,14,'BNLS_AUTHORIZE','(DWORD) Server code','If the bot ID is not recognized by the server, then this message is still sent as backwards compatibility with the previous version of BNLS, which required authorization.\r\n\r\nThe client should calculate the checksum of the auth password and the server code using the [BNLS Checksum Algorithm]. The result is sent in BNLS_AUTHORIZEPROOF (0x0F).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (397,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,50,'SID_CHECKDATAFILE','(DWORD) Status','This message is no longer used; developers should use the SID_CHECKDATAFILE2 message.\r\n<dl><dt>Status:</dt>\r\n<dd>0x00: Rejected</dd>\r\n<dd>0x01: Approved</dd>\r\n<dd>0x02: Ladder approved</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (398,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,124,'SID_CLANMOTD','(DWORD) Cookie','Requests the clan\'s MOTD.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (399,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,4,'D2GS_RUNTOENTITY','(DWORD) *Entity Type\r\n(DWORD) Entity ID\r\n\r\n*Entity Types\r\n  -------------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances','Makes your character run to the Entity specified in Entity ID.\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (400,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,3,'PACKET_DATABASE','(DWORD) Command\r\n\r\nCommands 1 & 2:\r\n(STRING) 	 Usermask\r\n\r\nCommand 3:\r\n(STRING) 	 Flags\r\n(STRING) 	 Usermask','Possible values for Command:\r\n1: request user database\r\n2: add a database entry/modify a database entry\r\n3: remove a database entry');
