INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (402,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,115,'SID_CLANDISBAND','(DWORD) Cookie\r\n(BYTE) Result','Result:<dl>\r\n<dd>0x00: Successfully disbanded the clan</dd>\r\n<dd>0x02: Cannot quit clan, not one week old yet</dd>\r\n<dd>0x07: Not authorized to disband the clan</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (403,NULL,'2010-07-15 02:47:39',0,0,NULL,1,6,1,9,'PACKET_CHANGEDBPASSWORD','(DWORD) Password to change (0: Read Only, 1: Full, 2: Restricted)\r\n(STRING) New password \r\n','Requires full access to the database.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (404,NULL,'2011-06-19 19:36:41',0,0,NULL,1,7,2,19,'BNLS_SERVERLOGONCHALLENGE','(DWORD) Slot index\r\n(DWORD) [16] Data for the server\'s SID_AUTH_ACCOUNTLOGON (0x53) response','The slot index is returned since individual operations may be returned in a different order than they are requested.\r\n\r\nThis message can also be used to calculate the server\'s SID_AUTH_ACCOUNTCHANGE (0x55) response. Simply substitute the SID_AUTH_ACCOUNTLOGON (0x53) data with the SID_AUTH_ACCOUNTCHANGE (0x55) data.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (405,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,90,'SID_RESETPASSWORD','(STRING) Account Name\r\n(STRING) Email Address','Requests that Battle.net reset your password. This packet must be sent before logon.\r\n\r\nThis message requires an email address because Battle.net has to prove it\'s your account. Since this message must be sent before SID_LOGONRESPONSE, SID_LOGONRESPONSE2, or SID_AUTH_ACCOUNTLOGON, you must supply an e-mail address so Battle.net knows that you may have ownership to it.\r\n\r\nThis packet used to be named SID_AUTH_RECONNECTPROOF, however Blizzard never had it implemented, and so reused the packet ID for their email system.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (406,NULL,'2011-05-29 16:18:07',0,0,NULL,1,1,1,20,'SID_UDPPINGRESPONSE','(DWORD) UDPCode ','Enables UDP support.\r\n\r\nServer supplies code via UDP packet PKT_SERVERPING (0x05). Usually \'bnet\'.\r\n\r\nNot responding will give you a UDP Plug in chat (flag 0x10).\r\n\r\nYou should send this anytime between S>C 0x50 SID_AUTH_INFO (or equivalent) and C>S 0x0A SID_ENTERCHAT. Sending this packet before or after these two packets will get you disconnected from official servers.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (407,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,85,'SID_AUTH_ACCOUNTCHANGE','(DWORD) Status\r\n(BYTE) [32] Salt (s)\r\n(BYTE) [32] Server key (B)','Reports success or failure on a password change operation.\r\n\r\nStatus Codes:<dl><dd>0x00: Change accepted, requires proof.</dd>\r\n<dd>0x01: Account doesn\'t exist.</dd>\r\n<dd>0x05: Account requires upgrade - see SID_AUTH_ACCOUNTUPGRADE</dd>\r\n<dd>Other: Unknown (failure).</dd></dl>\r\n\r\nIf an error occurs, the salt and server key values are set to zero.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (408,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,81,'SID_AUTH_CHECK','(DWORD) Client Token\r\n(DWORD) EXE Version\r\n(DWORD) EXE Hash\r\n(DWORD) Number of CD-keys in this packet\r\n(BOOLEAN) Spawn CD-key **\r\n\r\nFor Each Key:<dl><dd>(DWORD) Key Length</dd>\r\n<dd>(DWORD) CD-key\'s product value</dd>\r\n<dd>(DWORD) CD-key\'s public value</dd>\r\n<dd>(DWORD) Unknown (0)</dd>\r\n<dd>(DWORD) [5] Hashed Key Data</dd></dl>\r\n\r\n(STRING) Exe Information\r\n(STRING) CD-Key owner name *','Contains the EXE Version and Hash as reported by CheckRevision(), and CDKey values. Spawn may only be used for STAR and W2BN.\r\n\r\nThe data that should be hashed for \'Hashed Key Data\' is:\r\n<ol><li>Client Token</li><li>Server Token</li><li>Key Product (from decoded CD key)</li><li>Key Public (from decoded CD key)</li><li>(DWORD) 0</li><li>Key Private (from decoded CD key)</li></ol>\r\n\r\nThe data that should be used for EXE Information should be separated by one space, in the format of:\r\n<ol><li>EXE Name (like war3.exe)</li><li>Last Modified Date (like 08/16/09)</li><li>Last Modified Time (like 19:21:59)</li><li>Filesize in bytes (like 471040)</li></ol>An example of a valid string would be:\r\n<dl><dd>war3.exe 08/16/09 19:21:59 471040</dd></dl>\r\n\r\n* The CD Key owner name must be no greater than 15 characters, otherwise it becomes trimmed by Battle.net.\r\n\r\n** This BOOLEAN value is actually a DWORD, not a BYTE. You will be IP-banned for 5 minutes if you assume BYTE and not use the proper DWORD. Also, this value can only be TRUE for Starcraft, Japan Starcraft and Warcraft II only. Any other game will cause unexpected results.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (409,NULL,'2012-06-12 09:32:04',0,0,NULL,1,1,1,53,'SID_PROFILE','(DWORD) Cookie\r\n(STRING) Username','This requests a profile for a user. This message\'s official name is not known, and has been invented.\r\n\r\nIt is safe to look up any username you want. The username can have illegal characters, can be of any length, and can have all the other \"illy\" username characteristics. This message can also be used across all products without a problem, not just WAR3/W3XP.\r\n\r\nBattle.net will translate the realm in the username for you, so you can look up @Azeroth as well as @USEast, if you want. You cannot perform cross-realm look ups, however; that is to say you cannot look up @USWest if you\'re on @USEast and likewise for the other realms.\r\n\r\nIf you look up a user that is not on their Warcraft III user table, then the clan tag in the replying message will always be null. But it is safe to do this as well. The profile description and location strings will match that of the user you look up, regardless of which internal user table the user resides on.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (410,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,42,'D2GS_ITEMTOCUBE','(DWORD) Item ID\r\n(DWORD) Cube ID \r\n','Moves item from player\'s cursor buffer into Horadric cube.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (411,NULL,'2010-07-15 02:47:39',0,0,NULL,1,3,2,10,'MCP_CHARDELETE','(DWORD) Result','Deletion response.\r\n<dl>\r\n <dt>Known values:</dt>\r\n <dd>\r\n  <dl>\r\n   <dt>(Diablo II v1.10 or later)</dt>\r\n   <dd>0x00: Success</dd>\r\n   <dd>0x49: Character doesn\'t exist</dd>\r\n  </dl>\r\n  <dl>\r\n   <dt>(Diablo II v1.09 or earlier)</dt>\r\n   <dd>0x00: Character doesn\'t exist</dd>\r\n   <dd>0x49: Success</dd>\r\n  </dl>\r\n </dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (412,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,7,'SID_REPORTVERSION','(DWORD) Result\r\n(STRING) Patch path \r\n','Reports success/failure on challenge.\r\n\r\nResult:\r\n<dl><dd>0: Failed version check</dd>\r\n<dd>1: Old game version</dd>\r\n<dd>2: Success</dd>\r\n<dd>3: Reinstall required</dd></dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (413,NULL,'2011-02-04 22:35:25',0,0,NULL,1,7,1,14,'BNLS_AUTHORIZE','(STRING) Bot ID','This message logs on to the BNLS server.\r\n\r\nNote: The bot ID is not case sensitive, and is limited to 31 characters.\r\n\r\nThis message was used to let your bot log into the BNLS service. It is no longer required, but it may still be used for backwards compatibility. If you wish to require your bot to login to BNLS to be able to work, you can change your BNLS password and prevent your bot from logging in. Useful for required patches and such.\r\n\r\nTo get a bot ID and password, ask Yoni or Skywing.\r\n\r\nNote: As of June 28, 2004, BNLS IDs were no longer required to log into BNLS; clients could completely bypass using messages BNLS_AUTHORIZE and BNLS_AUTHORIZEPROOF to log on, and BNLS would validate any client that logged in via these methods. On August 14, 2004, BNLS was again changed so that, while clients can still bypass using messages BNLS_AUTHORIZE and BNLS_AUTHORIZEPROOF to log on, clients that do support these messages will be validated against the bot ID database, which allows those users who still own bot IDs to disable old versions by changing the password on the account.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (415,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,92,'SID_SWITCHPRODUCT','(DWORD) Product ID','When logging onto WAR3, while having W3XP installed on your system; the client sends two CDKey hashes in SID_AUTH_CHECK and uses \'W3XP\' as the Product Id in SID_AUTH_INFO. After a successful SID_AUTH_CHECK, the client then sends this packet with the Product ID set to \'WAR3\' to make the switch from expansion to non-expansion.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (420,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,94,'SID_WARDEN','(VOID) Encrypted Packet\r\n<dl>\r\n <dt>Contents of encrypted data</dt>\r\n <dd>(BYTE) Packet Code</dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x00 - Warden Module Info</dt>\r\n   <dd>(DWORD)[4] MD5 Hash of the current Module</dd>\r\n   <dd>(DWORD)[4] Decryption key for Module</dd>\r\n   <dd>(DWORD) Length of Module</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x01 - Warden Module Data</dt>\r\n   <dd>(WORD) Length of data (without 3-byte header)</dd>\r\n   <dd>(VOID) Data</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x02 - Data Checker</dt>\r\n   <dd>(BYTE) String Length (Usually 0)</dd>\r\n   <dd>(VOID) String Data</dd>\r\n   <dd>\r\n    <dl>\r\n     <dt>(BYTE) Check ID</dt>\r\n     <dd>\r\n      <dl>\r\n       <dt>MEM_CHECK:</dd>\r\n       <dd>(BYTE) String Index (Usually 0)</dd>\r\n       <dd>(DWORD) Address</dd>\r\n       <dd>(BYTE) Length to Read</dd>\r\n      </dl>\r\n     </dd>\r\n     <dd>\r\n      <dl>\r\n       <dt>PAGE_CHECK_A:</dd>\r\n       <dd>(DWORD) Unknown (Seed?)</dd>\r\n       <dd>(DWORD)[5] SHA1</dd>\r\n       <dd>(DWORD) Address</dd>\r\n       <dd>(BYTE) Length to Read</dd>\r\n      </dl>\r\n     </dd>\r\n    </dl>\r\n   </dd>\r\n   <dd>(BYTE) IDXor</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x03 - Library Loader</dt>\r\n   <dd>(WORD) Length of data (without 7-byte header)</dd>\r\n   <dd>(DWORD) Checksum of data (without 7-byte header)</dd>\r\n   <dd>(BYTE) Unknown (0x01)<dd>\r\n   <dd>(BYTE) Unknown (Usually 0x00)</dd>\r\n   <dd>(BYTE) Unknown (Usually 0x01)</dd>\r\n   <dd>(STRING) Library Name</dd>\r\n   <dd>(DWORD) Funct1</dd>\r\n   <dd>(DWORD) Funct2</dd>\r\n   <dd>(DWORD) Funct3</dd>\r\n   <dd>(DWORD) Funct4</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x05 - Initialization</dt>\r\n   <dd>(DWORD)[5] Unknown</dd>\r\n  </dl>\r\n </dd>\r\n</dl>','This packet is received after successfully logging onto Battle.net and usually after receiving the first initial chat events. If the client does not respond to this packet, the client gets dropped two minutes later (give or take about 10 seconds).\r\n\r\nThe packet is encrypted via standard RC4 hashing, using one key for outbound data and another for inbound. Its purpose is to download and execute Warden modules. Full information on how to handle this packet may be found at the <a href=\"http://forum.valhallalegends.com/index.php?topic=17356.0\">Rudimentary Warden information</a> topic.\r\n\r\nDocumentation provided by iago and Ringo.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (423,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,7,'D2GS_LEFTSKILLONENTITYEX','(DWORD) Entity Type\r\n(DWORD) Entity ID','Uses your left skill on the Entity specefied in Entity ID, while holding the hotkey for standing still(shift).\r\n\r\nEntity types\r\n---------------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.\r\n\r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (424,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,10,'D2GS_LEFTSKILLONENTITYEX3','(DWORD) *Entity Type\r\n(DWORD) Entity ID\r\n\r\n*Entity Types\r\n-------------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n','Uses your left skill on the Entity specefied in Entity ID, while holding the hotkey for standing still(shift).\r\n\r\nThis packet is sent repeatedly when the mouse button is held down after the initial packet has been sent. \r\n\r\nRefer to the D2GS Entity Types document for more information.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (425,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,17,'D2GS_RIGHTSKILLONENTITYEX3','(DWORD) Entity Type\r\n(DWORD) Entity ID\r\n\r\nEntity Types\r\n--------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances\r\n','Uses your right skill on the Entity specefied in Entity ID, while holding the hotkey for standing still(shift).\r\n\r\nThis packet is sent repeatedly when the mouse button is held down after the initial packet has been sent. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (426,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,14,'D2GS_RIGHTSKILLONENTITYEX','	(DWORD) Entity Type\r\n(DWORD) Entity ID\r\n\r\nEntity types\r\n--------\r\n00 - Players\r\n01 - Monsters, NPCs, and Mercenaries\r\n02 - Stash, Waypoint, Chests, Portals, others.\r\n03 - Missiles\r\n04 - Items\r\n05 - Entrances','Uses your right skill on the Entity specefied in Entity ID, while holding the hotkey for standing still(shift).\r\n\r\nPlease note: This message\'s official name is not known, and has been invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (428,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,1,28,'D2GS_PICKUPBODYITEM','(WORD) Body Location','Pickup an item from a Body Location to you\'re cursor buffer.\r\n\r\nPossible Body Locations:\r\n0x01 - Helm\r\n0x02 - Amulet\r\n0x03 - Armor\r\n0x04 - Left-hand weapon\r\n0x05 - Right-hand weapon\r\n0x06 - Left-hand ring\r\n0x07 - Right-hand ring\r\n0x08 - Belt\r\n0x09 - Boots\r\n0x0A - Gloves');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (429,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,2,78,'SID_TOURNAMENT','(BYTE) Unknown\r\n(BYTE) Unknown, maybe number of non-null strings sent?\r\n(STRING) Description\r\n(STRING) Unknown\r\n(STRING) Website\r\n(DWORD) Unknown\r\n(STRING) Name\r\n(STRING) Unknown\r\n(STRING) Unknown\r\n(STRING) Unknown\r\n(DWORD[5]) Unknown','<b>Research requested.</b>\r\nSent after a successful logon to an account in a WCG tournament. \r\nThe 0x0C packet sent with 0x01 flags would automatically have the user join channel WCG planetname #. \r\n\r\n<dt><b>Packet Log</b></dt>\r\n<dd>FF 4E 58 00</dd>\r\n<dd>01</dd>\r\n<dd>03</dd>\r\n<dd>57 6F 72 6C 64 20 43 79 62 65 72 20 47 61 6D 65 73 20 28 53 61 74 75 72 6E 29 00</dd>\r\n<dd>00</dd>\r\n<dd>68 74 74 70 3A 2F 2F 77 77 77 2E 77 63 67 2E 63 6F 6D 00 57 43 47 00</dd>\r\n<dd>00 00 00 00</dd>\r\n<dd>80 07 01 00</dd>\r\n<dd>00</dd>\r\n<dd>00</dd>\r\n<dd>00</dd>\r\n<dd>00 01 01 00</dd>\r\n<dd>02 02 00 00</dd>\r\n<dd>00 00 01 05</dd>\r\n<dd>00 00 00 00</dd>\r\n<dd>32 00 00 00</dd>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (430,NULL,'2010-07-15 02:47:39',0,0,NULL,1,4,2,137,'D2GS_UNIQUEEVENTS','(BYTE) EventId // see below,\r\n\r\nEvents known so far:\r\n00 = Killed all the monsters in the den.\r\n01 = Tristram portal opening for the first time.\r\n03 = Staff being put into the oriface in act2\r\n06 = Ammy being poped in clawviper temple.\r\n07 = Summoner area? death or when the tome is clicked unsure which.\r\n08 = Duriel just died\r\n0b = Meph just died.\r\n0c = The last seal in cs was hit diablo is now released.\r\n0d = Diablo was killed or is dead.\r\n','I\'m sure there are many others by looking at the gaps here.\r\n\r\nNote1: Unsure what to name this any suggestions will be taken into account.\r\nNote2: This seems to relate to various events that relate directly or indirectly to key quest states//events.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (431,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,94,'SID_WARDEN','(VOID) Encrypted Packet\r\n<dl>\r\n <dt>Contents of encrypted data</dt>\r\n <dd>(BYTE) Packet Code</dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x00 - Warden Module Info</dt>\r\n   <dd>(BYTE) Success (0x00 = Don\'t have the module, 0x01 = Have the module)</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x01 - Warden Module Data</dt>\r\n   <dd>(BYTE) Success (0x00 = MD5 doesn\'t match, 0x01 = MD5 matches)</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x02 - Data Checker</dt>\r\n   <dd>(WORD) String Length</dd>\r\n   <dd>(DWORD) String Checksum</dd>\r\n   <dd>\r\n    <dl>\r\n     <dt>(VOID) String Data</dt>\r\n     <dd>\r\n      <dl>\r\n       <dt>MEM_CHECK:</dd>\r\n       <dd>(BYTE) Success (0x00 = Read data, 0x01 = Unable to read)</dd>\r\n       <dd>(VOID) Data (0x00 only)</dd>\r\n      </dl>\r\n     </dd>\r\n     <dd>\r\n      <dl>\r\n       <dt>PAGE_CHECK_A:</dd>\r\n       <dd>(BYTE) Success (0x00 = SHA1s match, 0x01 = SHA1s don\'t match)</dd>\r\n      </dl>\r\n     </dd>\r\n    </dl>\r\n   </dd>\r\n   <dd>(BYTE) IDXor</dd>\r\n  </dl>\r\n </dd>\r\n <dd>\r\n  <dl>\r\n   <dt>0x04 - Initialization</dt>\r\n   <dd>(DWORD)[4] Unknown</dd>\r\n  </dl>\r\n </dd>\r\n</dl>','The packet is encrypted via standard RC4 hashing, using one key for outbound data and another for inbound. Its purpose is to return executable data and checksum information from various Warden modules. Full information on how to handle this packet may be found at the <a href=\"http://forum.valhallalegends.com/index.php?topic=17356.0\">Rudimentary Warden information</a> topic.\r\n\r\nDocumentation provided by iago and Ringo.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (432,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,88,'SID_AUTH_ACCOUNTUPGRADEPROOF','(DWORD) Client Token\r\n(DWORD) [5] Old Password Hash\r\n(BYTE) [32] New Password Salt\r\n(BYTE) [32] New Password Verifier','<dl>\r\n <dt>Old Password Hash:</dt>\r\n <dd>Broken SHA-1 Double password hash as seen in SID_LOGONRESPONSE(2) OLS.</dd>\r\n <dt>New Password Salt & Verifier:</dt>\r\n <dd>Salt and Verifier values as seen in SID_AUTH_ACCOUNTCREATE.</dd>\r\n</dl>\r\nOld Password is the account\'s current password. The New Password can be the same as the Old Password, but it can be used to change the password as well.\r\n\r\nBasically this packet would convert the stored password hash to a new one, and thus become NLS. However, this packet is no longer responded to, and upgrading accounts is therefore impossible.(Note: If you have an account in need of upgrading [created with SID_CREATEACCOUNT(2)], you can let the account expire and recreate it with SID_AUTH_ACCOUNTCREATE.)');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (433,NULL,'2010-07-15 02:47:39',0,0,NULL,1,1,1,93,'SID_REPORTCRASH','(DWORD) Version? (0x010A0027)\r\n(DWORD) Exception code\r\n(DWORD) Unknown\r\n(DWORD) Unknown','When the game crashes, (usually) a file named Crashdump is created. If this file exists at the next logon, the contents of it are sent to Battle.net in this message.\r\n\r\nThe first DWORD for Diablo II is a constant value (version?), as seen in Fog.dll.\r\n\r\nAll calls to Fog_10052 result in the output of Crashdump.\r\n\r\nMore research is required.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (434,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,47,'W3GS_SEARCHGAME','(DWORD) Product\r\n(DWORD) Version\r\n(DWORD) Unknown (0)','This is sent to the entire local area network to detect games.\r\n\r\nProduct is either WAR3 or W3XP.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (435,63,'2010-07-23 23:28:12',0,0,NULL,1,5,2,48,'W3GS_GAMEINFO','(DWORD) Product\r\n(DWORD) Host Counter\r\n(DWORD) Players In Game\r\n(STRING) Game name\r\n(BYTE) Unknown (0)\r\n(STRING) Statstring\r\n(DWORD) Slots total\r\n(BYTE)[] Game Type Info\r\n(DWORD) Slots available\r\n(DWORD) Time since creation\r\n(WORD) Game Port','This is sent every 5 seconds over a UDP broadcast to update the local area network about the game.\r\n\r\nThis is also sent when a client clicks on the game in a list.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (436,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,49,'W3GS_CREATEGAME','(DWORD) Product\r\n(DWORD) Host Counter\r\n(DWORD) Players In Game','Notifies the local area network that a game was created.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (437,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,50,'W3GS_REFRESHGAME','(DWORD) Host Counter\r\n(DWORD) Players In Game\r\n(DWORD) Slots available','Notifies the local area network about a game (occurs every 5 seconds or refresh slots).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (438,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,51,'W3GS_DECREATEGAME','(DWORD) Host Counter','Notifies the local area network that a game is no longer being hosted.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (439,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,38,'W3GS_OUTGOING_ACTION','(DWORD) CRC-32 encryption\r\n(VOID) Action data','A client sends this to the game host to execute an action in-game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (440,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,12,'W3GS_INCOMING_ACTION','(WORD) Send interval\r\n(WORD) CRC-16 encryption\r\n\r\nFor each action:\r\n(BYTE) Player number\r\n(WORD) Length of action data\r\n(VOID) Action data','Informs the client about an action in-game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (441,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,61,'W3GS_MAPCHECK','(DWORD) Unknown\r\n(STRING) File Path\r\n(DWORD) File size\r\n(DWORD) Map info\r\n(DWORD) File CRC encryption\r\n(DWORD) File SHA-1 hash','This is sent from the game host to a client that just joined to check if the client has the map.\r\n\r\nThe map info is the actual CRC and the other CRC is actually an \"xoro\" value.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (442,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,63,'W3GS_STARTDOWNLOAD','(DWORD) Unknown\r\n(BYTE) Player number','This tells the client that it is now in the downloading state and should expect chunks of file data.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (443,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,63,'W3GS_STARTDOWNLOAD','[blank]','A client sends this to the host to initiate a map download.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (444,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,67,'W3GS_MAPPART','(BYTE) To player number\r\n(BYTE) From player number\r\n(DWORD) Unknown (0x01)\r\n(DWORD) Chunk position in file\r\n(DWORD) CRC-32 encryption\r\n(BYTE)[1442] Data','This is received when you are downloading a map from the host.\r\n\r\nYou can calculate how many more chunks you have left based on the file size and the chunk position in file. You are done downloading when the chunk position in file (plus the size of the chunk) matches the file size received in 0x42 W3GS_MAPSIZE.\r\n\r\nIf the data does not match the CRC-32, you should send the host 0x45 W3GS_MAPPARTNOTOK, otherwise you should always send 0x44 W3GS_MAPPARTOK in reply to this packet.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (445,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,68,'W3GS_MAPPARTOK','(BYTE) To player number\r\n(BYTE) From player number\r\n(DWORD) Unknown\r\n(DWORD) Chunk position in file','The client sends this when it has successfully received a chunk of the map file from the host client.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (446,63,'2012-06-11 21:06:14',0,0,NULL,1,5,1,30,'W3GS_REQJOIN','(DWORD) Host Counter (Game ID)\r\n(DWORD) Entry Key (used in LAN)\r\n(BYTE) Unknown\r\n(WORD) Listen Port\r\n(DWORD) Peer Key\r\n(STRING) Player name\r\n(DWORD) Unknown\r\n(WORD) Internal Port\r\n(DWORD) Internal IP','A client sends this to the host to enter the game lobby.\r\n\r\nThe internal IP uses the Windows in_addr structure which is thus used by the sockaddr_in structure.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (447,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,1,'W3GS_PING_FROM_HOST','[blank]','This is sent every 30 seconds to make sure that the client is still responsive.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (448,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,70,'W3GS_PONG_TO_HOST','(DWORD) tickCount','This is sent in response to 0x01 W3GS_HOSTECHOREQ.\r\n\r\nThe tickCount value is from GetTickCount().\r\n\r\nPing = (GetTickCount()-tickCount)/2\r\nFor the local area network, it can be 0.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (449,63,'2011-06-02 20:08:40',0,0,NULL,1,5,3,53,'W3GS_PING_FROM_OTHERS','[blank]','Client requests an echo from another client (occurs every 10 seconds).');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (450,63,'2011-06-02 20:08:27',0,0,NULL,1,5,3,54,'W3GS_PONG_TO_OTHERS','[blank]','This is sent in response to an echo from another client.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (451,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,6,'W3GS_PLAYERINFO','(DWORD) Player Counter\r\n(BYTE) Player number\r\n(STRING) Player name\r\n(WORD) Unknown (1)\r\n(WORD) AF_INET (2)\r\n(WORD) Port\r\n(DWORD) External IP\r\n(DWORD) Unknown (0)\r\n(DWORD) Unknown (0)\r\n(WORD) AF_INET (2)\r\n(WORD) Port\r\n(DWORD) Internal IP\r\n(DWORD) Unknown (0)\r\n(DWORD) Unknown (0)','Tells a client about a player\'s information.\r\n\r\nThe external and internal IP are always zero for the host.\r\n\r\nNOTE: This packet needs a better structure in the Format. Until then, you will have to deal with the unorganized fields.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (452,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,55,'W3GS_CLIENTINFO','(DWORD) Player Counter\r\n(DWORD) Unknown (0)\r\n(BYTE) Player number\r\n(BYTE)[5] Unknown','A client sends this to another client to gain information about self when connected.\r\n\r\nThe first byte in the second unknown is possibly the status of the player.\r\n\r\n<b>Packet Log:</b>\r\n	F7 37 12 00\r\n	02 00 00 00\r\n	00 00 00 00\r\n	06\r\n	FF 5E 00 00 00');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (453,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,33,'W3GS_LEAVEREQ','(DWORD) Reason','A client requests to leave.\r\n\r\nReasons:<dt>\r\n<dd>0x01 PLAYERLEAVE_DISCONNECT</dd>\r\n<dd>0x07 PLAYERLEAVE_LOST</dd>\r\n<dd>0x08 PLAYERLEAVE_LOSTBUILDINGS</dd>\r\n<dd>0x09 PLAYERLEAVE_WON</dd>\r\n<dd>0x0A PLAYERLEAVE_DRAW</dd>\r\n<dd>0x0B PLAYERLEAVE_OBSERVER</dd>\r\n<dd>0x0D PLAYERLEAVE_LOBBY</dd>\r\n</dt>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (454,63,'2011-06-02 20:06:11',0,0,NULL,1,5,2,27,'W3GS_LEAVERS','[blank]','This is the response to 0x21 W3GS_LEAVEREQ.\r\n\r\nYou will leave the game once the connection is terminated.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (455,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,7,'W3GS_PLAYERLEFT','(BYTE) Player number\r\n(DWORD) Reason','This is received from the game host when a player leaves.\r\n\r\nReasons:<dt>\r\n<dd>0x01 PLAYERLEAVE_DISCONNECT</dd>\r\n<dd>0x07 PLAYERLEAVE_LOST</dd>\r\n<dd>0x08 PLAYERLEAVE_LOSTBUILDINGS</dd>\r\n<dd>0x09 PLAYERLEAVE_WON</dd>\r\n<dd>0x0A PLAYERLEAVE_DRAW</dd>\r\n<dd>0x0B PLAYERLEAVE_OBSERVER</dd>\r\n<dd>0x0D PLAYERLEAVE_LOBBY</dd>\r\n</dt>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (456,63,'2011-03-13 21:30:17',0,0,NULL,1,5,2,5,'W3GS_REJECTJOIN','(DWORD) Reason','This is sent in a response to a request to join the game lobby and indicates that the request was denied.\r\n\r\nReasons:<dt>\r\n<dd>0x09 REJECTJOIN_FULL -- The game lobby is full</dd>\r\n<dd>0x07 REJECTJOIN_INVALID -- There was an error in your request</dd>\r\n<dd>0x10 REJECTJOIN_STARTED -- The game has already been started</dd>\r\n<dd>0x27 REJECTJOIN_WRONGPASSWORD -- The password you sent was incorrect</dd>\r\n</dt>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (457,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,8,'W3GS_PLAYERLOADED','(BYTE) Player number','Sent to all other clients in-game to notify that a player has finished loading.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (458,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,10,'W3GS_COUNTDOWN_START','[blank]','The game has begun the countdown to start.\r\n\r\nThe official clients countdown from 5 seconds, however it is possible to use any time you wish. For example, the GHost++ bot uses 10 seconds when auto-hosted, but 5 seconds when started using an administrative command.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (459,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,11,'W3GS_COUNTDOWN_END','[blank]','The game has finished the countdown and has now started. Players should see a loading screen for the map once this is received.\r\n\r\n0x10 W3GS_COUNTDOWN_START should be received before this packet is, even if there is no countdown.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (460,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,4,'W3GS_SLOTINFOJOIN','(WORD) Length of Slot Info\r\n(BYTE) Number of slots\r\n(BYTE)[] Slot data\r\n(DWORD) Random seed\r\n(BYTE) Game type\r\n(BYTE) Number of player slots without observers\r\n(BYTE) Player number\r\n(DWORD) Port\r\n(DWORD) External IP\r\n(DWORD) Unknown (0)\r\n(DWORD) Unknown (0)\r\n\r\nFor each slot:\r\n(BYTE) Player number\r\n(BYTE) Download status\r\n(BYTE) Slot status\r\n(BYTE) Computer status\r\n(BYTE) Team\r\n(BYTE) Color\r\n(BYTE) Race\r\n(BYTE) Computer type\r\n(BYTE) Handicap','This is sent to tell the client about the game slots, upon entry of the lobby.\r\n\r\nDownload status is a percentage of their download progress. As such, this value can only be between 0 and 100.\r\n\r\nSlot statuses:<dt>\r\n<dd>0x00 Open</dd>\r\n<dd>0x01 Closed</dd>\r\n<dd>0x02 Occupied</dd>\r\n</dt>\r\n\r\nIf the slot is a computer, then Computer will be 0x01, otherwise it will be 0x00.\r\n\r\nAvailable races:<dt>\r\n<dd>0x01 Human</dd>\r\n<dd>0x02 Orc</dd>\r\n<dd>0x04 Night Elf</dd>\r\n<dd>0x08 Undead</dd>\r\n<dd>0x20 Random</dd>\r\n<dd>0x40 Fixed</dd>\r\n</dt>\r\n\r\nComputer types:<dt>\r\n<dd>0x00 Easy</dd>\r\n<dd>0x01 Normal / Human</dd>\r\n<dd>0x02 Hard</dd>\r\n</dt>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (461,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,9,'W3GS_SLOTINFO','(WORD) Length of Slot Info\r\n\r\nSlot Info:\r\n(BYTE) Player number\r\n(BYTE) Download status\r\n(BYTE) Slot status\r\n(BYTE) Computer status\r\n(BYTE) Team\r\n(BYTE) Color\r\n(BYTE) Race\r\n(BYTE) Computer type\r\n(BYTE) Handicap','This is sent for slot updates.\r\n\r\nThe length of slot info should always be 0x0B.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (462,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,15,'W3GS_CHAT_FROM_HOST','(BYTE) Player count\r\n(BYTE)[] Player numbers that will receive the message\r\n(BYTE) Player number that sent the message\r\n(BYTE) Flags\r\n(DWORD) Extra Flags\r\n(STRING) Message','This is sent to the clients to print a message on the screen from another player.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (463,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,35,'W3GS_GAMELOADED_SELF','[blank]','The client sends this to the host when they have finished loading the map.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (464,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,39,'W3GS_OUTGOING_KEEPALIVE','(DWORD) Unknown','This is sent to the host from each client.\r\n\r\nThe unknown value may be a checksum and is also used in replays.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (465,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,40,'W3GS_CHAT_TO_HOST','(BYTE) Total\r\n\r\nFor each total:\r\n(BYTE) To player number\r\n(BYTE) From player number\r\n(BYTE) Flags\r\n<dt>\r\n<dd>For Flag 0x10:</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(STRING) Message</dd>\r\n<dd>For Flag 0x11:</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(BYTE) Team</dd>\r\n<dd>For Flag 0x12:</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(BYTE) Color</dd>\r\n<dd>For Flag 0x13:</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(BYTE) Race</dd>\r\n<dd>For Flag 0x14:</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(BYTE) Handicap</dd>\r\n<dd>For Flag 0x20:</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(DWORD) Extra Flags</dd>\r\n<dd>&nbsp;&nbsp;&nbsp;(STRING) Message</dd>\r\n</dt>','This is sent from the client to the host to send a message to the other clients.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (466,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,47,'W3GS_SEARCHGAME','(DWORD) Product\r\n(DWORD) Version\r\n(DWORD) Unknown','This is a reply to a client\'s request for games.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (467,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,66,'W3GS_MAPSIZE','(DWORD) Unknown\r\n(BYTE) Size Flag\r\n(DWORD) Map Size','This is sent from the client to tell the host about the map file on the client\'s local system.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (468,63,'2010-07-15 02:47:39',0,0,NULL,1,5,2,72,'W3GS_INCOMING_ACTION2','(WORD) Send interval\r\n(WORD) CRC-16 encryption\r\n\r\nFor each action:\r\n(BYTE) Player number\r\n(WORD) Length of action data\r\n(VOID) Action data','Informs the client about an action in-game.\r\n\r\nThis is used when there is not enough room inside 0x0C W3GS_INCOMING_ACTION. If there are 1452 actions or more, then this packet should be used instead.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (469,63,'2010-07-15 02:47:39',0,0,NULL,1,5,1,69,'W3GS_MAPPARTNOTOK','[unknown]','More research is required.\r\n\r\nThis is sent when downloading a map in reply to 0x43 W3GS_MAPPART and a chunk of the map file does not match its CRC encryption.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (470,63,'2013-06-23 21:28:19',0,0,NULL,1,7,2,255,'BNLS_IPBAN','(STRING) Message from the server','This packet was not part of the original BNLS documentation, however it was necessary to add it because it can be seen on some BNLS software including BNLSWarden. The packet was created to warn users that their IP address has been restricted temporarily, and that they should reconnect later. However, some servers have been seen using this packet for other purposes.\r\n\r\nThe given message is a normal string except that it lacks a null-terminator, therefore for the lack of a length-descriptor and/or null-terminator, it has been given the VOID data type instead of STRING. To get the full length of the message, use the packet\'s length-descriptor in the header then subtract four (4) from that.\r\n\r\nWhen BNLSWarden sends you this, you will also be disconnected from the server. On all BNLS software implementing this packet, the server usually will send this to you immediately after you connect. There is no way to trigger this packet specifically.\r\n\r\nIn all cases, this packet can be disregarded (or left unrecognized), since the server will still operate normally as it would without the packet, even if you do not recognize or make use of this packet.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (471,NULL,'2010-08-07 00:41:29',0,0,NULL,1,7,1,125,'BNLS_WARDEN','(BYTE) Command\r\n(DWORD) Cookie\r\n\r\nCommand 0x00 (Warden Seed):\r\n(DWORD) Client\r\n(WORD) Length of Seed\r\n(VOID) Seed\r\n(STRING) Username (blank)\r\n(WORD) Length of password\r\n(VOID) Password\r\n\r\nCommand 0x01 (warden packet):\r\n(WORD) Length Of Warden Packet\r\n(VOID) Warden Packet Data\r\n\r\nCommand 0x02 (warden 0x05):\r\n(DWORD) Client\r\n(WORD) Length Of Seed\r\n(VOID) Seed\r\n(DWORD) Unused\r\n(BYTE) [16] Module MD5 Name\r\n(WORD) Length of Warden 0x05 packet\r\n(VOID) Warden 0x05 packet\r\n\r\nCommand 0x03 (warden checks/ini file):\r\n(DWORD) Client\r\n(DWORD) Info Type (0x01)\r\n(WORD) Unused (must be 0x00)\r\n(VOID) Unused','This packet can currently support 2 methods for SC/BW/W3ROC/W3TFT.\r\n\r\nThe first method (commands 0x00 and 0x01) allows you to support warden over battle.net with out any need for installing warden related code and algorithm. It does however, require you to stay connected to the server, or at least, connect each time a new request comes in from Battle.net.\r\n\r\nThe second method (commands 0x02 and 0x03) allows you to support the more basic side of warden, why leaving all the more complex things to the BNLS server. One side to this method, allows you to support warden, without having to worry about downloading, loading and executing warden modules, from Battle.net.\r\n\r\nA basic description of the packet values above:\r\nThe command tells the BNLS server how to parse your request.\r\n<dl>\r\n<dd>0x00 is for initializeing a new Battle.net connection, to allocate a packet handler for you, on the server.</dd>\r\n<dd>0x01 is for having your warden requests handled, via the preallocated packet handler, done in command 0x00.</dd>\r\n<dd>0x02 is for getting the correct data in response to warden 0x05 and a new set of RC4 keys, as well as any other future module requiring packets.</dd>\r\n<dd>0x03 is for getting a list of offsets and their contents, to be used in your warden 0x02 handler.</dd>\r\n</dl>\r\n\r\nThe cookie is sent back to the client, to identify one request from another. With command 0x01, the cookie must remain the same as the one you initialized for a given Battle.net connection, with command 0x00.\r\n\r\nIt is important to use different cookies for differnt bots when dealing with commands 0x00 and 0x01, so you do not have a RC4 encryption key corrupted and therefor having to reconnect to Battle.net. For more info on commands 0x00 and 0x01 cookies, see the \"First Method\" description below.\r\n\r\nThe client value identifies what client you connected to Battle.net with. It can be the DWORD you use over Battle.net (such as PXES) or one of the below:\r\n<dl>\r\n<dd>0x01 = Starcraft</dd>\r\n<dd>0x02 = Broodwar</dd>\r\n<dd>0x03 = Warcraft 2</dd>\r\n<dd>0x04 = Diablo 2</dd>\r\n<dd>0x05 = Diablo 2 LOD</dd>\r\n<dd>0x06 = Starcraft Japan</dd>\r\n<dd>0x07 = Warcraft 3</dd>\r\n<dd>0x08 = Warcraft 3 TFT</dd>\r\n<dd>0x09 = Diablo</dd>\r\n<dd>0x0A = Diablo SWare</dd>\r\n<dd>0x0B = Starcraft SWare</dd>\r\n</dl>\r\n\r\nThe seed value is used to initialize wardens cryptography. This value should be the 1st DWORD of the first CDKey hash in C>S 0x51 SID_AUTH_CHECK. For now, only a 4 byte seed is acceptable. If you are using the older logon protocol (packets 0x06, 0x07, etc), this seed value should be zero.\r\n\r\nThe Module MD5 Name is obtained from the warden 0x01 packet, starting at the 2nd byte.\r\n\r\nThe username and password are for possible future updates, they can be ignored for now - just leave the username blank and the length of password to zero.\r\n\r\nFor the result value in the response, see the \"Result values and meanings\" description below.\r\n\r\n<b>First Method (commands 0x00 and 0x01)</b>\r\nThis connection style allows you to support warden over Battle.net without any need for installing warden related code and algorithms into your program. It does however, require you stay connected to the server, or at least, connect each time a new request comes in from Battle.net. This is basically a fully managed remote warden handler. The Cookie value is used to allocate (command 0x00) and access (command 0x01) a warden connection/handler. Once you initialize a cookie (command 0x00), it can then be sent back to BNLS with a Battle.net warden packet (command 0x01) to have the request handled, and a response sent back to you to be sent on to Battle.net. You can manage multiple Battle.net connections/wardens via one or more BNLS sockets.\r\n\r\nCommand 0x00:\r\nWhen logging onto Battle.net and building your C>S 0x51 SID_AUTH_CHECK packet, you need to obtain the first DWORD of the first cdkey hash which will be the seed value used in command 0x00. You then send this seed value to BNLS, via command 0x00, to activate, or reset the given cookie. This cookie value must be different from any other bots using the same BNLS server from your network, that way you do not have clashes. The BNLS server will respond telling you if the cookie was successfully initialized or not. The data in the response should only ever be present if the response contains an error code.\r\n\r\nCommand 0x01:\r\nOnce you have successfully initialized a cookie for a Battle.net connection, you are ready to forward all Battle.net warden traffic to BNLS. If the result comes back with an error code, the \"Warden Packet Data\" may contain infomation about the given error code. If the result comes back successful, then the \"Warden Packet Data\" will contain the BNCS 0x5E SID_WARDEN payload to be sent to Battle.net. You should only send the response data to Battle.net if the request was successful, the length of the data is one or more and it is a 0x01 command response. Please note that after initializing a cookie with command 0x00, that space will remain allocated on the server, for up to three minutes. This allows you to reconnect, and still be able to resume where you left off, without having to reconnect to Battle.net. You should only send the PAYLOAD of the S>C BNCS 0x5E SID_WARDEM packet, and NOT the full BNCS packet.\r\n\r\n<b>Second Method (commands 0x02 and 0x03)</b>\r\nThis allows you to manage your own warden handler, and avoid the need to download and manage warden modules from Battle.net.\r\n\r\nCommand 0x02:\r\nThis is for managing packets that only the downloaded warden modules can do. Warden sends you an 0x05 packet that iss used to verify that you are truly running the correct warden module. You can send the whole decrypted 0x05 packet (including the packet ID) and get the 0x04 response and new set of RC4 keys. Since the algorithm warden uses to generate the 0x04 response data is differnt in each downloaded module, this usage requires you also send your orginal seed and the module name. It is possible, in the future, that more modules requiring packets, such as 0x05, may come into play. Because of that, the data in the response to command 0x02 maybe vary. This is the current supported format for the \"data\" in command 0x02 responses:\r\n<dl>\r\n<dt>(DWORD) Response Type</dt>\r\n<dd>Type 0x01:</dd>\r\n<dd>(BYTE) [258] New RC4 In Key</dd>\r\n<dd>(BYTE) [258] New RC4 Out Key</dd>\r\n<dd>(DWORD) Length Of warden Response</dd>\r\n<dd>(VOID) Warden responce packet Data</dd>\r\n</dl>\r\n\r\nThe warden response packet data will be the whole raw 0x04 packet. You then encrypt the response packet data with your existing RC4 Out key, then replace your existing RC4 keys with the new ones. It is possible that the BNLS server may respond with the result code 0x03 (warden module not loaded). There is a short delay between Battle.net switching to a new warden module and the BNLS server downloading it from Battle.net, so try again in 10 or more seconds.\r\n\r\nCommand 0x03:\r\nThis allows you to download a file and/or information about the checks warden is making. This infomation can be used by your warden 0x02 parser to identify the check type and its result. Currently the only info type 0x01 is supported (downloading of a .ini file). The \"data\" in the response to command 0x03 has the following format:\r\n<dl>\r\n<dt>(DWORD) Into type</dt>\r\n<dd>For type 0x01:</dd>\r\n<dd>(DWORD) [2] File time</dd>\r\n<dd>(VOID) ini File data</dd>\r\n</dl>\r\nIt may in the future support more info types, depending in what direction warden goes.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (472,NULL,'2010-08-07 00:41:07',0,0,NULL,1,7,2,125,'BNLS_WARDEN','(BYTE) Command\r\n(DWORD) Cookie\r\n(BYTE) Result code\r\n(WORD) Length of Data\r\n(VOID) Data','Provides you with the proper response to a 0x5E SID_WARDEN packet. If you receive result 0x00, then you should respond to SID_WARDEN with the Data, otherwise you should not respond to the SID_WARDEN packet.\r\n\r\n<dl>\r\n<dt>Possible result codes:</dt>\r\n<dd>0x00 Success</dd>\r\n<dd>0x01 Unable to find your allocated packet handler.</dd>\r\n<dd>0x02 Incoming data is corrupted</dd>\r\n<dd>0x03 Warden module is not loaded yet</dd>\r\n<dd>0x04 Error executing warden module</dd>\r\n<dd>0x05 Unsupported Battle.net client/game</dd>\r\n<dd>0x06 Warden hack check error</dd>\r\n<dd>0x07 Warden check invalid</dd>\r\n<dd>0x08 Incoming warden packet unsupported</dd>\r\n<dd>0x09 Unsupported warden info type</dd>\r\n<dd>0xFD Invalid password</dd>\r\n<dd>0xFE Invalid username</dd>\r\n<dd>0xFF Your request was corrupted</dd>\r\n</dl>');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (473,NULL,'2011-07-19 01:51:27',0,0,NULL,1,1,2,23,'SID_READMEMORY','(DWORD) Request ID\r\n(DWORD) Address\r\n(DWORD) Length','Rudimentary hack detection system. Was never used probably due to terrible implementation with little security.\r\n\r\nYes, it is possible for a PvPGN server to read <strong>EVERYTHING</strong> that is in the process\' memory, including sensitive information such as your CDKey.\r\n\r\nFound at:\r\nbattle!1901D470h (as of 1.16.1)\r\n\r\nNOTE: No longer used, as Warden is now used to monitor for hacks. The Battle.net clients still support this though so one can code in support for it if one so desires. Due to it being no longer actively used by Battle.net, this packet is being marked as defunct. ');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (474,NULL,'2010-09-25 23:15:05',0,0,NULL,1,1,1,23,'SID_READMEMORY','(DWORD) Request ID\r\n(VOID) Memory','Rudimentary hack detection system. Was never used probably due to terrible implementation with little security.\r\n\r\nYes, it is possible for a PvPGN server to read <strong>EVERYTHING</strong> that is in the process\' memory, including sensitive information such as your CDKey.\r\n\r\nFound at:\r\nbattle!1901D470h (as of 1.16.1)');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (475,NULL,'2010-09-25 23:17:23',0,0,NULL,1,1,2,32,'SID_ANNOUNCEMENT','(STRING) Text','Very simply prints out text with the string at 1903B9FBh (the default string, used anyway if the username field is NULL in the chat event struct -- currently a single 0x7F char) as the username.\r\n\r\nUsed to send announcements and arbitrary messages to the user, but this was soon superseded by SID_CHAT subcommands such as EID_INFO, EID_ERROR, and EID_BROADCAST. Printed out with the same color and style as an EID_BROADCAST.\r\n\r\nFound at:\r\nbattle!1901DDA0h (as of 1.16.1)');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (476,NULL,'2011-06-18 13:54:25',0,0,NULL,1,1,2,35,'SID_WRITECOOKIE','(DWORD) unknown/unparsed -- Flags, Request ID?\r\n(DWORD) unknown/unparsed -- Timestamp?\r\n(STRING) Registry key name\r\n(STRING) Registry key value','Much like a website cookie, simply stores some arbitrary string to a \'cookie jar\' to save preferences et al. which can be retrieved later by the server.\r\n\r\nNot used because it was quickly discovered that storing preferences produces less problems and were faster by storing them server-side, associating them with the account. It is somewhat curious that these packet IDs are close to SID_READUSERDATA/SID_WRITEUSERDATA (0x26 & 0x27).\r\n\r\nFound at: battle!190216FBh and battle!1901D660h, respectively.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (477,NULL,'2010-09-25 23:25:57',0,0,NULL,1,1,2,36,'SID_READCOOKIE','(DWORD) Echoed back, Request ID?\r\n(DWORD) Echoed back, Timestamp?\r\n(STRING) Registry key name','Much like a website cookie, simply stores some arbitrary string to a \'cookie jar\' to save preferences et al. which can be retrieved later by the server.\r\n\r\nNot used because it was quickly discovered that storing preferences produces less problems and were faster by storing them server-side, associating them with the account. It is somewhat curious that these packet IDs are close to SID_PROFILE/SID_WRITEPROFILE (0x26 & 0x27).\r\n\r\nFound at: battle!190216FBh and battle!1901D660h, respectively.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (478,NULL,'2010-09-25 23:26:30',0,0,NULL,1,1,1,36,'SID_READCOOKIE','(DWORD) First DWORD from S -> C\r\n(DWORD) Second DWORD from S -> C\r\n(STRING) Registry key name\r\n(STRING) Registry key value','Much like a website cookie, simply stores some arbitrary string to a \'cookie jar\' to save preferences et al. which can be retrieved later by the server.\r\n\r\nNot used because it was quickly discovered that storing preferences produces less problems and were faster by storing them server-side, associating them with the account. It is somewhat curious that these packet IDs are close to SID_PROFILE/SID_WRITEPROFILE (0x26 & 0x27).\r\n\r\nFound at: battle!190216FBh and battle!1901D660h, respectively.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (479,63,'2010-09-25 23:30:59',0,2,'2016-03-15 05:22:15',1,1,2,67,'SID_WARCRAFTUNKNOWN','(DWORD) Unknown (0)','Unknown. The person who found this was unable to disassemble Warcraft III\'s game.dll without a lot of trouble, and therefore had limited knowledge of it. It has been seen once, after SID_LOGONPROOF (0x54) in the NLS logon sequence.\r\n\r\nFound at: game.dll\r\n\r\n[Update 03/14/2016]: It was also seen during the Warcraft III 1.27a patch shortly before it went live. Here are some snapshots from a couple different bots:\r\n* [http://i.imgur.com/CnihMhs.png](http://i.imgur.com/CnihMhs.png)\r\n* [http://i.imgur.com/DTHJuwy.png](http://i.imgur.com/DTHJuwy.png)');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (481,NULL,'2011-05-03 16:52:45',0,0,NULL,1,3,1,22,'MCP_CHARRANK','(DWORD) Hardcore BOOL\r\n(DWORD) Expansion BOOL\r\n(STRING) Character Name','This packet is used to find a Diablo II character in the ladder. Hardcore and Expansion can either be 1 or 0 and define the ladder type (always Overall). The server responds with MCP_REQUESTLADDERDATA, listing (and possibly highlighting using character flag 0x08?) the page of ranks in which the character is found.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (482,NULL,'2012-03-20 12:30:21',0,0,NULL,2,2,3,0,'PKT_STORM','(WORD) Checksum\r\n(WORD) Length\r\n(WORD) Seq1\r\n(WORD) Seq2\r\n(BYTE) CLS\r\n(BYTE) Command\r\n(BYTE) PlayerID\r\n(BYTE) Resend\r\n(VOID) Payload','<b>Checksum:</b>\r\n\r\nCalculated from all of the data in this packet starting at Length. See <a href=http://darkblizz.org/Forum2/starcraft/sc-udp-game-research/msg96/#msg96>here</a> for the function, and more details on this protocol.\r\n\r\n<b>Length:</b>\r\n\r\nIncludes everything you see here (EX: 12 + length of Payload).\r\n\r\n<b>Seq1:</b>\r\n\r\nNumber of packets sent.\r\n\r\n<b>Seq2:</b>\r\n\r\nNumber of packets received.\r\n\r\n<b>CLS:</b>\r\n\r\nCan be 0, 1, or 2.\r\n\r\n0x00: Internal packets. Handled by network provider and sent to the game as high level events. Command is used by network provider.\r\n0x01: Asynchronous packets. Payload sent to StarCraft, Diablo, or WarCraft II game. Can be sent any time. Command is 0.\r\n0x02: Synchronous packets. Payload sent to StarCraft, Diablo, or WarCraft II game. Can be buffered but must be sent once per game turn. Command is 0.\r\n\r\n<b>Command:</b>\r\n\r\nThe packet ID of CLS 0 packets. 0 for other packets.\r\n\r\n<b>PlayerID:</b>\r\n\r\nThe player ID. If unknown (first few packets from client) set to 0xFF. Otherwise 0xFF means \"everyone\".\r\n\r\n<b>Resend:</b>\r\n\r\n0x00: Normal\r\nHandle this normally.\r\n\r\n0x01: Verify\r\nUsed to verify Seq1 and Seq2.\r\n\r\n0x02: Resend\r\nResend request.\r\n\r\n0x03: Callback\r\nResend response.\r\n\r\nPacket name was invented.\r\n\r\nSee Storm Messages for CLS 0 packets.\r\nSee SCGP Messages for CLS 1 and 2 packets for StarCraft, BW, JP, and SHR.\r\nSee W2GP Messages for CLS 1 and 2 packets for WarCraft II BNE.\r\nSee D1GP Messages for CLS 1 and 2 packets for Diablo and SHR.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (483,NULL,'2012-03-24 14:19:48',0,0,NULL,2,8,3,5,'SCGP_NOP','(BYTE) ID (0x05)','Packet CLS 2.\r\n\r\nSent in the synchronous stream if nothing else is to be sent that turn. Often only sent in lobby.\r\n\r\nPacket name was invented.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (484,NULL,'2012-03-24 15:32:33',0,0,NULL,2,8,3,0,'SCGP_GAMECHATPUBLIC','(BYTE) ID (0x00)\r\n(BYTE) Type\r\n(STRING) Chat message','Packet CLS 1.\r\n\r\nSent to talk to other ingame players.\r\nNote: This packet is used in-game game only.\r\nIf you want to talk to players in the game room, you must use CLS 1 packet 0x4C.\r\n\r\nType:\r\n0x00 = Normal');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (485,NULL,'2012-03-24 14:20:49',0,0,NULL,2,8,3,76,'SCGP_LOBBYCHAT','(BYTE) ID (0x4C)\r\n(STRING) Chat message','Packet CLS 1.\r\n\r\nSent to talk to other players in the game lobby.\r\nNote: If you\'re no longer in the game lobby, and are playing the game, you must use CLS 1, packet 0x00 to talk to other players.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (486,NULL,'2012-03-24 14:19:58',0,0,NULL,2,8,3,73,'SCGP_PLAYERJOIN','(BYTE) ID (0x49)\r\n(DWORD) Player ID','Packet CLS 1.\r\n\r\nSent from host to other players in lobby.\r\n\r\nNotifies other players that this player has joined the game.\r\nThe game would display \"X has joined the game\" and then /astat them when receiving this.\r\nThis is not sent for players who are already in the game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (487,NULL,'2012-03-24 14:20:26',0,0,NULL,2,8,3,74,'SCGP_ROOMDATA','(BYTE) ID (0x4A)\r\n(WORD) Map Tileset\r\n(WORD) Map Width\r\n(WORD) Map Height\r\n(BYTE[12]) OWNR\r\n(BYTE[12]) SIDE\r\n(BYTE[12]) OWNR Default\r\n(BYTE[8]) FORC\r\n(BYTE[4]) FORC Flag\r\n(BYTE[8]) RACE','Packet CLS 1.\r\n\r\nThe tileset, width and height are not retrieved from the map in case it hasn\'t been downloaded yet-- these 3 values are used on failth.\r\nValid tilesets:\r\n0x00 = Badlands\r\n0x01 = Space Platform\r\n0x02 = Installation\r\n0x03 = Ashworld\r\n0x04 = Jungle\r\n0x05 = Desert\r\n0x06 = Arctic\r\n0x07 = Twilight\r\n\r\nThe OWNR values are the base room slot values. They are modified values from the map to fit the given game configuration.\r\nThe OWNR default is simply that, the default values from the map file.\r\nThe 12 bytes is for each possible player (1 to 12)\r\nPossible values:\r\n0x00 = Doesn\'t exist\r\n0x02 = Human\r\n0x03 = Rescuable\r\n0x05 = Computer\r\n0x06 = Open\r\n0x07 = Neutral\r\n0x08 = Closed\r\nNote: OWNR of Rescuable and Neutral are not shown.\r\n\r\nThe SIDE tells you the race for each player. These are modified SIDE values from the map file, to fit the given game configuration.\r\n1 SIDE byte for each possible player (1 to 12)\r\nPossible values:\r\n0x00 = Zerg\r\n0x01 = Terran\r\n0x02 = Protoss\r\n0x03 = Independent\r\n0x04 = Neutral\r\n0x06 = Random\r\n\r\nThe FORC tells you the force/team index of each player.\r\nFor example, 0x00 would mean no team (like melee) and 0x01 to 0x04 means this player/slot belongs to this team.\r\n\r\nThe FORC Flag gives you the settings for each force.\r\n0x01 = Random start locations\r\n0x02 = Allies\r\n0x04 = Allie victory\r\n0x08 = Shared Vision\r\n\r\nThe RACE tells you if a player can change their race *I think*. I\'m not to sure about the RACE part, but i\'m pretty sure thats what it does. For example, disables a player from changing their race');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (488,NULL,'2012-03-24 14:20:38',0,0,NULL,2,8,3,75,'SCGP_FORCENAMES','(BYTE) ID (0x4B)\r\n(BYTE[30]) Force 1 Name\r\n(BYTE[30]) Force 2 Name\r\n(BYTE[30]) Force 3 Name\r\n(BYTE[30]) Force 4 Name','Packet CLS 1.\r\n\r\nThis is only sent by the host to joining players, in Use Map Settings games, so they can display the name of the teams before they have downloaded the map.\r\nEach block of 30 bytes is infact a null-terminated string.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (489,NULL,'2012-03-24 14:21:08',0,0,NULL,2,8,3,78,'SCGP_REJECT','(BYTE) ID (0x4E)\r\n(BYTE) Event','Packet CLS 1.\r\n\r\nNotifies you of an Event causing you to not be able to join:\r\nPossible values:\r\n0x01 = You have been banned.\r\n0x02 = Game creater has closed all avalible slots.\r\n0x03 = Game host has left the game.\r\n0x04 = Unable to join the game.\r\n0x05 = Save game file not found.\r\n0x06 = Unable to write scenario file.\r\n0x07 = Invalid game version.\r\n0x08 = Invalid client version.\r\n0x09 = Unable to authenticate map.\r\nOther = You have been booted.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (490,NULL,'2012-03-24 14:34:14',0,0,NULL,2,8,3,79,'SCGP_MAP','(BYTE) ID (0x4F)\r\n(WORD) Lengh of payload (discluding this value)\r\n(WORD) Event<dl><dt>Event 0x00:</dt>\r\n<dd>(WORD) 0x100</dd>\r\n<dd>(DWORD) File Position</dd>\r\n<dt>Event 0x01:</dt>\r\n<dd>(DWORD) File Lengh</dd>\r\n<dd>(DWORD) File Checksum</dd>\r\n<dd>(STRING) File Name</dd>\r\n<dt>Event 0x02:</dt>\r\n<dd>(BYTE) Player ID</dd>\r\n<dd>(DWORD) File Position</dd>\r\n<dt>Event 0x03:</dt>\r\n<dd>[blank]</dd>\r\n<dt>Event 0x04:</dt>\r\n<dd>(BYTE) 0x00</dd>\r\n<dd>(DWORD) File Position</dd>\r\n<dd>(WORD) Data Block Lengh</dd>\r\n<dd>(VOID) File Data Block</dd>\r\n<dt>Event 0x05:</dt>\r\n<dd>(BYTE) 0x00</dd>\r\n<dd>(DWORD) File Position</dd>\r\n</dl>','Packet CLS 1.\r\n\r\nThis packet is used to manage map downloading.\r\nSome important things to note:\r\nPlayers don\'t just download the map from the host. The host can request other ingame players send it, if they already have it.\r\nData blocks are send in blocks of 128 bytes at a time, but you can get away with 256 blocks with out issue.\r\n\r\nEvent 0x00 [C>H]:\r\nTells the host if you have the map or not.\r\nFor example, the map positon tells the host where to start sending the file from.\r\nIf you already have the map, then the Map position should be the lengh of the file.\r\n\r\nEvent 0x01 [H>C]:\r\nAsks a player if they have the given map.\r\nPlayers should respond to this, with event 0x00.\r\n\r\nEvent 0x02 [H>C]:\r\nTells the receiving player, to send the map to another player.\r\n\r\nEvent 0x03 [H>C]:\r\nI\'m not to sure about this one.\r\nThis is send to a player who has just finished sending the map to another player, and also when the game is starting.\r\n\r\nEvent 0x04 [C>C]:\r\nUsed to send a block of the map to another player.\r\nReceiving players should verify the block of data, by sending event 0x05.\r\nAny breaks in the UDP stream should be managed with standard resend requests.\r\n\r\nEvent 0x05 [C>C]:\r\nVerifies the position of the map you are currently at.\r\nThis should be sent as a respone to event 0x04.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (491,NULL,'2012-03-24 14:28:41',0,0,NULL,2,8,3,80,'SCGP_UNKNOWNREQUEST','(BYTE) ID (0x50)','Packet CLS 1.\r\n\r\nSent by the host during the join sequence.\r\n\r\nPlayers should respond to this, by sending CLS 2, 0x40.\r\nIt is sent to joiners as part of the join process, a lot with/after CLS 1, 0x4A.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (492,NULL,'2012-03-24 14:27:42',0,0,NULL,2,8,3,64,'SCGP_UNKNOWNRESPONSE','(BYTE) ID (0x40)\r\n(DWORD) 0x00\r\n(DWORD) 0x00\r\n(WORD) A\r\n(WORD) B\r\n(BYTE) 0x00\r\n(DWORD) Uniqueness','Packet CLS 2.\r\n\r\nSend to the host in response to CLS 1, packet 0x50.\r\n\r\nI\'m not sure what the A and B values in this message mean. However, it seems the host sends them to other players in CLS 2, packet 0x3F.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (493,NULL,'2012-03-24 14:30:08',0,0,NULL,2,8,3,60,'SCGP_GAMESTART','(BYTE) ID (0x3C)','Packet CLS 2.\r\n\r\nSent by the host to start game.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (494,NULL,'2012-03-24 14:33:59',0,0,NULL,2,8,3,61,'SCGP_MAPPERCENT','(BYTE) ID (0x3D)\r\n(BYTE) Download percent','Packet CLS 2.\r\n\r\nSent to all other players to tell them your current download percent.\r\nThis should be sent in 1 second intervals when your download percent has changed.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (495,NULL,'2012-03-24 14:37:38',0,0,NULL,2,8,3,62,'SCGP_SLOTUPDATE','(BYTE) ID (0x3E)\r\n(BYTE) Slot Index\r\n(BYTE) Player Index\r\n(BYTE) Slot State\r\n(BYTE) Race\r\n(BYTE) Team Index','Packet CLS 2.\r\n\r\nSent by the host to notify players of a given game room slot\'s status.\r\n\r\nPossible values for State:\r\n0x00 = Slot doesnt exist\r\n0x02 = Slot is Taken/Human\r\n0x03 = Slot is Rescuable\r\n0x05 = Slot is Computer\r\n0x06 = Slot is Open\r\n0x07 = Slot is Neutral\r\n0x08 = Slot is Closed\r\nNote: States of Rescuable and Neutral are not shown.\r\n\r\nPossible values for Race:\r\n0x00 = Zerg\r\n0x01 = Terran\r\n0x02 = Protoss\r\n0x03 = Independent\r\n0x04 = Neutral\r\n0x06 = Random');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (496,NULL,'2012-03-24 14:43:53',0,0,NULL,2,8,3,63,'SCGP_HUMAN','(BYTE) ID (0x3F)\r\n(BYTE) Unknown (often 0x00; seen 0x01)\r\n(BYTE) 0x00\r\n(BYTE) 0x00\r\n(WORD) A\r\n(WORD) B','Packet CLS 2.\r\n\r\nSent by the host sometimes with CLS 2, packet 0x3E.\r\n\r\nI\'m not really sure what this message means.\r\nThe values A and B (and probably the null values) are carbon copies of the values a player sends in CLS 2, packet 0x40.\r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (497,NULL,'2012-03-24 14:47:20',0,0,NULL,2,8,3,65,'SCGP_CHANGERACE','(BYTE) ID (0x41)\r\n(BYTE) Slot Index\r\n(BYTE) Race','Packet CLS 2.\r\n\r\nSent to other players requesting the race of a given slot be changed.\r\nIf changing your own race, the slot index must be 0x08.\r\nPossible Races:\r\n0x00 = Zerg\r\n0x01 = Terran\r\n0x02 = Protoss\r\n0x03 = Independent\r\n0x04 = Neutral\r\n0x06 = Random\r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (498,NULL,'2012-03-24 14:59:52',0,0,NULL,2,8,3,66,'SCGP_CHANGETEAM','(BYTE) ID (0x42)\r\n(BYTE) Team Index','Packet CLS 2.\r\n\r\nSent to other players requesting you be moved to the given team.\r\n\r\nUsed in non-UMS games. To move team in a UMS game, use CLS 2, packet 0x43.\r\n');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (499,NULL,'2012-03-24 14:59:40',0,0,NULL,2,8,3,67,'SCGP_CHANGETEAMUMS','(BYTE) ID (0x43)\r\n(BYTE) Team Index','Packet CLS 2.\r\n\r\nSent to other players requesting you be moved to the given team.\r\nIf you are already in the given team, you will be moved to the next available slot in that team.\r\n\r\nUsed in UMS games. To move team in a non-UMS game, use CLS 2, packet 0x42.');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (500,NULL,'2012-03-24 15:02:27',0,0,NULL,2,8,3,68,'SCGP_CHANGESLOTSTATE','(BYTE) ID (0x44)\r\n(BYTE) Slot Index\r\n(BYTE) State','Packet CLS 2.\r\n\r\nSent by the host to change a given slot index to a new state:\r\n0x00 = Computer\r\n0x01 = Open\r\n0x02 = Closed');
INSERT INTO `packets` (`id`, `user_id`, `created_datetime`, `options_bitmask`, `edited_count`, `edited_datetime`, `packet_transport_layer_id`, `packet_application_layer_id`, `packet_direction_id`, `packet_id`, `packet_name`, `packet_format`, `packet_remarks`) VALUES (501,NULL,'2012-03-24 15:04:48',0,0,NULL,2,8,3,69,'SCGP_SLOTSWITCH','(BYTE) ID (0x45)\r\n(BYTE) Slot Index From\r\n(BYTE) Slot Index To','Packet CLS 2.\r\n\r\nSent by the host to switch two slots. Used in Top vs Bottom games.');
